('14cad247250618', 'procedure', 'WIZARDSUBFORM', 'Wizard for creating a subform', 'setup', '14cad243c2d9ef', NULL, '', NULL, '', 0, '0', NULL, '', '', '', '', NULL, '$TN               = ''#TableName#'';\r\n$PK               = ''#PrimaryKey#'';\r\n$FK				  = ''#ForeignKey#'';\r\n$FN				  = ''#FormName#'';\r\n$formID           = uniqid(''1'');\r\n//echo $FN;\r\n//die();\r\n//======================================\r\n//         add form \r\n//======================================\r\n\r\n$insertObject       = "INSERT INTO zzsys_object (zzsys_object_id, sob_zzsys_form_id, sob_all_title, ";\r\n$insertObject    .= "sob_all_name, sob_all_column_number, sob_all_order_number, ";\r\n$insertObject    .= "sob_all_tab_title, sob_all_tab_number, sob_all_type, ";\r\n$insertObject    .= "sob_all_clone, sob_subform_table, sob_subform_primary_key, sob_subform_foreign_key,  ";\r\n$insertObject	 .= "sob_subform_sql, sob_subform_left, sob_subform_top, sob_subform_height, sob_subform_width, ";\r\n$insertObject	 .= "sob_subform_blank_rows, sob_subform_delete_box, sob_subform_title_height)";\r\n$insertObject    .= "VALUES (''$formID'', ''$FN'', ''subform_{$TN}'', ''subform_{$TN}'', ''10'', ";\r\n$insertObject    .= " ''10'', ''Subform'', ''20'', ''subform'', ''1'', ";\r\n$insertObject	 .= "''$TN'',''$PK'',''$FK'',''SELECT * FROM {$TN} WHERE $FK = \\''#id#\\'''',50,50,20,1000,3,1,2);";\r\n\r\n//print $insertObject . ''<br/>'';\r\nnuRunQuery($insertObject);\r\n//nuRunQuery("CREATE TABLE `fanste` SELECT * FROM zzsys_object WHERE zzsys_object_id = ''$formID''");\r\n\r\n\r\n//======================================\r\n//         add objects \r\n//======================================\r\n$insertObject        = "";\r\n$column              = 10;\r\n$order               = 10;\r\n\r\n$t = nuRunQuery(''SELECT #SelectedFieldNames# FROM #SelectedFieldNames# ORDER BY id'');\r\n\r\nwhile($r=db_fetch_row($t)){\r\n\r\n   $fname            = $r[0];\r\n   $ID               = uniqid(''1'');\r\n   if($order         > 100){\r\n   	$column      = $column + 10;\r\n   	$order       = 10;\r\n   }else{\r\n   	$order       = $order + 10;\r\n   }\r\n   $insertObject    .= "INSERT INTO zzsys_object ";\r\n   $insertObject    .= "(zzsys_object_id, sob_zzsys_form_id, sob_all_title, ";\r\n   $insertObject    .= "sob_all_name, sob_all_column_number, sob_all_order_number, ";\r\n   $insertObject    .= "sob_all_tab_title, sob_all_tab_number, sob_all_type, ";\r\n   $insertObject    .= "sob_all_clone, sob_text_align, sob_text_length) ";\r\n   $insertObject    .= "VALUES (''$ID'', ''$formID'', ''$fname'', ''$fname'', ''$column'', ";\r\n   $insertObject    .= " ''$order'', ''Subform'', ''20'', ''text'', ''1'', ''l'', ''10'');";\r\n\r\n\r\n   nuRunQuery($insertObject);\r\n   $insertObject   = '''';\r\n}\r\n\r\n$qry = nuRunQuery("SELECT * FROM zzsys_form WHERE zzsys_form_id = ''$FN''");\r\n$obj = db_fetch_object($qry);\r\n$string             = "<br/><br/>A Subform and its Objects have now been created ";\r\n$string            .= "on the $obj->sfo_name form.<br/>";\r\nprint $string;', '2010-10-07 12:07:54', '2010-11-26 09:28:30', 'globeadmin'),
('14c8dc47abd6e9', 'procedure', 'WIZARDFORM', 'Wizard for creating a form', 'setup', '14c8dc4aec9b4f', NULL, '', NULL, '', 0, '0', NULL, '', '', '', '', NULL, '$TN               = ''#TableName#'';\r\n$PK               = ''#PrimaryKey#'';\r\n$formID           = uniqid(''1'');\r\n\r\n//======================================\r\n//         add form \r\n//======================================\r\n\r\n$insertForm       = "INSERT INTO zzsys_form (zzsys_form_id, sfo_name, sfo_title, ";\r\n$insertForm      .= "sfo_access_without_login, sfo_close_after_save, ";\r\n$insertForm      .= "sfo_refresh_after_save, sfo_sql, sfo_table, ";\r\n$insertForm      .= "sfo_primary_key, sfo_add_button, sfo_save_button, ";\r\n$insertForm      .= "sfo_delete_button, sfo_clone_button, sfo_close_button) ";\r\n$insertForm      .= "VALUES (''$formID'', ''(Wizard) $TN'', ''$TN'', ''0'', ''0'', ''1'', ";\r\n$insertForm      .= "''SELECT * FROM $TN'', ''$TN'', ''$PK'', ''1'', ''1'', ''1'', ''1'', ''0'');";\r\n\r\n//print $insertForm . ''<br/>'';\r\nnuRunQuery($insertForm);\r\n\r\n \r\n\r\n//======================================\r\n//         add browse columns \r\n//======================================\r\n$insertColumns       = "";\r\n$order               = 10;\r\n$t = nuRunQuery(''SELECT #BrowseColumns# FROM #BrowseColumns#'');\r\n\r\nwhile($r=db_fetch_row($t)){\r\n\r\n   $fname            = $r[0];\r\n   $ID               = uniqid(''1'');\r\n   $order            = $order + 10;\r\n\r\n   $insertColumns   .= "INSERT INTO zzsys_browse ";\r\n   $insertColumns   .= "(zzsys_browse_id, sbr_zzsys_form_id, sbr_title, ";\r\n   $insertColumns   .= "sbr_display, sbr_align, ";\r\n   $insertColumns   .= "sbr_searchable, sbr_sort, sbr_sortable, ";\r\n   $insertColumns   .= "sbr_visible, sbr_order, sbr_width) VALUES (";\r\n   $insertColumns   .= "''$ID'', ''$formID'', ''$fname'', ''$fname'', ";\r\n   $insertColumns   .= "''l'', ''1'', ''$fname'', ''1'', ''1'', $order, 100);";\r\n\r\n   nuRunQuery($insertColumns);\r\n   $insertColumns   = '''';\r\n}\r\n\r\n//print $insertColumns;\r\n\r\n \r\n\r\n\r\n//======================================\r\n//         add objects \r\n//======================================\r\n$insertObject        = "";\r\n$column              = 10;\r\n$order               = 10;\r\n\r\n$t = nuRunQuery(''SELECT #SelectedFieldNames# FROM #SelectedFieldNames#'');\r\n\r\nwhile($r=db_fetch_row($t)){\r\n\r\n   $fname            = $r[0];\r\n   $ID               = uniqid(''1'');\r\n   if($order         > 100){\r\n    $column      = $column + 10;\r\n    $order       = 10;\r\n   }else{\r\n    $order       = $order + 10;\r\n   }\r\n   $insertObject    .= "INSERT INTO zzsys_object ";\r\n   $insertObject    .= "(zzsys_object_id, sob_zzsys_form_id, sob_all_title, ";\r\n   $insertObject    .= "sob_all_name, sob_all_column_number, sob_all_order_number, ";\r\n   $insertObject    .= "sob_all_tab_title, sob_all_tab_number, sob_all_type, ";\r\n   $insertObject    .= "sob_all_clone, sob_text_align, sob_text_length) ";\r\n   $insertObject    .= "VALUES (''$ID'', ''$formID'', ''$fname'', ''$fname'', ''$column'', ";\r\n   $insertObject    .= " ''$order'', ''Main'', ''10'', ''text'', ''1'', ''l'', ''10'');";\r\n\r\n\r\n   nuRunQuery($insertObject);\r\n   $insertObject   = '''';\r\n}\r\n\r\n$ID                  = uniqid(''1'');\r\n\r\n$insertObject       .= "INSERT INTO zzsys_object (sob_button_zzsys_form_id, ";\r\n$insertObject       .= "zzsys_object_id, sob_zzsys_form_id, sob_all_title, ";\r\n$insertObject       .= "sob_all_name, sob_all_column_number, sob_all_order_number, ";\r\n$insertObject       .= "sob_all_tab_title, sob_all_tab_number, sob_all_type, sob_button_left, ";\r\n$insertObject       .= "sob_button_top, sob_all_clone, sob_text_align, sob_button_length, sob_all_admin_only) ";\r\n$insertObject       .= "VALUES ( ''$formID'', ''$ID'', ''index'', ''$TN (wizard)'', ''$TN'', ''10'', ";\r\n$insertObject       .= " ''10'', ''Wizard'', ''1000'', ''button'', ''0'', ''0'', ''10'', ''0'', ''10'' ,''0'');";\r\n\r\n//print $insertObject;\r\nnuRunQuery($insertObject);\r\n\r\n$string             = "<br/><br/>A Form and its Objects have now been created, ";\r\n$string            .= "along with a Button to open this Form.<br/>";\r\n$string            .= "This Button, called <font color=''red''>$TN</font>, is found ";\r\n$string            .= "on the Index Form under a Tab called Wizard<br/>";\r\n$string            .= "(The Index Form will need to be refreshed for this Button to appear)";\r\nprint "<table align=''center''><tr align=''center''><td align=''center''>$string</td></tr></table>";', '2010-09-13 15:58:10', '2012-01-05 13:42:59', 'globeadmin'),
('14fd6a50a2829e', 'procedure', 'COLOURWIZARD', 'Quickly Change Colour Style', 'setup', '14fd6a640246f9', NULL, '', NULL, '', NULL, '2', NULL, '', '', '', '', NULL, '\r\n// ================================ FUNCTIONS ============================================\r\nfunction ColorLuminance($hex, $lum) {  \r\n	$hex = preg_replace("/[^0-9a-zA-Z]/", "", $hex);\r\n	// six hex digits\r\n	if (strlen($hex) == 6)\r\n		{\r\n		$r = hexdec(substr($hex,0,2));\r\n		$g = hexdec(substr($hex,2,2));\r\n		$b = hexdec(substr($hex,4,2));\r\n		}\r\n	// three hex digits	\r\n	else if (strlen($hex) == 3)\r\n		{\r\n		$r = hexdec(substr($hex,0,1) . substr($hex,0,1));\r\n		$g = hexdec(substr($hex,1,1) . substr($hex,1,1));\r\n		$b = hexdec(substr($hex,2,1) . substr($hex,2,1));\r\n		}\r\n	// unknown\r\n	else\r\n		{\r\n		return "000000";\r\n		}\r\n\r\n	$r = $r + ceil($r * $lum); if ($r < 0) {$r = 0;} if ($r > 255) {$r = 255;}\r\n	$g = $g + ceil($g * $lum); if ($g < 0) {$g = 0;} if ($g > 255) {$g = 255;}\r\n $b = $b + ceil($b * $lum); if ($b < 0) {$b = 0;} if ($b > 255) {$b = 255;}\r\n	return sprintf("%02X%02X%02X",$r,$g,$b);\r\n}  \r\n\r\nfunction getBrightness($hex) {\r\n	$hex = preg_replace("/[^0-9a-zA-Z]/", "", $hex);\r\n	if (strlen($hex) == 6)\r\n		{\r\n		$r = hexdec(substr($hex,0,2));\r\n		$g = hexdec(substr($hex,2,2));\r\n		$b = hexdec(substr($hex,4,2));\r\n		}\r\n	// three hex digits	\r\n	else if (strlen($hex) == 3)\r\n		{\r\n		$r = hexdec(substr($hex,0,1) . substr($hex,0,1));\r\n		$g = hexdec(substr($hex,1,1) . substr($hex,1,1));\r\n		$b = hexdec(substr($hex,2,1) . substr($hex,2,1));\r\n		}\r\n	// unknown\r\n	else\r\n		{\r\n		return 0;\r\n		}\r\n	return (($r * 299) + ($g * 587) + ($b * 114)) / 1000;\r\n}\r\n// ================================ FUNCTIONS ============================================\r\n\r\n\r\n// ================================ POSTED VARIABLES =====================================\r\n$BrsCol = "#cwiz_browse#";\r\n$TabCol = "#cwiz_tab#";\r\n$SrcCol = "#cwiz_search#";\r\n$ButCol = "#cwiz_button#";\r\nif (strcmp(trim($BrsCol),"") == 0) {\r\n	$res = nuRunQuery("SELECT ssp_property_value FROM zzsys_style_property WHERE (ssp_property_name = ''background-color'') AND (ssp_zzsys_style_id = (SELECT zzsys_style_id FROM zzsys_style WHERE (sst_class  = ''.selected'')))");\r\n	while($row = db_fetch_array($res)) { $BrsCol = $row[''ssp_property_value'']; }\r\n}\r\nif (strcmp(trim($TabCol),"") == 0) {\r\n	$res = nuRunQuery("SELECT ssp_property_value FROM zzsys_style_property WHERE (ssp_property_name = ''background-color'') AND (ssp_zzsys_style_id = (SELECT zzsys_style_id FROM zzsys_style WHERE (sst_class  = ''.tab'')))");\r\n	while($row = db_fetch_array($res)) { $TabCol = $row[''ssp_property_value'']; }\r\n}\r\nif (strcmp(trim($SrcCol),"") == 0) {\r\n	$res = nuRunQuery("SELECT ssp_property_value FROM zzsys_style_property WHERE (ssp_property_name = ''background-color'') AND (ssp_zzsys_style_id = (SELECT zzsys_style_id FROM zzsys_style WHERE (sst_class  = ''.searchcolor'')))");\r\n	while($row = db_fetch_array($res)) { $SrcCol = $row[''ssp_property_value'']; }\r\n}\r\nif (strcmp(trim($ButCol),"") == 0) {\r\n	$res = nuRunQuery("SELECT ssp_property_value FROM zzsys_style_property WHERE (ssp_property_name = ''background-color'') AND (ssp_zzsys_style_id = (SELECT zzsys_style_id FROM zzsys_style WHERE (sst_class  = ''.button'')))");\r\n	while($row = db_fetch_array($res)) { $ButCol = $row[''ssp_property_value'']; }\r\n}\r\n// remove Hashes from fronts of colors - added back when written to the database\r\n$BrsCol = preg_replace("/[^0-9a-zA-Z]/", "", $BrsCol);\r\n$TabCol = preg_replace("/[^0-9a-zA-Z]/", "", $TabCol);\r\n$SrcCol = preg_replace("/[^0-9a-zA-Z]/", "", $SrcCol);\r\n$ButCol = preg_replace("/[^0-9a-zA-Z]/", "", $ButCol);\r\n// ================================ POSTED VARIABLES =====================================\r\n\r\n// ================================ CALCULATED COLOURS =====================================\r\n$browseBackground = $BrsCol;\r\n$browseBorder = ColorLuminance($BrsCol, -0.5);  // %50 Darker\r\nif (getBrightness($BrsCol) > 128) {$tcol = "000000";} else {$tcol = "ffffff";}\r\n$browseText = $tcol;\r\n\r\n$selCol = ColorLuminance($BrsCol, -0.5); // %50 Darker for Selection\r\n$brsSelBackground = $selCol;\r\n$brsSelBorder = ColorLuminance($selCol, -0.5);  // %50 Darker\r\nif (getBrightness($selCol) > 128) {$tcol = "000000";} else {$tcol = "ffffff";}\r\n$brsSelText = $tcol;\r\n\r\n$lookCol = ColorLuminance($BrsCol, -0.5); // %50 Darker for Lookup\r\n$lkupdescBackground = $lookCol;\r\n$lkupdescBorder = ColorLuminance($lookCol, -0.5);  // %50 Darker\r\nif (getBrightness($lookCol) > 128) {$tcol = "000000";} else {$tcol = "ffffff";}\r\n$lkupdescText = $tcol;\r\n\r\n//	scrollbars\r\n$scrlbarFace = ColorLuminance($BrsCol, -0.1); // %10 Darker\r\n$scrlbarBase = ColorLuminance($BrsCol, 0.3);  // %30 Lighter\r\n$scrlbarShadow = ColorLuminance($BrsCol, -0.5); // %50 Darker\r\n$scrlbarDarkshadow = ColorLuminance($BrsCol, -0.9); // %90 Darker\r\n$scrlbarHighlight = ColorLuminance($BrsCol, 0.4);  // %40 Lighter\r\n\r\n$tabBackground = $TabCol;\r\n$tabBorder = ColorLuminance($TabCol, -0.5);  // %50 Darker\r\nif (getBrightness($TabCol) > 128) {$tcol = "000000";} else {$tcol = "ffffff";}\r\n$tabText = $tcol;\r\n\r\n$buttonBackground = $ButCol;\r\n$buttonBorder = ColorLuminance($ButCol, -0.4); // %40 Darker\r\nif (getBrightness($ButCol) > 128) {$tcol = "000000";} else {$tcol = "ffffff";}\r\n$buttonText = $tcol;\r\n\r\n$searchBackground = $SrcCol;\r\n$searchBorder = ColorLuminance($SrcCol, -0.4); // %40 Darker\r\nif (getBrightness($SrcCol) > 128) {$tcol = "000000";} else {$tcol = "ffffff";}\r\n$searchText = $tcol;\r\n\r\n$lkupcodeBackground = $SrcCol;\r\n$lkupcodeBorder = ColorLuminance($SrcCol, -0.4); // %40 Darker\r\nif (getBrightness($SrcCol) > 128) {$tcol = "000000";} else {$tcol = "ffffff";}\r\n$lkupcodeText = $tcol;\r\n// ================================ CALCULATED COLOURS =====================================\r\n\r\n// BUTTONS\r\n$SQL = "SELECT zzsys_style_id FROM zzsys_style WHERE (sst_class = ''.button'') or (sst_class = ''.actionButton'') or (sst_class = ''.calbutton'')";\r\n$res = nuRunQuery($SQL);\r\nwhile($row = db_fetch_array($res)) {\r\n	nuRunQuery("UPDATE zzsys_style_property SET ssp_property_value = ''#" . $buttonBackground . "'' WHERE (ssp_zzsys_style_id = ''" . $row[''zzsys_style_id''] . "'') AND (ssp_property_name = ''background-color'')");\r\n	nuRunQuery("UPDATE zzsys_style_property SET ssp_property_value = ''#" . $buttonBorder . "'' WHERE (ssp_zzsys_style_id = ''" . $row[''zzsys_style_id''] . "'') AND (ssp_property_name = ''border-color'')");\r\n	nuRunQuery("UPDATE zzsys_style_property SET ssp_property_value = ''#" . $buttonText . "'' WHERE (ssp_zzsys_style_id = ''" . $row[''zzsys_style_id''] . "'') AND (ssp_property_name = ''color'')");\r\n}\r\n\r\n// SEARCH\r\n$SQL = "SELECT zzsys_style_id FROM zzsys_style WHERE (sst_class = ''.searchcolor'')";\r\n$res = nuRunQuery($SQL);\r\nwhile($row = db_fetch_array($res)) {\r\n	nuRunQuery("UPDATE zzsys_style_property SET ssp_property_value = ''#" . $searchBackground . "'' WHERE (ssp_zzsys_style_id = ''" . $row[''zzsys_style_id''] . "'') AND (ssp_property_name = ''background-color'')");\r\n	nuRunQuery("UPDATE zzsys_style_property SET ssp_property_value = ''#" . $searchBorder . "'' WHERE (ssp_zzsys_style_id = ''" . $row[''zzsys_style_id''] . "'') AND (ssp_property_name = ''border-color'')");\r\n	nuRunQuery("UPDATE zzsys_style_property SET ssp_property_value = ''#" . $searchText . "'' WHERE (ssp_zzsys_style_id = ''" . $row[''zzsys_style_id''] . "'') AND (ssp_property_name = ''color'')");\r\n}\r\n\r\n// LOOKUP CODE\r\n$SQL = "SELECT zzsys_style_id FROM zzsys_style WHERE (sst_class = ''.lookupcode'')";\r\n$res = nuRunQuery($SQL);\r\nwhile($row = db_fetch_array($res)) {\r\n	nuRunQuery("UPDATE zzsys_style_property SET ssp_property_value = ''#" . $lkupcodeBackground . "'' WHERE (ssp_zzsys_style_id = ''" . $row[''zzsys_style_id''] . "'') AND (ssp_property_name = ''background-color'')");\r\n	nuRunQuery("UPDATE zzsys_style_property SET ssp_property_value = ''#" . $lkupcodeBorder . "'' WHERE (ssp_zzsys_style_id = ''" . $row[''zzsys_style_id''] . "'') AND (ssp_property_name = ''border-color'')");\r\n	nuRunQuery("UPDATE zzsys_style_property SET ssp_property_value = ''#" . $lkupcodeText . "'' WHERE (ssp_zzsys_style_id = ''" . $row[''zzsys_style_id''] . "'') AND (ssp_property_name = ''color'')");\r\n}\r\n\r\n// LOOKUP DESC\r\n$SQL = "SELECT zzsys_style_id FROM zzsys_style WHERE (sst_class = ''.lookupdesc'')";\r\n$res = nuRunQuery($SQL);\r\nwhile($row = db_fetch_array($res)) {\r\n	nuRunQuery("UPDATE zzsys_style_property SET ssp_property_value = ''#" . $lkupdescBackground . "'' WHERE (ssp_zzsys_style_id = ''" . $row[''zzsys_style_id''] . "'') AND (ssp_property_name = ''background-color'')");\r\n	nuRunQuery("UPDATE zzsys_style_property SET ssp_property_value = ''#" . $lkupdescBorder . "'' WHERE (ssp_zzsys_style_id = ''" . $row[''zzsys_style_id''] . "'') AND (ssp_property_name = ''border-color'')");\r\n	nuRunQuery("UPDATE zzsys_style_property SET ssp_property_value = ''#" . $lkupdescText . "'' WHERE (ssp_zzsys_style_id = ''" . $row[''zzsys_style_id''] . "'') AND (ssp_property_name = ''color'')");\r\n}\r\n\r\n// BROWSE\r\n$SQL = "SELECT zzsys_style_id FROM zzsys_style WHERE (sst_class = ''.nuUnselectedRow'') or (sst_class = ''.selected'')";\r\n$res = nuRunQuery($SQL);\r\nwhile($row = db_fetch_array($res)) {\r\n	nuRunQuery("UPDATE zzsys_style_property SET ssp_property_value = ''#" . $browseBackground . "'' WHERE (ssp_zzsys_style_id = ''" . $row[''zzsys_style_id''] . "'') AND (ssp_property_name = ''background-color'')");\r\n	nuRunQuery("UPDATE zzsys_style_property SET ssp_property_value = ''#" . $browseBorder . "'' WHERE (ssp_zzsys_style_id = ''" . $row[''zzsys_style_id''] . "'') AND (ssp_property_name = ''border-color'')");\r\n	nuRunQuery("UPDATE zzsys_style_property SET ssp_property_value = ''#" . $br