('150874640315e6', 'procedure', 'TESTEMAIL', 'Test Email Settings', 'setup', '14599715516e37', NULL, '', NULL, '', NULL, '2', NULL, '', '', '', '', NULL, 'require_once("emaillib.php");\r\necho "<pre>";\r\necho "Testing Email Settings\\n======================\\n";\r\n\r\n$sql="SELECT * FROM zzsys_setup";\r\n$res = nuRunQuery($sql);\r\nif($row = db_fetch_array($res)) {\r\n  $SMTPhost = $row[''set_smtp_host''];\r\n  $SMTPport = $row[''set_smtp_port''];\r\n  $SMTPauth = $row[''set_smtp_use_ssl''];\r\n  $SMTPuser = $row[''set_smtp_username''];\r\n  $SMTPpass = $row[''set_smtp_password''];\r\n  $SMTPfrom = $row[''set_smtp_from_address''];\r\n  $SMTPfnam = $row[''set_smtp_from_name''];\r\n\r\n  echo "\\nSMTPhost: " .$SMTPhost;\r\n  echo "\\nSMTPport: " .$SMTPport;\r\n  echo "\\nSMTPauth: " .$SMTPauth;\r\n  echo "\\nSMTPuser: " .$SMTPuser;\r\n  echo "\\nSMTPpass: " .$SMTPpass;\r\n  echo "\\nSMTPfrom: " .$SMTPfrom;\r\n  echo "\\nSMTPfnam: " .$SMTPfnam;\r\n\r\n  $content = "<H1>Test Email Settings</h1>";\r\n  $subject = "Test Email Settings";\r\n\r\n  $mail              = new PHPMailer();\r\n  $mail->IsSMTP();\r\n  $mail->Host          = $SMTPhost;\r\n  $mail->Port          = $SMTPport;\r\n  $mail->SMTPSecure    = $SMTPauth ? ''ssl'' : '''';\r\n  $mail->SMTPAuth      = $SMTPauth;\r\n  if ($SMTPauth) {\r\n    $mail->Username  = $SMTPuser;\r\n    $mail->Password  = $SMTPpass;\r\n  }\r\n   \r\n  $mail->From        = $SMTPfrom;\r\n  $mail->FromName    = $SMTPfnam;\r\n  $mail->AddAddress($SMTPfrom,$SMTPfnam);\r\n  $mail->AddReplyTo($SMTPfrom,$SMTPfnam);\r\n \r\n  $mail->AddReplyTo  = $SMTPfrom;\r\n  $mail->AddAddress  = $SMTPfrom;\r\n  $mail->WordWrap    = 80;\r\n  $mail->IsHTML(true);\r\n  $mail->Subject     = $subject;\r\n  $mail->Body        = $content;\r\n\r\n  echo "\\n\\nPhpMailer Says:\\n================\\n\\n";\r\n  if(!$mail->Send()) {\r\n    echo "Error sending: " . $mail->ErrorInfo;;\r\n  } else {\r\n    echo "Test sent";\r\n  } \r\n \r\n}\r\necho "</pre>";', '2012-10-2