('object', 'Objects', 'nuBuilder Objects', NULL, NULL, '0', '1', NULL, 'SELECT form_name, zzsys_object.* FROM zzsys_object\r\nINNER JOIN #browseTable# ON zzsys_object.sob_zzsys_form_id = #browseTable#.form_id \r\nWHERE (sys_setup != ''10'') \r\nORDER BY form_name, sob_all_tab_number, sob_all_column_number, sob_all_order_number \r\n', 'zzsys_object', 'zzsys_object_id', '1', '1', '1', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', '', '', '', NULL, NULL, '', '', '', '', '$sql  = "CREATE TABLE #browseTable# SELECT zzsys_form_id AS form_id, CONCAT(sfo_name,'' (form)'') AS form_name FROM zzsys_form ";\r\nnuRunQuery($sql);\r\n\r\n$sql  = "INSERT INTO #browseTable# SELECT zzsys_object_id AS form_id,";\r\n$sql .= "CONCAT(sob_all_name,'' (subform)'') AS form_name ";\r\n$sql .= "FROM zzsys_object WHERE sob_all_type = ''subform''";\r\nnuRunQuery($sql);\r\n\r\n\r\n', '$sql  = "SELECT zzsys_object_id as ID FROM zzsys_object WHERE sob_all_type = ''subform''";\r\n$t    = nuRunQuery($sql);\r\n$s    = "\\n\\nvar sfIDs = new Array();\\n";\r\nwhile($r = db_fetch_object($t)){\r\n   $s.= "sfIDs[''$r->ID''] = ''1'';\\n";\r\n}\r\naddJSFunction($s);\r\n\r\n\r\n\r\n', '', '', '', '// Function will test that the object does not contain any blanks or illegal parent/child relationships (e.g. subform within a subform)\r\n// Function will alert the user of where the error occured\r\nfunction validateObjectFields() {\r\n	\r\n	var objType = document.getElementById(''sob_all_type'').value;\r\n	var displOn = document.getElementById(''sob_zzsys_form_id'').value\r\n	var parentType = sfIDs[displOn];\r\n\r\n/*\r\n	Main validation functions:\r\n\r\n	 nuValidatePositiveNumber(fieldName,labelName,[alternateMessage]) &&\r\n	 nuValidateNonNegativeNumber(fieldName,labelName,[alternateMessage]) &&\r\n	 nuValidateNonBlank(fieldName,labelName,[alternateMessage]) &&\r\n	 nuValidateNonZero(fieldName,labelName,[alternateMessage]) &&\r\n	 validateNotOnSubform(fieldTypeName,parentType)\r\n*/\r\n\r\n	\r\n	//-- All object types must have a form to be displayed upon, a field name and tab, column & order numbers  --//\r\n	var returnCode = (	( nuValidateNonBlank(''sob_zzsys_form_id'',''Displayed On'') ) &&\r\n		nuValidateNonBlank(''sob_all_type'',''Type'') &&\r\n		nuValidateNonBlank(''sob_all_name'',''Field Name'') &&\r\n		nuValidateNonBlank(''sob_all_tab_number'',''Tab Number'') &&\r\n		nuValidateNonBlank(''sob_all_column_number'',''Column Number'') &&\r\n		nuValidateNonBlank(''sob_all_order_number'',''Order Number'') );\r\n	\r\n	//-- Different criteria apply for each object type selected in ''All'' Tab --//\r\n	if (returnCode) {\r\n	\r\n		switch(objType) {\r\n		\r\n			case ''button'':\r\n				returnCode = nuValidatePositiveNumber(''sob_button_length'',''Button > Length'');\r\n			break;\r\n			\r\n			case ''display'':\r\n				returnCode = (nuValidatePositiveNumber(''sob_display_length'',''Display > Length'') &&\r\n						nuValidateNonBlank(''sob_display_sql'',''SQL to Display'') );\r\n			break;\r\n			\r\n			case ''dropdown'':\r\n				returnCode = (nuValidatePositiveNumber(''sob_dropdown_length'',''Dropdown > Length'') &&\r\n						nuValidateNonBlank(''sob_dropdown_sql'',''SQL'') );\r\n			break;\r\n\r\n			case ''graph'':\r\n				returnCode = (validateNotOnSubform(''Graph'',parentType) &&\r\n						nuValidateNonBlank(''sob_graph_code'',''Graph Code'') && \r\n						nuValidateNonBlank(''sob_graph_zzsys_graph_id'',''Graph Type'')	);\r\n			break;\r\n			\r\n			case ''html'':\r\n				returnCode = validateNotOnSubform(''HTML'',parentType);\r\n			break;\r\n			\r\n			case ''listbox'':\r\n				returnCode = (	validateNotOnSubform(''ListBox'',parentType) &&\r\n							nuValidatePositiveNumber(''sob_listbox_height'',''Listbox > Height'') &&\r\n							nuValidatePositiveNumber(''sob_listbox_length'',''Listbox > Length'') &&\r\n							nuValidateNonBlank(''sob_listbox_sql'',''Listbox > SQL'') );\r\n			break;\r\n\r\n			case ''lookup'':\r\n				returnCode = (	nuValidateNonBlank(''sob_lookup_id_field'',''Lookup > ID Field'')  &&\r\n							nuValidateNonBlank(''sob_lookup_code_field'',''Lookup > Code Field'') &&\r\n							nuValidateNonBlank(''sob_lookup_description_field'',''Lookup > Description Field'') &&\r\n							nuValidatePositiveNumber(''sob_lookup_code_length'',''Lookup > Code Length'') );\r\n			break;\r\n			\r\n			case ''subform'':\r\n				if(document.getElementById(''sob_subform_zzsysform_id'').value == ''''){\r\n					returnCode = (	validateNotOnSubform(''SubForm'',parentType) &&\r\n								nuValidateNonBlank(''sob_subform_table'',''Subform > Table'') &&\r\n								nuValidateNonBlank(''sob_subform_primary_key'',''Subform > Primary Key'') &&\r\n								nuValidateNonBlank(''sob_subform_foreign_key'',''Subform > Foreign Key'') &&\r\n								nuValidateNonBlank(''sob_subform_sql'',''Subform > SQL'') &&\r\n								nuValidateNonNegativeNumber(''sob_subform_left'',''Subform > Left Coordinate'') &&\r\n								nuValidateNonNegativeNumber(''sob_subform_top'',''Subform > Top'') &&\r\n								nuValidatePositiveNumber(''sob_subform_height'',''Subform > Height'') &&\r\n								nuValidateNonNegativeNumber(''sob_subform_width'',''Subform > Width'') &&\r\n								nuValidatePositiveNumber(''sob_subform_title_height'',''Subform > Title Height'') );\r\n				}else{\r\n					returnCode = (	validateNotOnSubform(''SubForm'',parentType) &&\r\n								nuValidateNonNegativeNumber(''sob_subform_left'',''Subform > Left Coordinate'') &&\r\n								nuValidateNonNegativeNumber(''sob_subform_top'',''Subform > Top'') &&\r\n								nuValidatePositiveNumber(''sob_subform_height'',''Subform > Height'') &&\r\n								nuValidateNonNegativeNumber(''sob_subform_width'',''Subform > Width'') );\r\n				}\r\n			break;\r\n			\r\n			case ''text'':\r\n				//returnCode = nuValidatePositiveNumber(''sob_text_length'',''Text > Length'');\r\n			break;\r\n			\r\n			case ''textarea'':\r\n				returnCode = (	nuValidatePositiveNumber(''sob_textarea_height'',''TextArea > Height'') &&\r\n							nuValidatePositiveNumber(''sob_textarea_length'',''TextArea > Length'') );\r\n			break;\r\n\r\n			case ''words'':\r\n			//Do nothing\r\n			break;\r\n		}\r\n	}\r\n	return returnCode;\r\n}\r\n\r\n//Function checks to see if a value is a positive number (greater than zero)\r\nfunction nuValidatePositiveNumber(fieldName,labelName,altMessage) {\r\n\r\n	var val = document.getElementById(fieldName).value;\r\n	\r\n    if ( val == '''' || isNaN(parseFloat(val)) ||  parseInt(val) <= 0  ) {\r\n		var message;\r\n		if (altMessage == undefined || altMessage == null) { \r\n			message = ''"'' + labelName + ''" must be a number greater than zero'';\r\n		} else {\r\n			message = altMessage;\r\n		}\r\n		alert(message);\r\n		return false;\r\n    } else {\r\n		return true;\r\n	}\r\n}\r\n\r\n//Function checks to see if a value is a number greater than or equal to zero\r\nfunction nuValidateNonNegativeNumber(fieldName,labelName,altMessage) {\r\n\r\n	var val = document.getElementById(fieldName).value;\r\n\r\n    if ( val == '''' || isNaN(parseFloat(val)) || ( parseInt(val) < 0 ) ) {\r\n		var message;\r\n		if (altMessage == undefined || altMessage == null) { \r\n			message = ''"'' + labelName + ''" must be a number greater than or equal to zero'';\r\n		} else {\r\n			message = altMessage;\r\n		}\r\n		alert(message);\r\n		return false;\r\n    } else {\r\n		return true;\r\n	}\r\n}\r\n\r\nfunction nuValidateNonZero(fieldName,labelName,altMessage) {\r\n\r\n	var val = document.getElementById(fieldName).value;\r\n\r\n    if ( parseInt(val) == 0 ) {\r\n		var message;\r\n		if (altMessage == undefined || altMessage == null) { \r\n			message = ''"'' + labelName + ''" cannot be equal to zero'';\r\n		} else {\r\n			message = altMessage;\r\n		}\r\n		alert(message);\r\n		return false;\r\n    } else {\r\n		return true;\r\n	}\r\n}\r\n\r\nfunction nuValidateNonBlank(fieldName,labelName,altMessage) {\r\n	\r\n	var field = document.getElementById(fieldName);\r\n	if (field.type == "select") {\r\n		field = field.options[field.selectedIndex];\r\n	}\r\n	var val = field.value;\r\n	\r\n    if ( val == '''' || val == null ) {\r\n		var message;\r\n		if (altMessage == undefined || altMessage == null) { \r\n			message = ''"'' + labelName + ''" cannot be left blank'';\r\n		} else {\r\n			message = altMessage;\r\n		}\r\n		alert(message);\r\n		return false;\r\n    } else {\r\n		return true;\r\n	}\r\n}\r\n\r\nfunction validateNotOnSubform(fieldTypeName,parentType) {\r\n	if (parentType == ''1'') {\r\n		alert (''A "'' + fieldTypeName + ''" object cannot be placed onto a SubForm'');\r\n		return false;\r\n	} else return true;\r\n\r\n}\r\n\r\nfunction nuBeforeSave(){\r\n	return( validateObjectFields() );\r\n}\r\n\r\n$(function(){\r\n    // SQL inlines\r\n    var sqlInlines = [\r\n        ''sob_all_display_condition'',\r\n        ''sob_all_sql_run_before_display'',\r\n        ''sob_display_sql'',\r\n        ''sob_dropdown_default_value_sql'',\r\n        ''sob_dropdown_sql'',\r\n        ''sob_listbox_sql'',\r\n        ''sob_lookup_default_value_sql'',\r\n        ''sob_subform_sql'',\r\n        ''sob_text_default_value_sql'',\r\n        ''sob_textarea_default_value_sql'',\r\n    ]\r\n    $(''#'' + sqlInlines.join('',#'')).each(function(){\r\n        nuInlineEditor(this.id, ''text/x-plsql'');\r\n    });\r\n    \r\n    // Javascript inlines\r\n    var jsInlines = [\r\n        ''sob_all_on_blur'',\r\n        ''sob_all_on_change'',\r\n        ''sob_all_on_double_click'',\r\n        ''sob_all_on_focus'',\r\n        ''sob_all_on_key_press'',\r\n        ''sob_lookup_javascript'',\r\n    ]\r\n    $(''#'' + jsInlines.join('',#'')).each(function(){\r\n        nuInlineEditor(this.id, ''javascript'');\r\n    });\r\n    \r\n    // PHP inlines\r\n    nuInlineEditor(''sob_graph_code'', ''text/x-php'');\r\n    \r\n    // HTML inlines\r\n    nuInlineEditor(''sob_html_code'', ''text/html'');\r\n});', NULL, NULL, '1', NULL, '2012-05-06 08:02:09', 'globeadmin', '', NULL, 0, 0, 0, ''),
('form', 'Forms', 'nuBuilder Form', NULL, NULL, '0', '1', '', 'SELECT * FROM zzsys_form \r\nWHERE (sys_setup != ''1'')\r\nORDER BY sfo_name \r\n', 'zzsys_form', 'zzsys_form_id', '1', '1', '1', '1', '', NULL, '', '', '', '', '', NULL, '', 'SELECT 1', '', '', '', NULL, '', NULL, '', '', NULL, NULL, '\n	$setup       = nuSetup();\n	$tbl         = nuRunQuery("SELECT * FROM zzsys_form WHERE zzsys_form_id = ''form''");\n	$rec         = db_fetch_array($tbl);\n\n	if($setup->set_box_net != '''' and array_key_exists(''sys_box_net'', $rec) and ''#recordID#'' != ''#newID#''){\n		$url     = "https://www.box.net/api/1.0/rest?action=create_folder&api_key=$setup->set_box_net_key&auth_token=$setup->set_box_net_token&parent_id=$setup->set_box_net&name=#newID#&share=1";  \n		$ch      = curl_init();  \n		// set URL and other appropriate options  \n		curl_setopt($ch, CURLOPT_URL, $url);  \n		curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, false);\n		curl_setopt($ch, CURLOPT_HEADER, 0);  \n		curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);  \n		  \n		// grab URL and pass it to the browser  \n		$output  = curl_exec($ch);  \n		$array   = json_decode(json_encode((array) simplexml_load_string($output)),1); \n		curl_close($ch);  \n\n		nuRunQuery("UPDATE zzsys_form SET sys_box_net = ''" . $array[''folder''][''folder_id''] . "'' WHERE zzsys_form_id = ''#newID#''");\n	}\n', NULL, '0', 'function addToSort(pthis){\r\n\r\n   var ssort = pthis.id.substring(0,17) + "sbr_sort";\r\n   if(document.getElementById(ssort).value == ""){\r\n      document.getElementById(ssort).value = pthis.value;\r\n   }\r\n\r\n}\r\n\r\n$(function(){\r\n    // Setup main editor\r\n    nuEditor(''main_editor'');\r\n\r\n    // SQL inlines\r\n    var sqlInlines = [\r\n        ''sfo_sql'', \r\n        ''sfo_add_button_display_condition'', \r\n        ''sfo_save_button_display_condition'', \r\n        ''sfo_delete_button_display_condition'', \r\n        ''sfo_clone_button_display_condition'', \r\n        ''sfo_close_button_display_condition'', \r\n        ''sfo_print_button_display_condition''\r\n    ];\r\n    $(''#'' + sqlInlines.join('',#'') + '', [id^=extraActionButtons][id$=sfa_button_display_condition]'').each(function(){\r\n        nuInlineEditor(this.id, ''text/x-plsql'');\r\n    });\r\n    \r\n    // JS inlines\r\n    $(''[id^=extraActionButtons][id$=sfa_button_javascript]'').each(function(){\r\n        nuInlineEditor(this.id, ''javascript'');\r\n    });\r\n});\r\n', NULL, NULL, '1', NULL, '2009-05-04 11:20:41', 'globeadmin', NULL, NULL, 0, 0, 0, ''),
