('run', 'Run', 'Run Activity', NULL, NULL, '0', '1', '', 'SELECT * FROM #browseTable# ', 'zzsys_activity', 'zzsys_activity_id', '0', '0', '1', '1', '', NULL, '', '', '', '', '', NULL, '', '', '', '', '', NULL, '', NULL, '', '', '$currentAccessLevel = ''#access_level#'';\r\n$currentUserId      = ''#zzsys_user_id#'';\r\n\r\n$sql_default  = "CREATE TABLE $browseTable SELECT zzsys_activity.* FROM zzsys_access_level ";\r\n$sql_default .= "INNER JOIN zzsys_access_level_activity ON zzsys_access_level_id = sav_zzsys_access_level_id ";\r\n$sql_default .= "INNER JOIN zzsys_activity ON sav_zzsys_activity_id = zzsys_activity_id ";\r\n$sql_default .= "WHERE (''$currentAccessLevel'' = ''globeadmin'' OR sal_name = ''$currentAccessLevel'') ";\r\n$sql_default .= "GROUP BY zzsys_activity_id ";\r\n\r\n$sql_globeadmin = "CREATE TABLE $browseTable SELECT * FROM zzsys_activity ";\r\n\r\nif ($currentUserId == ''globeadmin'') {\r\n	nuRunQuery($sql_globeadmin);\r\n} else  {\r\n	nuRunQuery($sql_default);\r\n} ', NULL, NULL, NULL, '0', '', NULL, NULL, '1', '2006-12-31 04:56:34', '2009-03-26 12:21:55', 'globeadmin', NULL, NULL, 0, 0, 0, ''),
