lateFieldList();\r\n\r\n}\r\n\r\n\r\nfunction moveUnSelected(){\r\n\r\n   var sfieldLB    = document.getElementById("SelectedFieldNames");\r\n\r\n   for(var sc = sfieldLB.length - 1 ; sc >= 0 ; sc--){\r\n\r\n      if(sfieldLB.options[sc].selected){\r\n\r\n         sfieldLB.remove(sc) //removes last option within SELECT\r\n\r\n      }\r\n   }\r\n\r\n//   populateFieldList();\r\n\r\n}\r\n\r\n\r\nfunction moveUnSelected2(){\r\n\r\n   var sfieldLB    = document.getElementById("BrowseColumns");\r\n\r\n   for(var sc = sfieldLB.length - 1 ; sc >= 0 ; sc--){\r\n\r\n      if(sfieldLB.options[sc].selected){\r\n\r\n         sfieldLB.remove(sc) //removes last option within SELECT\r\n\r\n      }\r\n   }\r\n\r\n//   populateFieldList();\r\n\r\n}\r\n\r\nfunction updatesysvariables(){\r\n\r\n  var TheListBox = document.getElementById(''SelectedFieldNames'');\r\n  for(i=0;i<TheListBox.length;i++){\r\n   TheListBox[i].selected=true;\r\n  }\r\n\r\n  uDB(document.getElementById(''theform'').SelectedFieldNames,''listbox'');\r\n\r\n  for(i=0;i<TheListBox.length;i++){\r\n   TheListBox[i].selected=false;\r\n  }\r\n\r\n}\r\n\r\nfunction updatesysvariables2(){\r\n\r\n  var TheListBox = document.getElementById(''BrowseColumns'');\r\n  for(i=0;i<TheListBox.length;i++){\r\n   TheListBox[i].selected=true;\r\n  }\r\n\r\n  uDB(document.getElementById(''theform'').BrowseColumns,''listbox'');\r\n\r\n  for(i=0;i<TheListBox.length;i++){\r\n   TheListBox[i].selected=false;\r\n  }\r\n\r\n}\r\n\r\n\r\n', '', '', '1', '2010-09-13 15:59:02', '2010-11-25 13:34:50', 'globeadmin', '', NULL, 0, 0, 0, ''),
