('14cc901c1b3675', 'report', 'FM016BACKUP', 'Profit & Loss Statement BACKUP', 'Ledger', '145974108a4084', NULL, '	$fromdate       = msql_date_nq($formValue[''vFrom'']);\r\n	$todate         = msql_date_nq($formValue[''vTo'']);\r\n\r\n	//create a list of all Aledgers that have parents\r\n	$sql   = "CREATE TABLE $dataTable"."childledger ";\r\n	$sql  .= "SELECT ledger_id, led_type FROM ledger ";\r\n	$sql  .= "WHERE ledger_id NOT IN ( SELECT ledger.ledger_id FROM ledger INNER JOIN ledger as child ON child.led_parent_ledger_id = ledger.ledger_id )";  		\r\n	$temp  = nuRunQuery($sql);\r\n\r\n	//create a list of all ledgers that are top level parents \r\n	$sql  = "CREATE TABLE $dataTable"."headerledger SELECT * FROM ledger WHERE led_parent_ledger_id IS NULL";\r\n	$header = nuRunQuery($sql);    \r\n\r\n	//create a combined list of all ledgers\r\n	$sql   = "CREATE TABLE $dataTable"."temp SELECT * FROM ledger ";\r\n	nuRunQuery($sql);\r\n	nuRunQuery("ALTER TABLE $dataTable"."temp ADD Level INT ");\r\n\r\n	$sql    = "SELECT header.ledger_id AS headerledgerID, child.ledger_id AS childledgerID FROM $dataTable"."headerledger AS header ";\r\n	$sql   .= "LEFT JOIN $dataTable"."childledger AS child ON child.led_type = header.led_type";\r\n	$temp1   = nuRunQuery($sql);\r\n\r\n	//set all ledgers to a lowest level of 1000\r\n	while($ledger = db_fetch_object($temp1)) {\r\n		$header = $ledger->headerledgerID;\r\n		$child  = $ledger->childledgerID;\r\n		nuRunQuery("UPDATE $dataTable"."temp SET Level = 1000 WHERE ledger_id = ''$child''");\r\n\r\n		//cycle through all header ledgers and reduce level by 1 at each child\r\n		while($header != $child) {\r\n			$sql  = "SELECT led_parent_ledger_id, Level FROM $dataTable"."temp WHERE ledger_id = ''$child''";\r\n			$temp = nuRunQuery($sql);\r\n		\r\n			//subtract 1 from the current level\r\n			if($obj = db_fetch_object($temp)) {\r\n				$Level = $obj->Level -1;\r\n				$child = $obj->led_parent_ledger_id;\r\n				nuRunQuery("UPDATE $dataTable"."temp SET Level = $Level WHERE (ledger_id = ''$child'' AND Level>$Level) OR Level IS NULL");\r\n			} \r\n		}\r\n	}	\r\n\r\n	nuRunQuery("ALTER TABLE $dataTable"."temp ADD minLevel INT ");\r\n\r\n	//find the minimum level that has been set\r\n	$sql  = "SELECT MIN(Level) AS minLevel,led_type FROM $dataTable"."temp GROUP BY led_type";\r\n	$temp = nuRunQuery($sql);\r\n\r\n	//set the minimum level of all ledgers\r\n	while($obj = db_fetch_object($temp) ) {\r\n		$ledType  = $obj->led_type;\r\n		$minLevel = $obj->minLevel;\r\n		nuRunQuery("UPDATE $dataTable"."temp SET minLevel = $minLevel WHERE led_type = ''$ledType'' ");\r\n	}\r\n	\r\n	//subtract minimum level from current level to make the level numbers more reasonable\r\n	nuRunQuery("UPDATE $dataTable"."temp SET Level = Level - minLevel ");\r\n\r\n	$space = ''&nbsp&nbsp&nbsp'';\r\n	nuRunQuery("ALTER TABLE $dataTable"."temp ADD disledCode text, ADD disledName text ");\r\n	$sql  = "SELECT ledger_id, Level, led_code, led_name,led_type FROM $dataTable"."temp" ;\r\n	$temp = nuRunQuery($sql);\r\n	\r\n	//indent each ledger name by level\r\n	while($obj = db_fetch_object($temp)) {\r\n		$ledgerID = $obj->ledger_id;\r\n		$ledCode  = $obj->led_code;\r\n		$ledName  = $obj->led_name;\r\n		$ledType  = $obj->led_type;\r\n		$Level    = $obj->Level;\r\n\r\n		//indent by level\r\n		for($i=0; $i<=$Level;$i++) {\r\n			$ledName = $space.$ledName;\r\n			$ledCode = $space.$ledCode;\r\n		}\r\n		\r\n		nuRunQuery("UPDATE $dataTable"."temp SET disledName = \\"$ledName\\",disledCode = \\"$ledCode\\" WHERE ledger_id = ''$ledgerID'' ");\r\n	}\r\n\r\n	//create final data table ready for output\r\n	$sql  = "CREATE TABLE $dataTable SELECT ledger.ledger_id,ledger.led_type,ledger.led_code,ledger.disledName,ledger.disledCode, ";\r\n	$sql .= "SUM(ledger_log_item.lli_amount)AS balance, parentLedger.led_code AS pledCode ";\r\n	$sql .= "FROM $dataTable"."temp AS ledger ";\r\n	$sql .= "INNER JOIN ledger_log_item ON lli_ledger_id = ledger.ledger_id ";\r\n	$sql .= "INNER JOIN ledger_log     ON ledger_log_id = lli_ledger_log_id ";\r\n	$sql .= "INNER JOIN $dataTable"."headerledger AS parentLedger ON parentLedger.led_type = ledger.led_type ";\r\n	$sql .= "WHERE lld_date Between ''$fromdate'' AND ''$todate'' ";\r\n	$sql .= "Group BY ledger.ledger_id ";\r\n	nuRunQuery($sql); \r\n	\r\n	nuRunQuery("DROP TABLE $dataTable"."childledger ");\r\n	nuRunQuery("DROP TABLE $dataTable"."headerledger ");\r\n	nuRunQuery("DROP TABLE $dataTable"."temp ");\r\n\r\n	//Get General sale ledger amount\r\n	$sql            = "SELECT SUM(IF(balance<0,balance*-1,balance)) as balance FROM $dataTable WHERE led_code LIKE ''4-%''";\r\n	$saleLedger     = nuRunQuery($sql);\r\n		\r\n	while($saleLedgerObj  = db_fetch_object($saleLedger)) {\r\n		$sale           = iif($saleLedgerObj->balance == NULL OR $saleLedgerObj->balance == 0, 1 , $saleLedgerObj->balance);\r\n		$sale           = iif($sale<0, $sale*-1 ,$sale);\r\n\r\n		//create label and totals columns\r\n		nuRunQuery("ALTER TABLE $dataTable ADD percs double(10,2), ADD typeLabel1 varchar(255), ADD typeLabel2 varchar(255), ADD type1Balance Double(10,2),ADD type1Percentage Double(10,2), ADD type2Balance Double(10,2),ADD type2Percentage Double(10,2),ADD netProfit Double(10,2),ADD netProfitPerc Double(10,2)");\r\n		nuRunQuery("UPDATE $dataTable SET balance = balance *-1 WHERE balance <0 ");  \r\n		nuRunQuery("UPDATE $dataTable SET percs = (balance/$sale)*100 ");\r\n\r\n		nuRunQuery("UPDATE $dataTable SET typeLabel1 = ''Gross Profit'' WHERE led_type = ''Income'' OR led_type = ''CostofSales''");\r\n		nuRunQuery("UPDATE $dataTable SET typeLabel1 = ''Total Expense''WHERE led_type = ''Expense''");\r\n		nuRunQuery("UPDATE $dataTable SET typeLabel1 = ''Total Other Income''WHERE led_type = ''OtherIncome''");\r\n		nuRunQuery("UPDATE $dataTable SET typeLabel1 = ''Total Other Expense''WHERE led_type = ''OtherExpense''");\r\n\r\n		nuRunQuery("UPDATE $dataTable SET typeLabel2 = ''Operating Profit'' WHERE led_type IN (''Income'',''CostofSales'',''Expense'')");\r\n		nuRunQuery("UPDATE $dataTable SET typeLabel2 = ''Total Other'' WHERE led_type IN (''OtherIncome'')");\r\n		nuRunQuery("UPDATE $dataTable SET typeLabel2 = ''Total Other'' WHERE led_type IN (''OtherExpense'')");\r\n\r\n		// Calc Gross Profit And Total Expense\r\n		$sql  = "SELECT SUM(Balance) AS TotalIncome FROM $dataTable WHERE led_type = ''Income''";\r\n		$temp = nuRunQuery($sql);\r\n		$obj  = db_fetch_object($temp);\r\n		$totalIncome =  iif($obj->TotalIncome == NULL, 0,$obj->TotalIncome);\r\n\r\n		$sql  = "SELECT SUM(Balance) AS TotalCostofSales FROM $dataTable WHERE led_type = ''CostofSales''";\r\n		$temp = nuRunQuery($sql);\r\n		$obj  = db_fetch_object($temp);\r\n		$totalCostofSales =  iif($obj->TotalCostofSales == NULL, 0,$obj->TotalCostofSales);\r\n\r\n		$grossprofit      = $totalIncome-$totalCostofSales;\r\n		$grossprofitPerc = bcdiv($grossprofit,$sale,4)*100;\r\n\r\n		nuRunQuery("UPDATE $dataTable SET type1Balance = 0$grossprofit,  type1Percentage = $grossprofitPerc WHERE typeLabel1 = ''Gross Profit''" );\r\n\r\n		$sql  = "SELECT SUM(Balance) AS TotalExpense FROM $dataTable WHERE led_type = ''Expense''";\r\n		$temp = nuRunQuery($sql);\r\n		$obj  = db_fetch_object($temp);\r\n		$totalExpense =  iif($obj->TotalExpense == NULL, 0,$obj->TotalExpense);  \r\n		$totalexpensePerc = bcdiv($totalExpense,$sale,4)*100;\r\n		nuRunQuery("UPDATE $dataTable SET type1Balance = 0$totalExpense,  type1Percentage = 0$totalexpensePerc WHERE typeLabel1 = ''Total Expense''" );\r\n\r\n		$operationProfit     = $grossprofit - $totalExpense;\r\n		$operationProfitPerc = $grossprofitPerc - $totalexpensePerc;\r\n		nuRunQuery("UPDATE $dataTable SET type2Balance = 0$operationProfit,  type2Percentage = 0$operationProfitPerc WHERE typeLabel2 = ''Operating Profit''" );\r\n\r\n		$sql  = "SELECT SUM(Balance) AS TotalOtherIncome FROM $dataTable WHERE led_type = ''OtherIncome''";\r\n		$temp = nuRunQuery($sql);\r\n		$obj  = db_fetch_object($temp);\r\n		$totalOtherIncome =  iif($obj->TotalOtherIncome == NULL, 0,$obj->TotalOtherIncome);\r\n		$totalOtherIncomePerc = bcdiv($totalOtherIncome,$sale,4)*100;\r\n		nuRunQuery("UPDATE $dataTable SET type1Balance = $totalOtherIncome,  type1Percentage = $totalOtherIncomePerc,type2Balance = $totalOtherIncome,  type2Percentage = $totalOtherIncomePerc WHERE typeLabel1 = ''Total Other Income''" );\r\n\r\n		$sql  = "SELECT SUM(Balance) AS TotalOtherExpense FROM $dataTable WHERE led_type = ''OtherExpense''";\r\n		$temp = nuRunQuery($sql);\r\n		$obj  = db_fetch_object($temp);\r\n		$totalOtherExpense =  iif($obj->TotalOtherExpense == NULL, 0,$obj->TotalOtherExpense);\r\n		$totalOtherExpensePrec = bcdiv($totalOtherExpense,$sale,4)*100;\r\n		nuRunQuery("UPDATE $dataTable SET type1Balance = $totalOtherExpense,  type1Percentage = $totalOtherExpensePrec,type2Balance = $totalOtherExpense,  type2Percentage = $totalOtherExpensePrec WHERE typeLabel1 = ''Total Other Expense''" );\r\n		\r\n		$totalOther =  $totalOtherIncome - $totalOtherExpense;\r\n		$totalOtherPrec = $totalOtherIncomePerc - $totalOtherExpensePrec;\r\n		nuRunQuery("UPDATE $dataTable SET type2Balance = $totalOther,  type2Percentage = $totalOtherPrec WHERE typeLabel2 = ''Total Other''" );\r\n		\r\n		$netProfit     = $grossprofit - $totalExpense+$totalOtherIncome-$totalOtherExpense;\r\n		$netProfitPerc = $operationProfitPerc + $totalOtherIncomePerc - $totalOtherExpensePrec;\r\n		nuRunQuery("UPDATE $dataTable SET netProfit = $netProfit,  netProfitPerc = $netProfitPerc"); \r\n		\r\n		if($sale == 0 ) {\r\n			nuRunQuery("UPDATE $dataTable SET percs = 0, type2Percentage = 0, type1Percentage = 0");\r\n		}\r\n	}\r\n\r\n	nuRunQuery("DELETE FROM $dataTable WHERE typeLabel2 IS NULL");\r\n\r\n	$fms          = nuRunQuery("SELECT * FROM financial_module_setting");\r\n	$fmsObj       = db_fetch_object($fms);\r\n	$fmsName      = $fmsObj->fms_name;\r\n	$fmsAddress   = $fmsObj->fms_address; \r\n	$fmsCity      = $fmsObj->fms_city;\r\n	$fmsPostcode  = $fmsObj->fms_post_code;\r\n	$fmsPhone     = $fmsObj->fms_phone;\r\n	$fmsFax       = $fmsObj->fms_fax;\r\n	$fmsState     = $fmsObj->fms_state; \r\n\r\n	nuRunQuery("ALTER TABLE $dataTable ADD seCompany varchar(255), ADD seAddress varchar(255),ADD seCity varchar(100), ADD sePostcode varchar(20), ADD seState varchar(50),ADD sePhone varchar(20),ADD seFax varchar(20)");\r\n	$sql  = "UPDATE $dataTable SET seCompany = \\"$fmsName\\", seAddress = \\"$fmsAddress\\"," ;\r\n	$sql .=	"seCity = \\"$fmsCity\\", sePostcode = ''$fmsPostcode'', seState = \\"$fmsState\\", " ;\r\n	$sql .=	"sePhone = \\"$fmsPhone\\" ,seFax = ''$fmsFax''";\r\n	nuRunQuery($sql);\r\n\r\n	nuRunQuery("UPDATE $dataTable SET led_type = ''COST OF SALES'' WHERE led_type = ''COSTOFSALES''");\r\n	nuRunQuery("UPDATE $dataTable SET led_type = ''OTHER INCOME'' WHERE led_type = ''OTHERINCOME''");\r\n	nuRunQuery("UPDATE $dataTable SET led_type = ''OTHER EXPENSE'' WHERE led_type = ''OTHEREXPENSE''");', NULL, 'class Reporting{\r\n\r\n   var $nuBuilder          = ''1'';\r\n   var $Controls           = array();\r\n   var $Sections           = array();\r\n   var $Groups             = array();\r\n   var $Version            = ''3'';\r\n   var $Width              = ''825'';\r\n   var $Height             = ''1020'';\r\n   var $PaperType          = ''A4'';\r\n   var $Orientation        = ''p'';\r\n\r\n\r\n   function Reporting(){\r\n      $this->Controls[0]->Name                = ''Label64'';\r\n      $this->Controls[0]->Section             = ''3'';\r\n      $this->Controls[0]->ControlType         = ''Label'';\r\n      $this->Controls[0]->Tag                 = ''Label'';\r\n      $this->Controls[0]->ControlSource       = '''';\r\n      $this->Controls[0]->Caption             = '''';\r\n      $this->Controls[0]->Value               = '''';\r\n      $this->Controls[0]->Top                 = ''145'';\r\n      $this->Controls[0]->Left                = ''12'';\r\n      $this->Controls[0]->Width               = ''755'';\r\n      $this->Controls[0]->Heig