('1473126cae67e2', 'report', 'FM005', 'Trial Balance', 'Ledger', '1473125d467836', NULL, '	$lldPostable =  $formValue[''lld_postable''];\r\n	$toDate = msql_date_nq($formValue[''vTo'']);\r\n\r\n	// Get child ledger\r\n	$sql   = "CREATE TABLE $dataTable"."childledger ";\r\n	$sql  .= "SELECT ledger_id, led_type FROM ledger ";\r\n	$sql  .= "WHERE ledger_id NOT IN ( SELECT ledger.ledger_id FROM ledger INNER JOIN ledger as child ON child.led_parent_ledger_id = ledger.ledger_id )";  		\r\n	$temp  = nuRunQuery($sql);\r\n\r\n	// Get header ledger   \r\n	$sql  = "CREATE TABLE $dataTable"."headerledger SELECT * FROM ledger WHERE led_parent_ledger_id IS NULL";\r\n	$header = nuRunQuery($sql);    \r\n\r\n	$sql   = "CREATE TABLE $dataTable"."temp SELECT * FROM ledger ";\r\n	nuRunQuery($sql);\r\n	nuRunQuery("ALTER TABLE $dataTable"."temp ADD Level INT ");\r\n\r\n	$sql    = "SELECT header.ledger_id AS headerledgerID, child.ledger_id AS childledgerID FROM $dataTable"."headerledger AS header ";\r\n	$sql   .= "LEFT JOIN $dataTable"."childledger AS child ON child.led_type = header.led_type";\r\n	$temp1   = nuRunQuery($sql);\r\n\r\n	//set level of all children to level 1000 (maximum level)\r\n	while($ledger = db_fetch_object($temp1)) {\r\n		$header = $ledger->headerledgerID;\r\n		$child  = $ledger->childledgerID;\r\n		nuRunQuery("UPDATE $dataTable"."temp SET Level = 1000 WHERE ledger_id = ''$child''");\r\n\r\n		//for each level of child up to parent reduce the level\r\n		while($header != $child) {\r\n			// find uplevel\r\n			$sql  = "SELECT led_parent_ledger_id, Level FROM $dataTable"."temp WHERE ledger_id = ''$child''";\r\n			$temp = nuRunQuery($sql);\r\n			\r\n			//reduce level by 1\r\n			if($obj = db_fetch_object($temp)) {\r\n				$Level = $obj->Level -1;\r\n				$child = $obj->led_parent_ledger_id;\r\n				nuRunQuery("UPDATE $dataTable"."temp SET Level = $Level WHERE (ledger_id = ''$child'' AND Level>$Level) OR Level IS NULL");\r\n			} \r\n		}\r\n	}	\r\n\r\n	nuRunQuery("ALTER TABLE $dataTable"."temp ADD minLevel INT ");\r\n\r\n	$sql  = "SELECT MIN(Level) AS minLevel,led_type FROM $dataTable"."temp GROUP BY led_type";\r\n	$temp = nuRunQuery($sql);\r\n\r\n	//find the minimum level of all ledgers\r\n	while($obj = db_fetch_object($temp) ) {\r\n		$ledType  = $obj->led_type;\r\n		$minLevel = $obj->minLevel;\r\n		nuRunQuery("UPDATE $dataTable"."temp SET minLevel = $minLevel WHERE led_type = ''$ledType'' ");\r\n	}\r\n	\r\n	//reduce all levels by the minimul level for indentation\r\n	nuRunQuery("UPDATE $dataTable"."temp SET Level = Level - minLevel ");\r\n\r\n	$space = ''&nbsp&nbsp&nbsp'';\r\n	nuRunQuery("ALTER TABLE $dataTable"."temp ADD disledCode text, ADD disledName text ");\r\n	$sql  = "SELECT ledger_id, Level, led_code, led_name,led_type FROM $dataTable"."temp" ;\r\n	$temp = nuRunQuery($sql);\r\n	\r\n	//add indentations depending on level\r\n	while($obj = db_fetch_object($temp)) {\r\n		$ledgerID = $obj->ledger_id;\r\n		$ledCode  = $obj->led_code;\r\n		$ledName  = $obj->led_name;\r\n		$ledType  = $obj->led_type;\r\n		$Level    = $obj->Level;\r\n\r\n		//indent level\r\n		for($i=0; $i<=$Level;$i++) {\r\n			$ledName = $space.$ledName;\r\n			$ledCode = $space.$ledCode;\r\n		}	\r\n		nuRunQuery("UPDATE $dataTable"."temp SET disledName = \\"$ledName\\",disledCode = \\"$ledCode\\" WHERE ledger_id = ''$ledgerID'' ");\r\n	}\r\n\r\n	$sql  = "CREATE TABLE $dataTable SELECT ledger.ledger_id,ledger.led_type,ledger.led_code,ledger.disledName,ledger.disledCode, ";\r\n	$sql .= "SUM(ledger_log_item.lli_amount)AS Balance, parentLedger.led_code AS pledCode ";\r\n	$sql .= "FROM $dataTable"."temp AS ledger ";\r\n	$sql .= "INNER JOIN ledger_log_item ON lli_ledger_id = ledger.ledger_id ";\r\n	$sql .= "INNER JOIN $dataTable"."headerledger AS parentLedger ON parentLedger.led_type = ledger.led_type ";\r\n	$sql .= "INNER JOIN ledger_log     ON ledger_log_id = lli_ledger_log_id ";\r\n	$sql .= "WHERE lld_date > ''$toDate'' ";\r\n	\r\n	//if postable is yes then select posted record