INSERT INTO `zzsys_activity` (`zzsys_activity_id`, `sat_all_type`, `sat_all_code`, `sat_all_description`, `sat_all_group`, `sat_all_zzsys_form_id`, `sat_report_data_file`, `sat_report_data_code`, `sat_report_display_file`, `sat_report_display_code`, `sat_report_page_length`, `sat_report_display_type`, `sat_export_data_file`, `sat_export_data_code`, `sat_export_delimiter`, `sat_export_header`, `sat_export_use_quotes`, `sat_procedure_file`, `sat_procedure_code`, `sys_added`, `sys_changed`, `sys_user_id`) VALUES
('147312b85a160a', 'report', 'FM017', 'Balance Sheet', 'Ledger', '14599715516e37', NULL, '	include_once("FMLib.php");\r\n\r\n	$fromdate       = Today();\r\n	$fmSettingsObj  = getFMSettings();\r\n\r\n	//create a list of ledgers that have parents\r\n	$sql   = "CREATE TABLE $dataTable"."childledger ";\r\n	$sql  .= "SELECT ledger_id, led_type FROM ledger ";\r\n	$sql  .= "WHERE ledger_id NOT IN ( SELECT ledger.ledger_id FROM ledger INNER JOIN ledger as child ON child.led_parent_ledger_id = ledger.ledger_id )";  		\r\n	$temp  = nuRunQuery($sql);\r\n\r\n	// create a list of all top level parents \r\n	$sql  = "CREATE TABLE $dataTable"."headerledger SELECT * FROM ledger WHERE led_parent_ledger_id IS NULL";\r\n	$header = nuRunQuery($sql);    \r\n\r\n	//create a list of all ledgers\r\n	$sql   = "CREATE TABLE $dataTable"."temp SELECT * FROM ledger ";\r\n	nuRunQuery($sql);\r\n	nuRunQuery("ALTER TABLE $dataTable"."temp ADD Level INT ");\r\n	$sql    = "SELECT header.ledger_id AS headerledgerID, child.ledger_id AS childledgerID FROM $dataTable"."headerledger AS header ";\r\n	$sql   .= "LEFT JOIN $dataTable"."childledger AS child ON child.led_type = header.led_type";\r\n	$temp1   = nuRunQuery($sql);\r\n\r\n	// SET the lowest child a base level of 1000	\r\n	while($ledger = db_fetch_object($temp1)) {\r\n		$header = $ledger->headerledgerID;\r\n		$child  = $ledger->childledgerID;\r\n		nuRunQuery("UPDATE $dataTable"."temp SET Level = 1000 WHERE ledger_id = ''$child''");\r\n	\r\n		// find childrens parents\r\n		while($header != $child) {\r\n			$sql  = "SELECT led_parent_ledger_id, Level FROM $dataTable"."temp WHERE ledger_id = ''$child''";\r\n			$temp = nuRunQuery($sql);\r\n			\r\n			//set the parent one level higher\r\n			if($obj = db_fetch_object($temp)) {\r\n				$Level = $obj->Level -1;\r\n				$child = $obj->led_parent_ledger_id;\r\n				nuRunQuery("UPDATE $dataTable"."temp SET Level = $Level WHERE (ledger_id = ''$child'' AND Level>$Level) OR Level IS NULL");\r\n			} \r\n		}\r\n	}	\r\n\r\n	//find the minimum level of children\r\n	nuRunQuery("ALTER TABLE $dataTable"."temp ADD minLevel INT ");\r\n	$sql  = "SELECT MIN(Level) AS minLevel,led_type FROM $dataTable"."temp GROUP BY led_type";\r\n	$temp = nuRunQuery($sql);\r\n\r\n	// set the minimum level of all ledgers \r\n	while($obj = db_fetch_object($temp)) {\r\n		$ledType  = $obj->led_type;\r\n		$minLevel = $obj->minLevel;\r\n		nuRunQuery("UPDATE $dataTable"."temp SET minLevel = $minLevel WHERE led_type = ''$ledType'' ");\r\n	}\r\n	\r\n	//reduce level by subtracting the min level for more reasonable level numbers\r\n	nuRunQuery("UPDATE $dataTable"."temp SET Level = Level - minLevel ");\r\n	$sql  = "SELECT * FROM $dataTable"."temp ORDER BY Level ";   \r\n	$temp = nuRunQuery($sql);	\r\n\r\n	// Appending space for the ledcode, led_name according to their level\r\n	$space = ''&nbsp\\;&nbsp\\;&nbsp\\;'';\r\n	nuRunQuery("ALTER TABLE $dataTable"."temp ADD disledCode text, ADD disledName text ");\r\n	$sql  = "SELECT ledger_id, Level, led_code, led_name,led_type FROM $dataTable"."temp" ;\r\n	$temp = nuRunQuery($sql);\r\n	\r\n	//take each node for indentation\r\n	while($obj = db_fetch_object($temp)) {\r\n		$ledgerID = $obj->ledger_id;\r\n		$ledCode  = $obj->led_code;\r\n		$ledName  = $obj->led_name;\r\n		$ledType  = $obj->led_type;\r\n		$Level    = $obj->Level;\r\n\r\n		//indents to the desired level\r\n		for($i=0; $i<=$Level;$i++) {\r\n			$ledName = $space.$ledName;\r\n			$ledCode = $space.$ledCode;\r\n		}\r\n		nuRunQuery("UPDATE $dataTable"."temp SET disledName = \\"$ledName\\",disledCode = \\"$ledCode\\" WHERE ledger_id = ''$ledgerID'' ");\r\n	}\r\n\r\n	// Statement for all ledger transaction before today.\r\n	$sql  = "CREATE TABLE $dataTable SELECT ledger.ledger_id,ledger.led_type,ledger.led_code,ledger.disledName,ledger.disledCode, ";\r\n	$sql .= "SUM(ledger_log_item.lli_amount)AS Balance, parentLedger.led_code AS pledCode ";\r\n	$sql .= "FROM $dataTable"."temp AS ledger ";\r\n	$sql .= "INNER JOIN ledger_log_item ON lli_ledger_id = ledger.ledger_id ";\r\n	$sql .= "INNER JOIN ledger_log     ON ledger_log_id = lli_ledger_log_id ";\r\n	$sql .= "INNER JOIN $dataTable"."headerledger AS parentLedger ON parentLedger.led_type = ledger.led_type ";\r\n	$sql .= "WHERE lld_date <= ''$fromdate'' ";\r\n	$sql .= "Group BY ledger.ledger_id ";\r\n	nuRunQuery($sql); \r\n\r\n	nuRunQuery("DROP TABLE $dataTable"."childledger");\r\n	nuRunQuery("DROP TABLE $dataTable"."headerledger");\r\n	nuRunQuery("DROP TABLE $dataTable"."temp");\r\n\r\n	// Update balance to positive value \r\n	nuRunQuery("ALTER TABLE $dataTable ADD typeLabel1 varchar(255), ADD type1Balance Double(10,2),ADD EqualsNetAssets Double(10,2),ADD totalEquity Double(10,2)");\r\n	nuRunQuery("UPDATE $dataTable SET balance = balance *-1 WHERE balance <0 ");  \r\n\r\n	//SG 10/Aug/07\r\n	$gst_paid 		= getLedgerCode($fmSettingsObj->fmsGSTLedgerID);\r\n	$gst_remitted	= getLedgerCode($fmSettingsObj->fmsGSTRemittedLedgerID);\r\n\r\n	nuRunQuery("UPDATE $dataTable SET balance = balance *-1 WHERE led_code = ''$gst_paid''"); //GST Paid\r\n	nuRunQuery("UPDATE $dataTable SET balance = balance *-1 WHERE led_code = ''$gst_remitted''"); //GST Remitted\r\n\r\n	nuRunQuery("UPDATE $dataTable SET typeLabel1 = ''EQUALS WORKING CAPITAL'' WHERE (led_code Between ''1-0001'' AND ''1-1999'') OR (led_code Between ''2-1001'' AND ''2-1999'') ");\r\n	nuRunQuery("UPDATE $dataTable SET typeLabel1 = ''NONE-CURRENT ASSET''WHERE led_code Between ''1-4000'' AND ''1-9999''");\r\n	nuRunQuery("UPDATE $dataTable SET typeLabel1 = ''NONE-CURRENT LIABILITY'' WHERE led_code Between ''2-2000'' AND ''2-9999''");\r\n	nuRunQuery("UPDATE $dataTable SET typeLabel1 = '' '' WHERE led_type = ''EQUITY''");\r\n\r\n	nuRunQuery("UPDATE $dataTable SET led_type = ''CURRENT ASSET'' WHERE (led_code Between ''1-0001'' AND ''1-1999'')" );\r\n	nuRunQuery("UPDATE $dataTable SET led_type = ''CURRENT LIABILITY'' WHERE (led_code Between ''2-1001'' AND ''2-1999'')" );\r\n\r\n	// Calc EQUALS WORKING CAPITAL\r\n	// Get Current Assets ledger amount\r\n	$sql                    = "SELECT SUM(balance) AS currentAsset FROM $dataTable WHERE led_code Between ''1-0001'' AND ''1-1999''";\r\n	$currentAssetLedger     = nuRunQuery($sql);    \r\n	$currentAssetLedgerObj  = db_fetch_object($currentAssetLedger);\r\n	$currentAssetLedger     = iif($currentAssetLedgerObj->currentAsset == NULL, 0 , $currentAssetLedgerObj->currentAsset);\r\n\r\n	// Get Current Liability ledger amount   \r\n	$sql  = "SELECT SUM(Balance) AS currentLiability FROM $dataTable WHERE led_code Between ''2-1001'' AND ''2-1999''";\r\n	$temp = nuRunQuery($sql);\r\n	$obj  = db_fetch_object($temp);\r\n	$currentLiability =  iif($obj->currentLiability == NULL, 0,$obj->currentLiability);\r\n	$workingCapital   = $currentAssetLedger-$currentLiability;\r\n\r\n	nuRunQuery("UPDATE $dataTable SET type1Balance = 0$workingCapital WHERE typeLabel1 = ''EQUALS WORKING CAPITAL''" );\r\n\r\n	// Get None Current Assets ledger amount  \r\n	$sql  = "SELECT SUM(Balance) AS noneCurrentAsset FROM $dataTable WHERE led_code Between ''1-4000'' AND ''1-9999''";\r\n	$temp = nuRunQuery($sql);\r\n	$obj  = db_fetch_object($temp);\r\n	$noneCurrentAsset =  iif($obj->noneCurrentAsset == NULL, 0,$obj->noneCurrentAsset);  \r\n	nuRunQuery("UPDATE $dataTable SET type1Balance = 0$noneCurrentAsset WHERE typeLabel1 = ''NONE-CURRENT ASSET''" );\r\n	nuRunQuery("UPDATE $dataTable SET led_type = ''NONE-CURRENT ASSET'' WHERE typeLabel1 = ''NONE-CURRENT ASSET''" );\r\n\r\n	// Get None Current Liability ledger amount    \r\n	$sql  = "SELECT SUM(Balance) AS noneCurrentLiability FROM $dataTable WHERE led_code Between ''2-2000'' AND ''2-9999''";\r\n	$temp = nuRunQuery($sql);\r\n	$obj  = db_fetch_object($temp);\r\n	$noneCurrentLiability =  iif($obj->noneCurrentLiability == NULL, 0,$obj->noneCurrentLiability);\r\n	nuRunQuery("UPDATE $dataTable SET type1Balance = $noneCurrentLiability WHERE typeLabel1 = ''NONE-CURRENT LIABILITY''" );\r\n	nuRunQuery("UPDATE $dataT