('14732329c19b47', 'report', 'FM007', 'Debtors List', 'Debtors', '14599715516e37', NULL, '     $sql  = "CREATE TABLE $dataTable"."debtor SELECT * FROM transaction ";\r\n     $sql .= "INNER JOIN customer ON tra_customer_id = customer_id ";\r\n     $sql .= "WHERE tra_outstanding<>0 AND tra_type = ''I''";\r\n     nuRunQuery($sql);\r\n     \r\n     nuRunQuery("CREATE TABLE $dataTable SELECT * FROM $dataTable"."debtor");\r\n     nuRunQuery("ALTER TABLE $dataTable MODIFY tra_outstanding VARCHAR(255)");\r\n     nuRunQuery("ALTER TABLE $dataTable ADD DaysOutstanding INT(2), ADD tra_outstandingCustomer DOUBLE(10,2), ADD tra_outstandingTotal DOUBLE(10,2),\r\n	 		   ADD OutstandingTotal30Days VARCHAR(255), ADD OutstandingTotal60Days VARCHAR(255), ADD OutstandingTotal90Days VARCHAR(255), ADD OutstandingTotal90PlusDays VARCHAR(255)");\r\n      \r\n	 nuRunQuery("ALTER TABLE $dataTable"."debtor ADD DaysOutstanding INT(2)"); \r\n	 \r\n	 // Check for transactions which are either over 7 days or over 14 days outstanding,\r\n	 // and apply the font colour to the Invoice Outstanding value     \r\n     $sql = "SELECT * FROM $dataTable"."debtor";\r\n     $debtorSQL = nuRunQuery($sql);\r\n     while ($debtorObj = db_fetch_object($debtorSQL)) {\r\n      		$transaction_id = $debtorObj->transaction_id;\r\n			$tra_date = $debtorObj->tra_date;\r\n			$tra_outstanding = $debtorObj->tra_outstanding;\r\n			\r\n			$q = nuRunQuery("SELECT DATEDIFF(CURDATE(),''$tra_date'') AS DaysDiff");\r\n			$r = db_fetch_object($q);\r\n			$daysdiff = $r->DaysDiff;\r\n			\r\n			nuRunQuery("UPDATE $dataTable SET DaysOutstanding = $daysdiff, tra_outstanding = ''$tra_outstanding'' WHERE transaction_id = ''$transaction_id''");\r\n			nuRunQuery("UPDATE $dataTable"."debtor SET DaysOutstanding = $daysdiff WHERE transaction_id = ''$transaction_id''");				\r\n			\r\n	 }\r\n	 \r\n	 // Calculate the Invoice Outstanding Total for each indiviudal customer.\r\n     $sql = "SELECT * FROM $dataTable"."debtor GROUP BY customer_id";\r\n     $debtorSQL = nuRunQuery($sql);\r\n     while ($debtorObj = db_fetch_object($debtorSQL)) {\r\n			$customer_id = $debtorObj->customer_id;\r\n			$q = nuRunQuery("SELECT SUM(tra_outstanding) AS tra_outstandingCustomer FROM $dataTable"."debtor WHERE customer_id = ''$customer_id''");\r\n			$r = db_fetch_object($q);\r\n			$tra_outstandingCustomer = $r->tra_outstandingCustomer;\r\n			nuRunQuery("UPDATE $dataTable SET tra_outstandingCustomer = $tra_outstandingCustomer WHERE customer_id = ''$customer_id''");			\r\n	 }     \r\n\r\n	 // Calculate the Invoice Outstanding Grand Total for the whole Debtors List report.\r\n	 $debtorSQL = nuRunQuery("SELECT SUM(tra_outstanding) AS tra_outstandingTotal FROM $dataTable"."debtor");\r\n	 $debtorObj = db_fetch_object($debtorSQL);\r\n	 $tra_outstandingTotal = $debtorObj->tra_outstandingTotal;\r\n	 nuRunQuery("UPDATE $dataTable SET tra_outstandingTotal = $tra_outstandingTotal");	 \r\n\r\n	 // Calculate the Total for all Invoices Outstanding which are Between 7 and 14 days.\r\n	 $debtorSQL = nuRunQuery("SELECT SUM(tra_outstanding) AS OutstandingTotal30Days FROM $dataTable"."debtor WHERE DaysOutstanding < 30");\r\n	 $debtorObj = db_fetch_object($debtorSQL);\r\n	 $OutstandingTotal30Days = $debtorObj->OutstandingTotal30Days;\r\n	 nuRunQuery("UPDATE $dataTable SET OutstandingTotal30Days = ''$OutstandingTotal30Days''");\r\n	 \r\n	 // Calculate the Total for all Invoices Outstanding which are over 14 days.	 \r\n	 $debtorSQL = nuRunQuery("SELECT SUM(tra_outstanding) AS OutstandingTotal60Days FROM $dataTable"."debtor WHERE DaysOutstanding >= 30 AND DaysOutstanding < 60");\r\n	 $debtorObj = db_fetch_object($debtorSQL);\r\n	 $OutstandingTotal60Days = $debtorObj->OutstandingTotal60Days;\r\n	 nuRunQuery("UPDATE $dataTable SET OutstandingTotal60Days = ''$OutstandingTotal60Days''");\r\n\r\n	 // Calculate the Total for all Invoices Outstanding which are over 14 days.	 \r\n	 $debtorSQL = nuRunQuery("SELECT SUM(tra_outstanding) AS OutstandingTotal9