('149ee812c3cde3', 'report', 'FM027', 'Customer Statement Run', 'Debtors', '149eea55a9615a', NULL, '	//get values from selection form\r\n	$todate			= msql_date_nq($formValue[''ToDate'']);\r\n    //fromdate display on report\r\n	$fromdate		 = date(''Y-m-'',strtotime($todate)).''01'';\r\n	$fromdatedisplay = date(''d-M-Y'',strtotime($fromdate));\r\n    \r\n	$customer			=	$formValue[''singleCust''];\r\n	$customerCode       =	$formValue[''codesingleCust''];\r\n	$range				=	$formValue[''customerRange''];\r\n    $fmsOBJ             =   financialSettings();\r\n    \r\n	//get the codes to select between\r\n	$code1 = ''none'';\r\n	$code2 = ''none'';\r\n	if($range == ''A-C'') {\r\n		$tmp = nuRunQuery("SELECT cus_name FROM customer WHERE cus_name LIKE ''A%'' ORDER BY cus_name LIMIT 1");\r\n		$temp = db_fetch_object($tmp);\r\n		$code1 = $temp->cus_name ? $temp->cus_name : ''a'';\r\n		$tmp = nuRunQuery("SELECT cus_name FROM customer WHERE cus_name LIKE ''C%'' ORDER BY cus_name DESC LIMIT 1");\r\n		$temp = db_fetch_object($tmp);\r\n		$code2 = $temp->cus_name ? $temp->cus_name : ''c'';\r\n	} else if($range == ''D-J'') {\r\n		$tmp = nuRunQuery("SELECT cus_name FROM customer WHERE cus_name LIKE ''D%'' ORDER BY cus_name LIMIT 1");\r\n		$temp = db_fetch_object($tmp);\r\n		$code1 = $temp->cus_name ? $temp->cus_name : ''d'';\r\n		$tmp = nuRunQuery("SELECT cus_name FROM customer WHERE cus_name LIKE ''J%''  ORDER BY cus_name DESC LIMIT 1");\r\n		$temp = db_fetch_object($tmp);\r\n		$code2 = $temp->cus_name ? $temp->cus_name : ''j'';\r\n	} else if($range == ''K-P'') {\r\n		$tmp = nuRunQuery("SELECT cus_name FROM customer WHERE cus_name LIKE ''K%'' ORDER BY cus_name LIMIT 1");\r\n		$temp = db_fetch_object($tmp);\r\n		$code1 = $temp->cus_name ? $temp->cus_name : ''k'';\r\n		$tmp = nuRunQuery("SELECT cus_name FROM customer WHERE cus_name LIKE ''P%''  ORDER BY cus_name DESC LIMIT 1");\r\n		$temp = db_fetch_object($tmp);\r\n		$code2 = $temp->cus_name ? $temp->cus_name : ''p'';\r\n	} else if($range == ''Q-Z'') {\r\n		$tmp = nuRunQuery("SELECT cus_name FROM customer WHERE cus_name LIKE ''Q%'' ORDER BY cus_name LIMIT 1");\r\n		$temp = db_fetch_object($tmp);\r\n		$code1 = $temp->cus_name ? $temp->cus_name : ''q'';\r\n		$tmp = nuRunQuery("SELECT cus_name FROM customer WHERE cus_name LIKE ''Z%''  ORDER BY cus_name DESC LIMIT 1");\r\n		$temp = db_fetch_object($tmp);\r\n		$code2 = $temp->cus_name ? $temp->cus_name : ''z'';\r\n	}\r\n    \r\n	//get all customers within the name range\r\n	if($customer != '''' && $customerCode != '''') {\r\n        $outstandingAmountTableCustomerCriteria = $customer;\r\n        $outstandingTable = getOutstandingAmountTable($todate, false, $outstandingAmountTableCustomerCriteria);\r\n	} else if($code1 != '''' && $code2 != '''' && $code1 != ''none'' && $code2 != ''none''){\r\n        // setting outstandingAmountTableCustomerCriteria to false will get outstanding amounts for all customers\r\n        $outstandingAmountTableCustomerCriteria = false;\r\n        $outstandingTable = getOutstandingAmountTable($todate, false, $outstandingAmountTableCustomerCriteria);\r\n        nuRunQuery("DELETE FROM $outstandingTable WHERE NOT(cus_name BETWEEN ''$code1'' AND ''$code2'') ");\r\n	} else {\r\n        $outstandingAmountTableCustomerCriteria = false;\r\n        $outstandingTable = getOutstandingAmountTable($todate, false, $outstandingAmountTableCustomerCriteria);\r\n    }\r\n    \r\n	//reduce to a list of customers with an outstanding value greater then 0\r\n	nuRunQuery("DELETE FROM $outstandingTable WHERE outstandingAmount BETWEEN -0.01 AND 0.01");\r\n    \r\n    //make customer table to pass to statement function of outstanding customers\r\n    nuRunQuery("CREATE TABLE $dataTable"."customers SELECT DISTINCT customer_id FROM $outstandingTable ");\r\n    \r\n    // line counts for line padding\r\n    $lineCounts[''headerAndFooter'']  = 21;\r\n    $lineCounts[''header'']           = 28;\r\n    $lineCounts[''noHeaderOrFooter''] = 41;\r\n    $lineCounts[''Footer'']           = 35;\r\n    \r\n    buildStatement($dataTable, $dataTable."customers", $todate, $lineCounts);\r\n    \r\n    //add required fields for header (cus_name, cus_phone etc...)\r\n    nuRunQuery("\r\n        ALTER TABLE $dataTable\r\n        ADD cus_code VARCHAR(50) NULL,\r\n        ADD cus_phone VARCHAR(50) NULL,\r\n        ADD cus_mobile VARCHAR(50) NULL,\r\n        ADD cus_fax VARCHAR(50) NULL,\r\n        ADD cus_address TEXT NULL,\r\n        ADD fms_name VARCHAR(100) NULL DEFAULT ''".mysql_real_escape_string($fmsOBJ->fms_name)."'',\r\n        ADD fms_address VARCHAR(200) NULL DEFAULT ''".mysql_real_escape_string($fmsOBJ->fms_address)."'',\r\n        ADD fms_city VARCHAR(200) NULL DEFAULT ''".mysql_real_escape_string($fmsOBJ->fms_city)."'',\r\n        ADD fms_state VARCHAR(200) NULL DEFAULT ''".mysql_real_escape_string($fmsOBJ->fms_state)."'',\r\n        ADD fms_post_code VARCHAR(200) NULL DEFAULT ''".mysql_real_escape_string($fmsOBJ->fms_post_code)."'',\r\n        ADD fms_fax VARCHAR(50) NULL DEFAULT ''".mysql_real_escape_string($fmsOBJ->fms_fax)."'',\r\n        ADD fms_phone VARCHAR(50) NULL DEFAULT ''".mysql_real_escape_string($fmsOBJ->fms_phone)."'',\r\n        ADD fms_abn VARCHAR(100) NULL DEFAULT ''".mysql_real_escape_string($fmsOBJ->fms_abn)."''\r\n    ");\r\n    nuRunQuery("\r\n        UPDATE $dataTable\r\n        INNER JOIN customer ON customer.customer_id = tra_customer_id SET\r\n        $dataTable.cus_code = customer.cus_code,\r\n        $dataTable.cus_phone = customer.cus_phone,\r\n        $dataTable.cus_mobile = customer.cus_mobile,\r\n        $dataTable.cus_fax = customer.cus_fax,\r\n        $dataTable.cus_address = customer.cus_address\r\n    ");\r\n    \r\n    // update fromdate into main datatable to display on the reports\r\n    // it will be the first of the month\r\n    nuRunQuery("ALTER TABLE $dataTable ADD FromDate VARCHAR(20) DEFAULT ''$fromdatedisplay'' ");\r\n    \r\n    //drop tables\r\n    nuRunQuery("DROP TABLE $out