('149f7dbe151691', 'report', 'FM013', 'Aged Debtors Report', 'Debtors', '14a39a41831106', NULL, '/* Debtors Aged Trial Balance Report\r\n // Changelog\r\n // Date		Developer	Comment\r\n // 13/05/09		CAM		Recreated report based on updated Statement Run\r\n // 18-06/09 		CAM		Removed customer list and replaced with single customer lookup\r\n // 21/10/09		SDR		Revised to allow ageing from a date other than today\r\n */\r\n\r\n//temp table names\r\n$customerID = $formValue[''customer_id''];\r\n$customerListTable = "$dataTable"."_DATB_customer_list__";\r\n//$today = date("Y-m-d"); 					// Removed by SDR to be MYOB compliant 2009-10-21\r\n$today	= msql_date_nq($formValue[''ToDate'']);			// SDR 2009-10-21\r\n\r\n//get values from selection form\r\n\r\n$toDate = $today;\r\n$customers = getOverdueCustomers($toDate, $customerID, $customerListTable);\r\n\r\n//Define accounting periods (for Debtors Aged Trial Balance summary only\r\n//Based on current date, NOT the statement "to" date).		// No longer true -- SDR 2009-10-21\r\n\r\n$monthDay = nuDateAddDays($today, -30);\r\n$monthDay2 = nuDateAddDays($today, -60);\r\n$monthDay3 = nuDateAddDays($today, -90);\r\n$monthDay4 = nuDateAddDays($today, -35600); //100 years ago\r\n\r\n//get transaction period\r\n$fms = nuRunQuery("SELECT * FROM financial_module_setting");\r\n$fmsObj = db_fetch_object($fms);\r\n\r\n\r\n//Create table containing customer data\r\nnuRunQuery(\r\n<<<EOSQL\r\n	CREATE TABLE $dataTable\r\n	SELECT DISTINCT\r\n		customer.*,\r\n		fms_detail_display AS fmsDetailDisplay,\r\n		fms_name AS seCompany,\r\n		fms_address AS seAddress,\r\n		fms_city AS seCity,\r\n		fms_post_code AS sePostcode,\r\n		fms_fax AS seFax,\r\n		fms_phone AS sePhone,\r\n		fms_state AS seState\r\n	FROM customer\r\n	INNER JOIN $customerListTable\r\n		ON (customer_id = tra_customer_id)\r\n	CROSS JOIN financial_module_setting\r\n\r\n\r\nEOSQL\r\n);\r\n\r\n//Add fields for aging balances\r\nnuRunQuery (\r\n<<<EOSQL\r\n	ALTER TABLE $dataTable\r\n	ADD per1amount double(19,2) DEFAULT ''0.00'',\r\n	ADD per2amount double(19,2) DEFAULT ''0.00'',\r\n	ADD per3amount double(19,2) DEFAULT ''0.00'',\r\n	ADD per4amount double(19,2) DEFAULT ''0.00'',\r\n	ADD perTotal double(19,2) DEFAULT ''0.00'',\r\n	ADD `thedate` DATE DEFAULT ''$toDate''\r\nEOSQL\r\n);\r\n\r\nif (count($customers) > 0) {\r\n	foreach ($customers as $customer_id) {\r\n\r\n		//calculate Aged Trial Balances for each accounting period (current, 30, 60, 90+)\r\n		$per1amount	=	calculateAgedBalance($customer_id, $monthDay, $today);\r\n		$per2amount	=	calculateAgedBalance($customer_id, $monthDay2, $monthDay);\r\n		$per3amount	=	calculateAgedBalance($customer_id, $monthDay3, $monthDay2);\r\n		$per4amount	=	calculateAgedBalance($customer_id, $monthDay4, $monthDay3);\r\n		$perTotal	=	round($per4amount + $per3amount + $per2amount + $per1amount,2);\r\n		$overdue	= $per2amount + $per3amount + $per4amount;\r\n\r\n		nuRunQuery (\r\n<<<EOSQL\r\n			UPDATE $dataTable\r\n			SET\r\n\r\n				per1amount = ''$per1amount'',\r\n				per2amount = ''$per2amount'',\r\n				per3amount = ''$per3amount'',\r\n				per4amount = ''$per4amount'',\r\n				perTotal = ''$perTotal''\r\n			WHERE\r\n				customer_id = ''$customer_id''\r\nEOSQL\r\n		);\r\n	} // End for each customer\r\n} //End if more than zero customers\r\n\r\n//***********balance calculation function*******************************\r\nfunction calculateAgedBalance($customer_id, $fromDate, $toDate ) {\r\n\r\n	//initalise amount\r\n	$amount = 0.00;\r\n\r\n	//select all transactions from individual within this date range\r\n	$sql	=\r\n<<<EOSQL\r\n			SELECT SUM(tra_outstanding) AS Balance\r\n			FROM transaction\r\n			WHERE tra_type IN (''C'',''I'',''U'') AND tra_posted = ''1''\r\n			AND tra_date > ''$fromDate'' AND tra_date <= ''$toDate''\r\n			AND tra_customer_id = ''$customer_id''\r\nEOSQL;\r\n\r\n$transactions = nuRunQuery($sql);\r\n$result = db_fetch_object($transactions);\r\n$amount = $result->Balance;\r\n\r\nreturn round($amount, 2);\r\n} // End function calculateAgedBalance\r\n//*********************************************************\r\n\r\n//drop temp tables\r\nnuRunQuery("DROP TABLE $customerListTable ");\r\n\r\n\r\n/************Overdue customers list function*******************************/\r\nfunction getOverdueCustomers($toDate, $customerID, $customerListTable)\r\n{\r\n\r\n    //Retrieves  list of customers with overdue amounts\r\n    //Returns customer IDs as an array\r\n\r\n    //only include selected customer (regardless of debt) if there is one\r\n    if (($customerID != null) && ($customerID != '''')) {\r\n        nuRunQuery("CREATE TABLE $customerListTable SELECT ''$customerID'' AS tra_customer_id ");\r\n		$customers[] = $customerID;\r\n    } else\r\n    {\r\n        nuRunQuery(\r\n<<<EOSQL\r\nCREATE TABLE $customerListTable\r\n    SELECT\r\n            tra_customer_id\r\n        FROM\r\n            (\r\n                SELECT\r\n                        tra_customer_id\r\n                        ,COALESCE(SUM(IF (\r\n                            tra_type IN (''P'', ''D'')\r\n                            ,tra_total * -1\r\n                            ,IF (\r\n                                tra_type IN (''I'', ''B'', ''C'', ''R'')\r\n                                ,tra_total\r\n                                ,''0''\r\n                            )\r\n                        )), ''0'') AS SumValue\r\n                    FROM\r\n                        transaction\r\n                    WHERE\r\n                        (tra_date <= ''$toDate'')\r\n                        AND (\r\n                            tra_type IN (''P'', ''R'', ''B'', ''D'')\r\n                            OR (\r\n                                tra_type IN (''C'', ''I'')\r\n                                AND tra_posted = ''1''\r\n                            )\r\n                        )\r\n                    GROUP BY\r\n                        tra_customer_id\r\n            ) AS innerTable\r\n        WHERE\r\n            SumValue != ''0''\r\nEOSQL\r\n        );\r\n    \r\n	    $overdueCustomersResultSet = nuRunQuery("SELECT * FROM $customerListTable ");\r\n	    while ($customerRow = db_fetch_object($overdueCustomersResultSet))\r\n	    {\r\n	        $customers[] = $customerRow->tra_customer_id;\r\n    	}\r\n	} //End Else if customerID is empty\r\n    return $customers;\r\n}\r\n//End overdue customers list function', NULL, 'class Reporting{\r\n\r\n   var $nuBuilder          = ''1'';\r\n   var $Controls           = array();\r\n   var $Sections           = array();\r\n   var $Groups             = array();\r\n   var $Version            = ''3'';\r\n   var $Width              = ''825'';\r\n   var $Height             = ''1020'';\r\n   var $PaperType          = ''A4'';\r\n   var $Orientation        = ''p'';\r\n\r\n\r\n   function Reporting(){\r\n      $this->Controls[0]->Name                = ''Label64'';\r\n      $this->Controls[0]->Section             = ''3'';\r\n      $this->Controls[0]->ControlType         = ''Label'';\r\n      $this->Controls[0]->Tag                 = ''Label'';\r\n      $this->Controls[0]->ControlSource       = '''';\r\n      $this->Controls[0]->Caption             = '''';\r\n      $this->Controls[0]->Value               = '''';\r\n      $this->Controls[0]->Top                 = ''140'';\r\n      $this->Controls[0]->Left                = ''13'';\r\n      $this->Controls[0]->Width               = ''755'';\r\n      $this->Controls[0]->Height              = ''23'';\r\n      $this->Controls[0]->ForeColor           = ''#000000'';\r\n      $this->Controls[0]->FontSize            = ''13'';\r\n      $this->Controls[0]->FontWeight          = ''bold'';\r\n      $this->Controls[0]->FontName            = ''Arial'';\r\n      $this->Controls[0]->BackColor           = ''#c0c0c0'';\r\n      $this->Controls[0]->BorderWidth         = ''2'';\r\n      $this->Controls[0]->BorderColor         = ''#000000'';\r\n      $this->Controls[0]->Graph               = '''';\r\n      $this->C