INSERT INTO `zzsys_activity` (`zzsys_activity_id`, `sat_all_type`, `sat_all_code`, `sat_all_description`, `sat_all_group`, `sat_all_zzsys_form_id`, `sat_report_data_file`, `sat_report_data_code`, `sat_report_display_file`, `sat_report_display_code`, `sat_report_page_length`, `sat_report_display_type`, `sat_export_data_file`, `sat_export_data_code`, `sat_export_delimiter`, `sat_export_header`, `sat_export_use_quotes`, `sat_procedure_file`, `sat_procedure_code`, `sys_added`, `sys_changed`, `sys_user_id`) VALUES
('14a39a5e2c297d', 'report', 'FM028', 'Aged Debtors Report (Detailed)', 'Debtors', '14a39a41831106', NULL, '/** Debtors Aged Trial Balance Report (Detailed)\r\n // Changelog\r\n // Date		Developer	Comment\r\n // 18/05/09		CAM		Created report\r\n // 21/10/09		SDR		Revised to allow ageing from a date other than today\r\n \r\n */\r\n\r\n//temp table names\r\n$customerID 		= $formValue[''customer_id''];\r\n$transactionTable 	= "$dataTable"."_DATB_detailed_transaction__";\r\n$customerTable 		= "$dataTable"."_DATB_detailed_customer__";\r\n//$customerListTable 	= "$dataTable"."_DATB_detailed_customer_list__";\r\n\r\n\r\n//Based on current date, NOT the statement "to" date).			// No longer true -- SDR 2009-10-21\r\n//$today		=	date("Y-m-d");				// Removed by SDR to be MYOB compliant 2009-10-21\r\n$today		=	msql_date_nq($formValue[''ToDate'']);		// SDR 2009-10-21\r\n$monthDay	=	nuDateAddDays( $today,-30);\r\n$monthDay2	=	nuDateAddDays( $today,-60);\r\n$monthDay3	=	nuDateAddDays( $today,-90);\r\n$monthDay4	=	nuDateAddDays( $today,-35600);	//100 years ago\r\n\r\n//report is always relative to the current date				// No longer true -- SDR 2009-10-21\r\n//$toDate			=	msql_date_nq($today);\r\n$toDate			=	$today;\r\n$fromDate		=	$monthDay4;\r\n$customers = getOverdueCustomers($toDate,$customerID,$customerTable);\r\n\r\n// First run creates table instead of inserting into it\r\n$actionSQL = "CREATE TABLE $transactionTable ";\r\n\r\n// Loop through the customers\r\nforeach ($customers as $customer_id) {\r\n\r\n	//table to store transaction data\r\n	$transactionTableSQL	= $actionSQL.\r\n<<<EOSQL\r\n\r\n	SELECT\r\n		transaction_id,\r\n		tra_date,\r\n		tra_number,\r\n		tra_customer_id,\r\n		IF(tra_type IN(''P'',''D'',''R''), tra_total*-1,'''') AS cramount,\r\n		IF(tra_type IN(''I'',''B'',''C''),tra_total,'''') AS dramount,\r\n		IF(tra_type IN(''P'',''D''),tra_total,IF(tra_type IN(''C''), -1*tra_total,'''')) AS croutput,\r\n		IF(tra_type IN(''I'',''B''),\r\n		tra_total,\r\n		IF(tra_type IN(''R''),tra_total*-1,'''')) AS droutput,\r\n		(Case\r\n			WHEN tra_type =''P'' THEN ''Payment''\r\n			WHEN tra_type =''C'' THEN ''Credit''\r\n			WHEN tra_type = ''R'' THEN ''Refund''\r\n			WHEN tra_type = ''D'' THEN ''Discount''\r\n			WHEN tra_type = ''I'' THEN ''Invoice''\r\n			WHEN tra_type = ''B'' THEN ''Reverse Payment''\r\n			WHEN tra_type = ''U'' THEN ''Unallocated''\r\n		END) AS tra_type,\r\n		IF(tra_type IN(''P'',''D'',''R''), tra_outstanding*-1,tra_outstanding) AS tra_outstanding\r\n	FROM transaction\r\n	WHERE\r\n		tra_customer_id = ''$customer_id''\r\n		AND (tra_date >= ''$fromDate'')\r\n		AND (tra_date <= ''$toDate'')\r\n		AND (\r\n				tra_type IN (''U'')\r\n				OR\r\n				(\r\n					tra_type IN (''C'',''I'')\r\n					AND tra_posted = ''1''\r\n				)\r\n			)\r\n		AND tra_total!=0\r\n		AND tra_outstanding!=0\r\n	ORDER BY tra_date\r\nEOSQL;\r\n\r\n	nuRunQuery($transactionTableSQL);\r\n	\r\n	//For subsequent runs, do not create the table, instead insert records\r\n	$actionSQL = "INSERT INTO $transactionTable ";\r\n	\r\n} // End for each customer (retrieving transactions)\r\n\r\n\r\n\r\n\r\n//get transaction period\r\n$fms = nuRunQuery("SELECT * FROM financial_module_setting");\r\n$fmsObj = db_fetch_object($fms);\r\n\r\n//Add fields for aging balances\r\nnuRunQuery (\r\n<<<EOSQL\r\n	ALTER TABLE $customerTable\r\n	ADD per1amount double(19,2) DEFAULT ''0.00'',\r\n	ADD per2amount double(19,2) DEFAULT ''0.00'',\r\n	ADD per3amount double(19,2) DEFAULT ''0.00'',\r\n	ADD per4amount double(19,2) DEFAULT ''0.00'',\r\n	ADD overdue double(19,2) DEFAULT ''0.00'',	\r\n	ADD perTotal double(19,2) DEFAULT ''0.00'',\r\n	ADD `thedate` DATE DEFAULT ''$toDate''\r\nEOSQL\r\n);\r\n\r\n\r\n//Add fields for aging balance items\r\nnuRunQuery (\r\n<<<EOSQL\r\n	ALTER TABLE $transactionTable\r\n	ADD per1item_amount VARCHAR(12) NULL,\r\n	ADD per2item_amount VARCHAR(12) NULL,\r\n	ADD per3item_amount VARCHAR(12) NULL,\r\n	ADD per4item_amount VARCHAR(12) NULL,\r\n	ADD phone VARCHAR(20) NULL\r\n\r\n\r\nEOSQL\r\n);\r\n//add columns to store calculated balance totals and store the date in the page header (report writer bug)\r\nnuRunQuery("ALTER TABLE $transactionTable ADD `balanceamount` DOUBLE(19,2) ");\r\n\r\n//add indexes on fields to be used in ORDER BY\r\nnuRunQuery("ALTER TABLE $transactionTable ADD INDEX (tra_customer_id)");\r\nnuRunQuery("ALTER TABLE $transactionTable ADD INDEX (tra_date)");\r\nnuRunQuery("ALTER TABLE $transactionTable ADD INDEX (tra_number)");\r\nif (count($customers) > 0) {\r\n	foreach ($customers as $customer_id) {\r\n	\r\n		\r\n		//calculate running balance owed\r\n		$t = nuRunQuery (\r\n<<<EOSQL\r\n	SELECT\r\n		$transactionTable.*,\r\n		cus_phone\r\n	FROM $transactionTable\r\n	INNER JOIN $customerTable ON (customer_id = tra_customer_id)\r\n	WHERE tra_customer_id = ''$customer_id'' \r\n	ORDER BY tra_customer_id, tra_date, tra_number\r\nEOSQL\r\n		);\r\n		$docbalance = 0.00;\r\n		\r\n\r\n		\r\n		while($r=db_fetch_object($t)){\r\n		\r\n			$phone = ($phone === NULL) ? $r->cus_phone : ''''; // Add the phone number on the first transaction record\r\n			$phone = ($phone != NULL) ? "Phone: ".$phone : $phone;\r\n			\r\n			$transaction_id =$r->transaction_id;\r\n			$dramount	=$r->dramount;\r\n			$cramount 	=$r->cramount;\r\n			$docbalance = $docbalance + $dramount + $cramount;\r\n			\r\n			\r\n			$tra_date	=$r->tra_date;\r\n			$tra_outstanding	=$r->tra_outstanding;\r\n\r\n			$per1item_amount	= "-";	\r\n			$per2item_amount	= "-";		\r\n			$per3item_amount	= "-";			\r\n			$per4item_amount	= "-";\r\n\r\n\r\n\r\n			if ($tra_date > $monthDay) {\r\n				$per1item_amount = $tra_outstanding;\r\n			} elseif (($tra_date > $monthDay2)&&($tra_date <= $monthDay)) {\r\n				$per2item_amount = $tra_outstanding;\r\n			} elseif (($tra_date > $monthDay3)&&($tra_date <= $monthDay2)) {\r\n				$per3item_amount = $tra_outstanding;\r\n			} else {\r\n				$per4item_amount = $tra_outstanding;\r\n			}\r\n			\r\n			nuRunQuery(\r\n<<<EOSQL\r\n				UPDATE $transactionTable\r\n				SET\r\n					balanceamount = ''$docbalance'',\r\n					per1item_amount	= ''$per1item_amount'',\r\n					per2item_amount	= ''$per2item_amount'',\r\n					per3item_amount	= ''$per3item_amount'',\r\n					per4item_amount	= ''$per4item_amount'',\r\n					phone = ''$phone''\r\n				WHERE transaction_id = ''$transaction_id''\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\nEOSQL\r\n			);\r\n			\r\n		} //End while\r\n	\r\n\r\n		//calculate Aged Trial Balances for each accounting period (current, 30, 60, 90+)\r\n		$per1amount	=	calculateAgedBalance($customer_id, $monthDay, $today);\r\n		$per2amount	=	calculateAgedBalance($customer_id, $monthDay2, $monthDay);\r\n		$per3amount	=	calculateAgedBalance($customer_id, $monthDay3, $monthDay2);\r\n		$per4amount	=	calculateAgedBalance($customer_id, $monthDay4, $monthDay3);\r\n		$perTotal	=	round($per4amount + $per3amount + $per2amount + $per1amount,2);\r\n		$overdue	= $per2amount + $per3amount + $per4amount;\r\n\r\n		nuRunQuery (\r\n<<<EOSQL\r\n			UPDATE $customerTable\r\n			SET\r\n\r\n				per1amount = ''$per1amount'',\r\n				per2amount = ''$per2amount'',\r\n				per3amount = ''$per3amount'',\r\n				per4amount = ''$per4amount'',\r\n				perTotal = ''$perTotal''\r\n			WHERE\r\n				customer_id = ''$customer_id''\r\nEOSQL\r\n		);\r\n	} // End for each customer\r\n} //End if more than zero customers\r\n\r\n//***********balance calculation function*******************************\r\nfunction calculateAgedBalance($customer_id, $fromDate, $toDate ) {\r\n\r\n	//initalise amount\r\n	$amount = 0.00;\r\n\r\n	//select all transactions from individual within this date range\r\n	$sql	=\r\n<<<EOSQL\r\n			SELECT SUM(tra_outstanding) AS Balance\r\n			FROM transaction\r\n			WHERE tra_type IN (''C'',''I'',''U'') AND tra_posted = ''1''\r\n			AND tra_date > ''$fromDate'' AND tra_date <= ''$toDate''\r\n			AND tra_customer_id = ''$customer_id''\r\nEOSQL;\r\n\r\n$transactions = nuRunQuery($sql);\r\n$result = db_fetch_object($transactions);\r\n$amount = $result->Balance;\r\n\r\nreturn round($amount, 2);\r\n} // End function calculateAgedBalance\r\n//*********************************************************\r\n\r\n/*******add extra lines to pad out page******************/\r\n\r\n// THIS SECTION NEEDS TO BE TESTED BEFORE BEING IMPLEMENTED.\r\n// IT IS DESIGNED TO FORMAT THE RECORDS NEATLY AND AVOID ORPHAN\r\n// OR WIDOW ROWS ON THE REPORT.\r\n\r\n/*\r\n	//add primary ID and orderID for the padded lines\r\n	nuRunQuery("ALTER TABLE $transactionTable ADD ID INT NOT NULL AUTO_INCREMENT PRIMARY KEY, ADD OrderID INT DEFAULT ''0''");\r\n\r\nforeach ($customers as $customer_id) {\r\n\r\n	$rs			= nuRunQuery("SELECT * FROM $transactionTable WHERE tra_customer_id = ''$customer_id''");\r\n	$num_rows	= db_num_rows($rs);\r\n\r\n\r\n\r\n	$a = 26; //Total lines fitted on a page with a header and a footer\r\n	$b = 79; //Total lines fitted on a page with no footer + total lines fitted on a page with no header\r\n	$c = 54; //Total lines fitted on a page with no header or footer\r\n\r\n	if ($num_rows <= $a) {\r\n		$lines_difference = $a - $num_rows;\r\n	} elseif (($num_rows > $a) && ($num_rows <= $b)) {\r\n		$lines_difference = $b - $num_rows;\r\n	} else {\r\n		$lines_difference = $b + ceil(($num_rows - $b) / $c) * $c - $num_rows ;\r\n	}\r\n\r\n\r\n\r\n\r\n	//write the extra records maintaining correct balances needed in report footer\r\n	for ($i = 1; $i <= $lines_difference; $i++) {\r\n		nuRunQuery(\r\n<<<EOSQL\r\n			INSERT INTO $transactionTable (tra_customer_id, OrderID)\r\n			VALUES (''$customer_id'', ''1'')\r\nEOSQL\r\n		);\r\n	} // End for each padding line\r\n\r\n} // End for each customer\r\n*/\r\n/*************end extra lines code*********************************/\r\n\r\n/************Create main data table*******************************/\r\n// Join the temporary customer and transaction tables\r\n// with the financial_module_setting tables.\r\nnuRunQuery(\r\n<<<EOSQL\r\n	CREATE TABLE $dataTable\r\n	SELECT\r\n		$transactionTable.*, \r\n		$customerTa