RunQuery($sumSTR);\r\n\r\n	// Loop through the rows to separate out each type/sum pairing\r\n	while ( $sumOBJ = db_fetch_object($sumQRY) ) {\r\n	\r\n		$invType 			= $sumOBJ->tra_type;\r\n		$invPaymentMethod 	= $sumOBJ->tra_payment_method;\r\n		$theSum   			= $sumOBJ->theSum;	\r\n\r\n		if ( $invType == ''I'' ) {							\r\n			$invoice_INC	= $theSum;											// Invoices\r\n			$invoice_GST 	= round($invoice_INC / 11, 2);\r\n			$invoice_EXC = $invoice_INC - $invoice_GST;\r\n		} else if ( $invType == ''P'' && $invPaymentMethod == ''Bad Debt'') {		// Bad Debts written off\r\n			$bad_debt_INC = $theSum;\r\n			$bad_debt_GST = round($bad_debt_INC / 11, 2);\r\n			$bad_debt_EXC = $bad_debt_INC - $bad_debt_GST;	\r\n		} else if ( $invType == ''C'' ) {					\r\n			$credit_INC = $theSum * -1;											// Credit Notes are negative, but Journal figures need to be non-negative\r\n			$credit_GST = round($credit_INC / 11, 2);\r\n			$credit_EXC = $credit_INC - $credit_GST;\r\n		} else if ( $invType == ''D'' ) {					\r\n			$discount_INC = $theSum;											// Discounts are positive\r\n			$discount_GST = round($discount_INC / 11, 2);\r\n			$discount_EXC = $discount_INC - $discount_GST;	\r\n		} else if ( $invType == ''B'' ) {					\r\n			$rev_pmt_INC = $theSum;												// Reverse Payments are positive\r\n			$rev_pmt_GST = round($rev_pmt_INC / 11, 2);\r\n			$rev_pmt_EXC = $rev_pmt_INC - $rev_pmt_GST;	\r\n		}\r\n\r\n	}\r\n\r\n	// Get the list of MYOB account codes -- these are configured by nuSoftware on behalf of the client (read only subform on financialmodulesettings)\r\n	$accQRY = nuRunQuery("SELECT * FROM myobaccountlink");\r\n	\r\n	// Loop through the results to get each MYOB account (ledger) code\r\n	while ( $accOBJ = db_fetch_object($accQRY) ) {\r\n	\r\n		$AccNo 	 = $accOBJ->mal_code ;\r\n		$AccDesc = $accOBJ->mal_description;\r\n		\r\n		switch ($AccDesc) {\r\n			case ''Bad Debts'':\r\n				$BadDebtsAcc = $AccNo;\r\n				$BadDebtsDesc = $AccDesc;\r\n				break;\r\n			case ''Cash at 