('14ccfa8b3dd6a2', 'report', 'FM016B', 'Profit & Loss Statement Between Date Range', 'Ledger', '145974108a4084', NULL, '/******************************** Profit & Loss Statement ***************************/\r\n/*************************************** BY J''RAD ***********************************/\r\n\r\n//get form values\r\n$from = msql_date_nq($formValue[''vFrom'']);\r\n$to = msql_date_nq($formValue[''vTo'']);\r\n\r\n//calculate the totals for both periods\r\n$sql = "CREATE TABLE $dataTable SELECT ledger_id, led_type, led_description, ''$from'' AS year1, ''$to'' AS year2, ";\r\n$sql .= "SUM(IF(lld_date BETWEEN ''$from'' AND ''$to'',IF(led_type = ''INCOME'',lli_amount*-1,lli_amount),0)) AS amount ";\r\n$sql .= "FROM ledger ";\r\n$sql .= "INNER JOIN ledger_log_item ON lli_ledger_id = ledger_id ";\r\n$sql .= "INNER JOIN ledger_log ON ledger_log_id = lli_ledger_log_id ";\r\n$sql .= "WHERE led_type IN (''INCOME'',''EXPENSE'',''COSTOFSALES'') ";\r\n$sql .= "GROUP BY ledger_id ";\r\nnuRunQuery($sql);\r\n\r\n//add in some ordering so we can insert totals and labels etc\r\nnuRunQuery("ALTER TABLE $dataTable ADD theorder int(1)");\r\nnuRunQuery("UPDATE $dataTable SET theorder = 2 WHERE led_type = ''INCOME''");\r\nnuRunQuery("UPDATE $dataTable SET theorder = 12 WHERE led_type = ''EXPENSE''");\r\nnuRunQuery("UPDATE $dataTable SET theorder = 6 WHERE led_type = ''COSTOFSALES''");\r\n\r\n//add label INCOME\r\nnuRunQuery("INSERT INTO $dataTable (led_description, theorder, year1, year2) VALUES (''<b>INCOME</b>'',1, ''$from'', ''$to'')");\r\n\r\n//add total INCOME\r\n$qry = nuRunQuery("SELECT SUM(amount) AS tot FROM $dataTable WHERE led_type = ''INCOME''");\r\n$obj = db_fetch_object($qry);\r\nnuRunQuery("INSERT INTO $dataTable (led_description, amount, theorder) VALUES (''<b>Total Income</b>'', ''$obj->tot'', 3)");\r\nnuRunQuery("INSERT INTO $dataTable (led_description, amount, theorder) VALUES ('''', '''', 4)");\r\n\r\n//add label COST OF SALES\r\nnuRunQuery("INSERT INTO $dataTable (led_description, theorder) VALUES (''<b>COST OF SALES</b>'',5)");\r\n\r\n//add total COST OF SALES\r\n$qry = nuRunQuery("SELECT SUM(amount) AS tot FROM $dataTable WHERE led_type = ''COSTOFSALES''");\r\n$obj = db_fetch_object($qry);\r\nnuRunQuery("INSERT INTO $dataTable (led_description, amount, theorder) VALUES (''<b>Total Costs</b>'', ''$obj->tot'', 7)");\r\nnuRunQuery("INSERT INTO $dataTable (led_description, amount, theorder) VALUES ('''', '''', 8)");\r\n\r\n//add total GROSS PROFIT\r\n$qry = nuRunQuery("SELECT SUM(IF(led_type = ''INCOME'',amount,amount*-1)) AS tot FROM $dataTable WHERE theorder IN (2,6)");\r\n$obj = db_fetch_object($qry);\r\nnuRunQuery("INSERT INTO $dataTable (led_description, amount, theorder) VALUES (''<b>Gross Surplus</b>'', ''$obj->tot'', 9)");\r\nnuRunQuery("INSERT INTO $dataTable (led_description, amount, theorder) VALUES ('''', '''', 10)");\r\n\r\n//add label EXPENSE\r\nnuRunQuery("INSERT INTO $dataTable (led_description, theorder) VALUES (''<b>EXPENSE</b>'',11)");\r\n\r\n//add total EXPENSE\r\n$qry = nuRunQuery("SELECT SUM(amount) AS tot FROM $dataTable WHERE led_type = ''EXPENSE''");\r\n$obj = db_fetch_object($qry);\r\nnuRunQuery("INSERT INTO $dataTable (led_description, amount, theorder) VALUES (''<b>Total Expense</b>'', ''$obj->tot'', 13)");\r\nnuRunQuery("INSERT INTO $dataTable (led_description, amount, theorder) VALUES ('''', '''', 14)");\r\n\r\n//add grand totals\r\n$qry = nuRunQuery("SELECT SUM(IF(led_type = ''INCOME'',amount,amount*-1)) AS tot FROM $dataTable WHERE theorder IN (2,6,12)");\r\n$obj = db_fetch_object($qry);\r\nnuRunQuery("INSERT INTO $dataTable (led_description, amount, theorder) VALUES (''<b>Net Surplus (Deficit)</b>'', ''$obj->tot'', 15)");\r\n\r\n//Fix up doubles that are meant to be blank\r\nnuRunQuery("ALTER TABLE $dataTable MODIFY amount VARCHAR(255)");\r\nnuRunQuery("UPDATE $dataTable SET amount = '''' WHERE theorder IN(1,4,5,8,10,11,14)");\r\n\r\n//change negatives to have brackets and bold totals\r\nnuRun