('14a4834bc2d378', 'report', 'FM029', 'Debtors Summary Journal (NOW BEEN SPLIT UP)', 'Debtors', '145974108a4084', NULL, '/** FM029 -- Debtors Summary Journal\r\n// Produces a set of 4 journals for transactions of type\r\n// invoices/discounts, payments, credit notes & refunds/(reverse payments) from the specified time period\r\n//\r\n// Changelog\r\n// Date			Developer	Comment\r\n// 29/06/2009		SDR			Original version\r\n*/\r\n\r\n// Increase memory allocation if necessary\r\n// ini_set ("memory_limit", 128000000);\r\n\r\n// Get values from selection form\r\n$toDate	   	=	msql_date_nq(''#vTo#'');\r\n$fromDate  	= 	msql_date_nq(''#vFrom#'');\r\n\r\n/* Create the dataTable structure */\r\n\r\n// Get the financial module setttings\r\nnuRunQuery( <<<EOSQL\r\n	CREATE TABLE #dataTable#\r\n	SELECT\r\n		fms_detail_display AS fmsDetailDisplay,\r\n		fms_abn AS fmsABN,\r\n		fms_name AS seCompany,\r\n		fms_address AS seAddress,\r\n		fms_city AS seCity,\r\n		fms_post_code AS sePostcode,\r\n		fms_fax AS seFax,\r\n		fms_phone AS sePhone,\r\n		fms_state AS seState\r\n	FROM\r\n		financial_module_setting\r\nEOSQL\r\n);\r\n\r\n// Add additional fields to be populated later\r\nnuRunQuery( <<<EOSQL\r\n	ALTER TABLE #dataTable#\r\n		ADD invoice_EXC DOUBLE(10,2) DEFAULT 0.00,\r\n		/*ADD invoice_EXC_DR DOUBLE(10,2) DEFAULT 0.00,*/\r\n		/*ADD invoice_EXC_CR DOUBLE(10,2) DEFAULT 0.00,*/\r\n		ADD payment_INC DOUBLE(10,2) DEFAULT 0.00,\r\n		ADD payment_GST DOUBLE(10,2) DEFAULT 0.00,	\r\n		ADD payment_EXC DOUBLE(10,2) DEFAULT 0.00,\r\n		ADD credit_EXC DOUBLE(10,2) DEFAULT 0.00,\r\n		ADD refund_INC DOUBLE(10,2) DEFAULT 0.00,\r\n		ADD refund_GST DOUBLE(10,2) DEFAULT 0.00,\r\n		ADD refund_EXC DOUBLE(10,2) DEFAULT 0.00,\r\n		ADD discount_EXC DOUBLE(10,2) DEFAULT 0.00,\r\n		ADD rev_pmt_INC DOUBLE(10,2) DEFAULT 0.00,\r\n		ADD rev_pmt_GST DOUBLE(10,2) DEFAULT 0.00,	\r\n		ADD rev_pmt_EXC DOUBLE(10,2) DEFAULT 0.00\r\nEOSQL\r\n);\r\n\r\n// Get the sum ''tra_total'' for each transaction type\r\n$sumSTR = <<<EOSQL\r\n	\r\n	SELECT\r\n		tra_type,\r\n		SUM(tra_total) AS theSum\r\n	FROM transaction\r\n	WHERE\r\n		tra_date >= ''$fromDate''\r\n		AND tra_date <= ''$toDate''\r\n		AND (\r\n				tra_type IN (''P'',''R'',''B'',''D'')\r\n				OR\r\n				(\r\n					tra_type IN (''C'',''I'')\r\n					AND tra_posted = ''1''\r\n				)\r\n			)\r\n	AND tra_total IS NOT NULL\r\n	GROUP BY tra_type	\r\nEOSQL;\r\n\r\n$sumQRY = nuRunQuery($sumSTR);\r\n\r\n// Loop through the rows to separate out each type/sum pairing\r\nwhile ( $sumOBJ = db_fetch_object($sumQRY) ) {\r\n\r\n	$tra_type = $sumOBJ->tra_type;\r\n	$theSum   = $sumOBJ->theSum;\r\n\r\n	if ( $tra_type == ''I'' ) {							\r\n		$invoice_INC	= $theSum;						// Invoices\r\n		$invoice_GST 	= $invoice_INC / 11;\r\n		$invoice_EXC = $invoice_INC - $invoice_GST;\r\n		//$invoice_EXC_CR = $invoice_INC - $invoice_GST;	// Invoice ''CR'' column totals\r\n	} else if ( $tra_type == ''P'' ) {					// (Allocated) Payments\r\n		$payment_INC = $theSum;\r\n		$payment_GST = $payment_INC / 11;\r\n		$payment_EXC = $payment_INC - $payment_GST;\r\n	} else if ( $tra_type == ''C'' ) {					\r\n		$credit_INC = $theSum * -1;						// Credit Notes are negative, but Journal figures need to be non-negative\r\n		$credit_GST = $credit_INC / 11;\r\n		$credit_EXC = $credit_INC - $credit_GST;\r\n	} else if ( $tra_type == ''R'' ) {					\r\n		$refund_INC = $theSum * -1;						// Refunds are negative\r\n		$refund_GST = $refund_INC / 11;\r\n		$refund_EXC = $refund_INC - $refund_GST;\r\n	} else if ( $tra_type == ''D'' ) {					\r\n		$discount_INC = $theSum;						// Discounts are positive\r\n		$discount_GST = $discount_INC / 11;\r\n		$discount_EXC = $discount_INC - $discount_GST;	\r\n	} else if ( $tra_type == ''B'' ) {					\r\n		$rev_pmt_INC = $theSum;							// Reverse Payments are positive\r\n		$rev_pmt_GST = $rev_pmt_INC / 11;\r\n		$rev_pmt_EXC = $rev_pmt_INC - $rev_pmt_GST;	\r\n	}\r\n	\r\n}	\r\n\r\n/*\r\n// Update the variables -- ''Refunds'' and ''Reverse Payments'' are recorded in the same journal\r\n$refund_INC += $rev_pmt_INC;\r\n$refund_GST += $rev_pmt_GST;\r\n$refund_EXC += $rev_pmt_EXC;\r\n\r\n// Apply any discounts to the invoice ''DR'' column totals\r\n$invoice_EXC_DR  = $invoice_EXC_CR - $discount_EXC;\r\n*/\r\n	\r\n// Update the row of the dataTable	\r\nnuRunQuery( <<<EOSQL\r\n	UPDATE #dataTable#\r\n	SET\r\n		/*invoice_EXC_DR	= ''$invoice_EXC_DR'',*/\r\n		/*invoice_EXC_CR	= ''$invoice_EXC_CR'',*/\r\n		invoice_EXC	 = ''$invoice_EXC'',\r\n		payment_INC  = ''$payment_INC'',\r\n		payment_GST	 = ''$payment_GST'',\r\n		payment_EXC	 = ''$payment_EXC'',\r\n		credit_EXC 	 = ''$credit_EXC'',\r\n		refund_INC	 = ''$refund_INC'',\r\n		refund_GST	 = ''$refund_GST'',\r\n		refund_EXC	 = ''$refund_EXC'',\r\n		discount_EXC = ''$discount_EXC'',\r\n		rev_pmt_INC	 = ''$rev_pmt_INC'',\r\n		rev_pmt_GST	 = ''$rev_pmt_GST'',\r\n		rev_pmt_EXC	 = ''$rev_pmt_EXC''	\r\nEOSQL\r\n);\r\n\r\n// Test Table\r\n//nuRunQuery("CREATE TABLE #dataTable#TEST_FM029 SELECT * FROM #dataTable#");', NULL, 'class Reporting{\r\n\r\n   var $nuBuilder          = ''1'';\r\n   var $Controls           = array();\r\n   var $Sections           = array();\r\n   var $Groups             = array();\r\n   var $Version            = ''3'';\r\n   var $Width              = ''825'';\r\n   var $Height             = ''1020'';\r\n   var $PaperType          = ''A4'';\r\n   var $Orientation        = ''p'';\r\n\r\n\r\n   function Reporting(){\r\n      $this->Controls[0]->Name                = ''Label64'';\r\n      $this->Controls[0]->Section             = ''3'';\r\n      $this->Controls[0]->ControlType         = ''Label'';\r\n      $this->Controls[0]->Tag                 = ''Label'';\r\n      $this->Controls[0]->ControlSource       = '''';\r\n      $this->Controls[0]->Caption             = '''';\r\n      $this->Controls[0]->Value               = '''';\r\n      $this->Controls[0]->Top                 = ''145'';\r\n      $this->Controls[0]->Left                = ''12'';\r\n      $this->Controls[0]->Width  