('14a6655fd1237a', 'report', 'FM015', 'Chart of Accounts', 'Ledger', '147312ef339840', NULL, '	//Prevent parent ledgers from looking like children\r\n	nuRunQuery("UPDATE ledger SET led_parent_ledger_id = NULL WHERE led_parent_ledger_id = ''''");\r\n\r\n	//create a list of all ledgers that have a parent\r\n	$sql   = "CREATE TABLE $dataTable"."childledger ";\r\n	$sql  .= "SELECT ledger_id, led_type FROM ledger ";\r\n	$sql  .= "WHERE ledger_id NOT IN ( SELECT ledger.ledger_id FROM ledger INNER JOIN ledger as child ON child.led_parent_ledger_id = ledger.ledger_id )";  		\r\n	$temp  = nuRunQuery($sql);\r\n\r\n	//create a list of all ledgers that are highest level parents  \r\n	$sql  = "CREATE TABLE $dataTable"."headerledger SELECT * FROM ledger WHERE led_parent_ledger_id IS NULL";\r\n	$header = nuRunQuery($sql);    \r\n\r\n	//create a level for indentation\r\n	$sql   = "CREATE TABLE $dataTable SELECT * FROM ledger ";\r\n	nuRunQuery($sql);\r\n	nuRunQuery("ALTER TABLE $dataTable ADD Level INT ");\r\n\r\n	$sql    = "SELECT header.ledger_id AS headerledger_id, child.ledger_id AS childledger_id FROM $dataTable"."headerledger AS header ";\r\n	$sql   .= "LEFT JOIN $dataTable"."childledger AS child ON child.led_type = header.led_type";\r\n	$temp1   = nuRunQuery($sql);\r\n\r\n	//set all children to have a max level of 1000\r\n	while($ledger = db_fetch_object($temp1)) {\r\n	$header = $ledger->headerledger_id;\r\n	$child  = $ledger->childledger_id;\r\n	nuRunQuery("UPDATE $dataTable SET Level = 1000 WHERE ledger_id = ''$child''");\r\n\r\n		//if the ledger is a child then reduce its level\r\n		while($header != $child) {\r\n			// find the next level up\r\n			$sql  = "SELECT led_parent_ledger_id, Level FROM $dataTable WHERE ledger_id = ''$child''";\r\n			$temp = nuRunQuery($sql);\r\n			if($obj = db_fetch_object($temp)) {\r\n				$Level = $obj->Level -1;\r\n				$child = $obj->led_parent_ledger_id;\r\n				nuRunQuery("UPDATE $dataTable SET Level = $Level WHERE (ledger_id = ''$child'' AND Level>$Level) OR Level IS NULL");\r\n			} \r\n		}\r\n	}	\r\n\r\n	nuRunQuery("ALTER TABLE $dataTable ADD minLevel INT ");\r\n\r\n	$sql  = "SELECT MIN(Level) AS minLevel,led_type FROM $dataTable GROUP BY led_type";\r\n	$temp = nuRunQuery($sql);\r\n\r\n	//find the minimum level of children\r\n	while($obj = db_fetch_object($temp) ) {\r\n		$led_type  = $obj->led_type;\r\n		$minLevel = $obj->minLevel;\r\n		nuRunQuery("UPDATE $dataTable SET minLevel = ''$minLevel'' WHERE led_type = ''$led_type'' ");\r\n	}\r\n	\r\n	//reduce level by minimum level for more reasonable numbers\r\n	nuRunQuery("UPDATE $dataTable SET Level = Level - minLevel ");\r\n\r\n	$space = ''&nbsp;&nbsp;&nbsp;'';\r\n	nuRunQuery("ALTER TABLE $dataTable ADD disled_code text, ADD disled_name text ");\r\n	$sql  = "SELECT ledger_id, Level, led_code, led_name,led_type FROM $dataTable" ;\r\n	$temp = nuRunQuery($sql);\r\n	\r\n	///add indents to all children, one for each level down\r\n	while($obj = db_fetch_object($temp)) {\r\n		$ledger_id = $obj->ledger_id;\r\n		$led_code  = $obj->led_code;\r\n		$led_name  = $obj->led_name;\r\n		$led_type  = $obj->led_type;\r\n		$Level    = $obj->Level;\r\n\r\n		//add indents to names and codes\r\n		for($i=0; $i<=$Level;$i++) {\r\n			$led_name = $space.$led_name;\r\n			$led_code = $space.$led_code;\r\n		}\r\n		nuRunQuery("UPDATE $dataTable SET disled_name = \\"$led_name\\",disled_code = \\"$led_code\\" WHERE ledger_id = ''$ledger_id'' ");\r\n	}\r\n\r\n	nuRunQuery("DROP TABLE $dataTable"."childledger ");\r\n	nuRunQuery("DROP TABLE $dataTable"."headerledger ");\r\n\r\n	$fms          = nuRunQuery("SELECT * FROM financial_module_setting");\r\n	$fmsObj       = db_fetch_object($fms);\r\n	$fmsName      = $fmsObj->fms_name;\r\n	$fmsAddress   = $fmsObj->fms_address; \r\n	$fmsCity      = $fmsObj->fms_city;\r\n	$fmsPostcode  = $fmsObj->fms_post_code;\r\n	$fmsPhone     = $fmsObj->fms_phone;\r\n	$fmsFax       = $fmsObj->fms_fax;\r\n	$fmsState     = $fmsObj->fms_state; \r\n\r\n	nuRunQuery("ALTER TABLE $dataTable ADD seCompany varchar(255), ADD seAddress varchar(255),ADD seCity varchar(100), ADD sePostcode varchar(20), ADD seState varchar(50),ADD sePhone varchar(20),ADD seFax varchar(20)");\r\n	$sql  = "UPDATE $dataTable SET seCompany = \\"$fmsName\\", seAddress = \\"$fmsAddress\\"," ;\r\n	$sql .=	"seCity = \\"$fmsCity\\", sePostcode = ''$fmsPostcode'', seState = \\"$fmsState\\", " ;\r\n	$sql .=	"sePhone = \\"$fmsPhone\\" ,seFax = ''$fmsFax''";\r\n	nuRunQuery($sql);\r\n\r\n	nuRunQuery("UPDATE $dataTable SET led_type = ''COST OF SALES'' WHERE led_type = ''COSTOFSALES''");\r\n	nuRunQuery("UPDATE $dataTable SET led_type = ''OTHER INCOME'' WHERE led_type = ''OTHERINCOME''");\r\n	nuRunQuery("UPDATE $dataTable SET led_type = ''OTHER EXPENSE'' WHERE led_type = ''OTHEREXPENSE''");\r\n\r\n	$active = $formValue[''active''];\r\n	\r\n	// check all postable ledgers, check if they dont violate any of the deletion rules\r\n	$temp = nuRunQuery("SELECT * FROM $dataTable WHERE led_postable = ''1''");\r\n	while($obj = db_fetch_object($temp)) {\r\n		$id = $obj->ledger_id;	\r\n		// Check the ledger log item \r\n		$lli    = nuRunQuery("SELECT * FROM ledger_log_item WHERE lli_ledger_id = ''$id''");\r\n		$lliObj = db_fetch_object($lli);\r\n	\r\n		// Check financial module  \r\n		$sql  = "SELECT * FROM financial_module_setting ";\r\n		$sql .= "WHERE fms_account_payable_ledger_id=''$id'' ";\r\n		$sql .= "OR fms_expense_ledger_id          =''$id'' ";\r\n		$sql .= "OR fms_cost_of_sale_ledger_id       =''$id'' ";\r\n		$sql .= "OR fms_gst_ledger_id              =''$id'' ";\r\n		$sql .= "OR fms_account_receivable_ledger_id=''$id'' ";\r\n		$sql .= "OR fms_reciept_ledger_id          =''$id'' ";\r\n		$sql .= "OR fms_cash_at_bank_ledger_id       =''$id'' ";\r\n		$sql .= "OR fms_gst_collected_ledger_id     =''$id'' ";\r\n		$sql .= "OR fms_asset_ledger_id            =''$id'' ";\r\n		$sql .= "OR fms_income_ledger_id           =''$id'' ";\r\n		$fms  = nuRunQuery($sql);\r\n		$fmsObj = db_fetch_object($fms);\r\n\r\n		//Check customer group\r\n		$sql  = "SELECT * FROM customer_group ";\r\n		$sql .= "WHERE csg_discount_ledger_id = ''$id'' ";\r\n		$sql .= "OR csg_ledger_id = ''$id'' ";\r\n		$csg  = nuRunQuery($sql);\r\n		$csgObj = db_fetch_object($csg);\r\n\r\n		//Check product group \r\n		$sql  = "SELECT * FROM product_group ";\r\n		$sql .= "WHERE prg_income_ledger_id = ''$id'' ";\r\n		$sql .= "OR prg_cost_of_sale_ledger_id = ''$id''";\r\n		$prg  = nuRunQuery($sql);\r\n		$prgObj = db_fetch_object($prg);\r\n	\r\n		//Check supplier group \r\n		$sql  = "SELECT * FROM supplier_group ";\r\n		$sql .= "WHERE sug_expense_ledger_id    = ''$id'' ";\r\n		$sql .= "OR sug_liability_ledger_id     = ''$id'' ";\r\n		$sug  = nuRunQuery($sql);\r\n		$sugObj = db_fetch_object($sug);\r\n	\r\n		//Check receipt type \r\n		$sql  = "SELECT * FROM receipt_type ";\r\n		$sql .= "WHERE ret_general_ledger_id = ''$id'' ";\r\n		$ret  = nuRunQuery($sql);\r\n		$retObj = db_fetch_object($ret);\r\n	\r\n		//if ledger is being used then omit from inactive report or add to active report and vice versa\r\n		if($lliObj || $fmsObj || $csgObj || $prgObj || $sugObj || $retObj){\r\n			if($active == ''1'') {\r\n				$delete = ''Active'';		 		\r\n			} else if($active == ''0'') {\r\n				nuRunQuery("DELETE FROM $dataTable WHERE ledger_id = ''$id''");\r\n			}\r\n		} else {\r\n			if($active == ''1'') {\r\n				nuRunQuery("DELETE FROM $dataTable WHERE ledger_id = ''$id''");\r\n			} else if($active == ''0'') {\r\n				$delete = ''Active'';\r\n			}\r\n		}\r\n	}\r\n  \r\n	$temp = nuRunQuery("SELECT * FROM $dataTable WHERE led_postable = ''0'' Order BY disled_name");\r\n	\r\n	//remove ledgers if they are not postable\r\n	while($obj = db_fetch_object($temp)) {\r\n		$ledger_id    = $obj->ledger_id;\r\n		$parent      = nuRunQuery("SELECT * FROM $dataTable WHERE led_parent_ledger_id = ''$ledger_id''");\r\n		if(!($parentObj = db_fetch_object($parent))) {\r\n			nuRunQuery("DELETE FROM $dataTable WHERE ledger_id = ''$ledger_id''");\r\n		}\r\n	}', NULL, 'class Reporting{\r\n\r\n   var $nuBuilder          = ''1'';\r\n   var $Controls           = array();\r\n   var $Sections           = array();\r\n   var $Groups             = array();\r\n   var $Version            = ''3'';\r\n   var $Width              = ''825'';\r\n   var $Height             = ''1020'';\r\n   var $PaperType          = ''A4'';\r\n   var $Orientation        = ''p'';\r\n\r\n\r\n   function Reporting(){\r\n      $this->Controls[0]->Name                = ''Line251'';\r\n      $this->Controls[0]->Section             = ''3'';\r\n      $this->Controls[0]->ControlType         = '''';\r\n      $this->Controls[0]->Tag                 = '''';\r\n      $this->Controls[0]->ControlSource       = '''';\r\n      $this->Controls[0]->Caption             = '''';\r\n      $this->Controls[0]->Value               = '''';\r\n      $this->Controls[0]->Top                 = ''127'';\r\n      $this->Controls[0]->Left                = ''12'';\r\n      $this->Controls[0]->Width               = ''0'';\r\n      $this->Controls[0]->Height              = ''1'';\r\n      $this->Controls[0]->ForeColor           = '''';\r\n      $this->Controls[0]->FontSize            = ''0'';\r\n      $this->Controls[0]->FontWeight          = '''';\r\n      $this->Controls[0]->FontName            = '''';\r\n      $this->Controls[0]->BackColor           = '''';\r\n      $this->Controls[0]->BorderWidth         = ''0'';\r\n      $this->Controls[0]->BorderColor         = ''#000000'';\r\n      $this->Controls[0]->Graph               = '''';\r\n      $this->Controls[0]->CanGrow             = '''';\r\n      $this->Controls[0]->TextAlign           = '''';\r\n      $this->Controls[0]->Format              = '''';\r\n\r\n      $this->Controls[1]->Name                = ''ReportTitle'';\r\n      $this->Controls[1]->Section             = ''3'';\r\n      $this->Controls[1]->ControlType         = ''Field'';\r\n      $this->Controls[1]->Tag                 = ''Field'';\r\n      $this->Co