INSERT INTO `zzsys_activity` (`zzsys_activity_id`, `sat_all_type`, `sat_all_code`, `sat_all_description`, `sat_all_group`, `sat_all_zzsys_form_id`, `sat_report_data_file`, `sat_report_data_code`, `sat_report_display_file`, `sat_report_display_code`, `sat_report_page_length`, `sat_report_display_type`, `sat_export_data_file`, `sat_export_data_code`, `sat_export_delimiter`, `sat_export_header`, `sat_export_use_quotes`, `sat_procedure_file`, `sat_procedure_code`, `sys_added`, `sys_changed`, `sys_user_id`) VALUES
('14b6b6f19ebf70', 'report', 'FM025', 'Cash Debtors Summary Journal', 'Debtors', '145974108a4084', NULL, '// Increase memory allocation if necessary\r\n	// ini_set ("memory_limit", 128000000);\r\n\r\n	// Get values from selection form\r\n	$toDate	   	=	msql_date_nq($formValue[''vTo'']);\r\n	$fromDate  	= 	msql_date_nq($formValue[''vFrom'']);\r\n	\r\n	/* Create the dataTable structure */\r\n	\r\n	// Get the financial module setttings\r\n	nuRunQuery ( <<<EOSQL\r\n		CREATE TABLE $dataTable \r\n		SELECT\r\n			fms_detail_display AS fmsDetailDisplay,\r\n			fms_abn AS fmsABN,\r\n			fms_name AS seCompany,\r\n			fms_address AS seAddress,\r\n			fms_city AS seCity,\r\n			fms_post_code AS sePostcode,\r\n			fms_fax AS seFax,\r\n			fms_phone AS sePhone,\r\n			fms_state AS seState\r\n		FROM\r\n			financial_module_setting\r\nEOSQL\r\n	);\r\n	\r\n	// Add additional fields to be populated later\r\n	nuRunQuery( <<<EOSQL\r\n		ALTER TABLE $dataTable\r\n		ADD invoice_EXC DOUBLE(10,2) DEFAULT 0.00,\r\n		ADD payment_INC DOUBLE(10,2) DEFAULT 0.00,\r\n		ADD payment_GST DOUBLE(10,2) DEFAULT 0.00,	\r\n		ADD payment_EXC DOUBLE(10,2) DEFAULT 0.00,\r\n		ADD credit_EXC DOUBLE(10,2) DEFAULT 0.00,\r\n		ADD refund_INC DOUBLE(10,2) DEFAULT 0.00,\r\n		ADD refund_GST DOUBLE(10,2) DEFAULT 0.00,\r\n		ADD refund_EXC DOUBLE(10,2) DEFAULT 0.00,\r\n		ADD discount_EXC DOUBLE(10,2) DEFAULT 0.00,\r\n		ADD rev_pmt_INC DOUBLE(10,2) DEFAULT 0.00,\r\n		ADD rev_pmt_GST DOUBLE(10,2) DEFAULT 0.00,	\r\n		ADD rev_pmt_EXC DOUBLE(10,2) DEFAULT 0.00,\r\n		ADD bad_debt_EXC DOUBLE(10,2) DEFAULT 0.00\r\nEOSQL\r\n	);\r\n	\r\n	// Get the sum ''invTotals'' for each transaction type\r\n	$sumSTR = <<<EOSQL\r\n		\r\n		SELECT\r\n			tra_type, tra_payment_method,\r\n			SUM(tra_total) AS theSum\r\n		FROM transaction\r\n		WHERE\r\n			tra_date >= ''$fromDate''\r\n			AND tra_date <= ''$toDate''\r\n			AND (\r\n					tra_type IN (''P'',''R'',''B'',''D'')\r\n					OR\r\n					(\r\n						tra_type IN (''C'',''I'')\r\n						AND tra_posted = ''1''\r