('14b6b6f4925a47', 'report', 'FM026', 'Accrual Debtors Summary Journal', 'Debtors', '145974108a4084', NULL, '// Increase memory allocation if necessary\r\n	// ini_set ("memory_limit", 128000000);\r\n\r\n	// Get values from selection form\r\n	$toDate	   	=	msql_date_nq($formValue[''vTo'']);\r\n	$fromDate  	= 	msql_date_nq($formValue[''vFrom'']);\r\n	\r\n	/* Create the dataTable structure */\r\n	\r\n	// Get the financial module setttings\r\n	nuRunQuery ( <<<EOSQL\r\n		CREATE TABLE $dataTable \r\n		SELECT\r\n			fms_detail_display AS fmsDetailDisplay,\r\n			fms_abn AS fmsABN,\r\n			fms_name AS seCompany,\r\n			fms_address AS seAddress,\r\n			fms_city AS seCity,\r\n			fms_post_code AS sePostcode,\r\n			fms_fax AS seFax,\r\n			fms_phone AS sePhone,\r\n			fms_state AS seState\r\n		FROM\r\n			financial_module_setting\r\nEOSQL\r\n	);\r\n	\r\n	// Add additional fields to be populated later\r\n	nuRunQuery( <<<EOSQL\r\n		ALTER TABLE $dataTable\r\n		ADD invoice_INC DOUBLE(10,2) DEFAULT 0.00,\r\n		ADD invoice_GST DOUBLE(10,2) DEFAULT 0.00,\r\n		ADD invoice_EXC DOUBLE(10,2) DEFAULT 0.00,\r\n		ADD payment_EXC DOUBLE(10,2) DEFAULT 0.00,\r\n		ADD credit_INC DOUBLE(10,2) DEFAULT 0.00,\r\n		ADD credit_GST DOUBLE(10,2) DEFAULT 0.00,\r\n		ADD credit_EXC DOUBLE(10,2) DEFAULT 0.00,\r\n		ADD refund_EXC DOUBLE(10,2) DEFAULT 0.00,\r\n		ADD discount_INC DOUBLE(10,2) DEFAULT 0.00,\r\n		ADD discount_GST DOUBLE(10,2) DEFAULT 0.00,\r\n		ADD discount_EXC DOUBLE(10,2) DEFAULT 0.00,\r\n		ADD rev_pmt_EXC DOUBLE(10,2) DEFAULT 0.00,\r\n		ADD bad_debt_INC DOUBLE(10,2) DEFAULT 0.00,\r\n		ADD bad_debt_GST DOUBLE(10,2) DEFAULT 0.00,\r\n		ADD bad_debt_EXC DOUBLE(10,2) DEFAULT 0.00\r\nEOSQL\r\n	);\r\n	\r\n	// Get the sum ''invTotals'' for each transaction type\r\n	$sumSTR = <<<EOSQL\r\n		\r\n		SELECT\r\n			tra_type, tra_payment_method,\r\n			SUM(tra_total) AS theSum\r\n		FROM transaction\r\n		WHERE\r\n			tra_date >= ''$fromDate''\r\n			AND tra_date <= ''$toDate''\r\n			AND (\r\n					tra_type IN (''P'',''R'',''B'',''D'')\r\n					OR\r\n					(\r\n						tra_type IN (''C'',''I'')\r\n						AND tra_posted = ''1''\r\n					)\r\n				)\r\n		AND tra_total IS NOT NULL\r\n		GROUP BY tra_type	\r\nEOSQL;\r\n\r\n	$sumQRY = nuRunQuery($sumSTR);\r\n	\r\n	// Loop through the rows to separate out each type/sum pairing\r\n	while ( $sumOBJ = db_fetch_object($sumQRY) ) {\r\n	\r\n		$invType 			= $sumOBJ->tra_type;\r\n		$invPaymentMethod 	= $sumOBJ->tra_payment_method;						// SDR 2009-11-13\r\n		$theSum   			= $sumOBJ->theSum;\r\n		\r\n		if ( $invType == ''I'' ) {							\r\n			$invoice_INC	= $theSum;											// Invoices\r\n			$invoice_GST 	= $invoice_INC / 11;\r\n			$invoice_EXC = $invoice_INC - $invoice_GST;\r\n		} else if ( $invType == ''P'' && $invPaymentMethod != ''Bad Debt'') {		// (Allocated) Payments\r\n			$payment_INC = $theSum;\r\n			$payment_GST = $payment_INC / 11;\r\n			$payment_EXC = $payment_INC - $payment_GST;\r\n		} else if ( $invType == ''P'' && $invPaymentMethod == ''Bad Debt'') {		// Bad Debts written off || SDR 2009-11-13\r\n			$bad_debt_INC = $theSum;\r\n			$bad_debt_GST = $bad_debt_INC / 11;\r\n			$bad_debt_EXC = $bad_debt_INC - $bad_debt_GST;	\r\n		} else if ( $invType == ''C'' ) {					\r\n			$credit_INC = $theSum * -1;											// Credit Notes are negative, but Journal figures need to be non-negative\r\n			$credit_GST = $credit_INC / 11;\r\n			$credit_EXC = $credit_INC - $credit_GST;\r\n		} else if ( $invType == ''R'' ) {					\r\n			$refund_INC = $theSum * -1;											// Refunds are negative\r\n			$refund_GST = $refund_INC / 11;\r\n			$refund_EXC = $refund_INC - $refund_GST;\r\n		} else if ( $invType == ''D'' ) {					\r\n			$discount_INC = $theSum;											// Discounts are positive\r\n			$discount_GST = $discount_INC / 11;\r\n			$discount_EXC = $discount_INC - $discount_GST;	\r\n		} else if ( $invType == ''B'' ) {					\r\n			$rev_pmt_INC = $theSum;												// Reverse Payments are positive\r\n			$rev_pmt_GST = $rev_pmt_INC / 11;\r\n			$rev_pmt_EXC = $rev_pmt_INC - $rev_pmt_GST;	\r\n		}\r\n\r\n	}	\r\n	\r\n	// Update the row of the dataTable	\r\n	nuRunQuery ( <<<EOSQL\r\n		UPDATE $dataTable\r\n		SET\r\n			invoice_INC	 = ''$invoice_INC'',\r\n			invoice_GST	 = ''$invoice_GST'',\r\n			invoice_EXC	 = ''$invoice_EXC'',\r\n			payment_EXC	 = ''$payment_EXC'',\r\n			credit_INC 	 = ''$credit_INC'',\r\n			credit_GST 	 = ''$credit_GST'',\r\n			credit_EXC 	 = ''$credit_EXC'',\r\n			refund_EXC	 = ''$refund_EXC'',\r\n			discount_INC = ''$discount_INC'',\r\n			discount_GST = ''$discount_GST'',\r\n			discount_EXC = ''$discount_EXC'',\r\n			rev_pmt_EXC	 = ''$rev_pmt_EXC'',\r\n			bad_debt_INC = ''$bad_debt_INC'',\r\n			bad_debt_GST = ''$bad_debt_GST'',\r\n			bad_debt_EXC = ''$bad_debt_EXC''\r\nEOSQL\r\n	);', NULL, 'class Reporting{\r\n\r\n   var $nuBuilder          = ''1'';\r\n   var $Controls           = array();\r\n   var $Sections           = array();\r\n   var $Groups             = array();\r\n   var $Version            = ''3'';\r\n   var $Width              = ''10200'';\r\n   var $Height             = ''1020'';\r\n   var $PaperType          = '''';\r\n   var $Orientation        = ''0'';\r\n\r\n\r\n   function Reporting(){\r\n      $this->Controls[0]->Name                = ''ReportTitle'';\r\n      $this->Controls[0]->Section             = ''3'';\r\n      $this->Controls[0]->ControlType         = ''Field'';\r\n      $this->Controls[0]->Tag                 = ''Field'';\r\n      $this->Controls[0]->ControlSource       = ''ReportTitle'';\r\n      $this->Controls[0]->Caption             = ''ReportTitle'';\r\n      $this->Controls[0]->Value               = ''ReportTitle'';\r\n      $this->Controls[0]->Top                 = ''35'';\r\n      $this->Controls[0]->Left                = ''307'';\r\n      $this->Controls[0]->Width               = ''365'';\r\n      $this->Controls[0]->Height              = ''27'';\r\n      $this->Controls[0]->ForeColor           = '''';\r\n      $this->Controls[0]->FontSize            = ''20'';\r\n      $this->Control