('14fd6a50a2829e', 'procedure', 'COLOURWIZARD', 'Quickly Change Colour Style', 'setup', '14fd6a640246f9', NULL, '', NULL, '', NULL, '2', NULL, '', '', '', '', NULL, '\r\n// ================================ FUNCTIONS ============================================\r\nfunction ColorLuminance($hex, $lum) {  \r\n	$hex = preg_replace("/[^0-9a-zA-Z]/", "", $hex);\r\n	// six hex digits\r\n	if (strlen($hex) == 6)\r\n		{\r\n		$r = hexdec(substr($hex,0,2));\r\n		$g = hexdec(substr($hex,2,2));\r\n		$b = hexdec(substr($hex,4,2));\r\n		}\r\n	// three hex digits	\r\n	else if (strlen($hex) == 3)\r\n		{\r\n		$r = hexdec(substr($hex,0,1) . substr($hex,0,1));\r\n		$g = hexdec(substr($hex,1,1) . substr($hex,1,1));\r\n		$b = hexdec(substr($hex,2,1) . substr($hex,2,1));\r\n		}\r\n	// unknown\r\n	else\r\n		{\r\n		return "000000";\r\n		}\r\n\r\n	$r = $r + ceil($r * $lum); if ($r < 0) {$r = 0;} if ($r > 255) {$r = 255;}\r\n	$g = $g + ceil($g * $lum); if ($g < 0) {$g = 0;} if ($g > 255) {$g = 255;}\r\n $b = $b + ceil($b * $lum); if ($b < 0) {$b = 0;} if ($b > 255) {$b = 255;}\r\n	return sprintf("%02X%02X%02X",$r,$g,$b);\r\n}  \r\n\r\nfunction getBrightness($hex) {\r\n	$hex = preg_replace("/[^0-9a-zA-Z]/", "", $hex);\r\n	if (strlen($hex) == 6)\r\n		{\r\n		$r = hexdec(substr($hex,0,2));\r\n		$g = hexdec(substr($hex,2,2));\r\n		$b = hexdec(substr($hex,4,2));\r\n		}\r\n	// three hex digits	\r\n	else if (strlen($hex) == 3)\r\n		{\r\n		$r = hexdec(substr($hex,0,1) . substr($hex,0,1));\r\n		$g = hexdec(substr($hex,1,1) . substr($hex,1,1));\r\n		$b = hexdec(substr($hex,2,1) . substr($hex,2,1));\r\n		}\r\n	// unknown\r\n	else\r\n		{\r\n		return 0;\r\n		}\r\n	return (($r * 299) + ($g * 587) + ($b * 114)) / 1000;\r\n}\r\n// ================================ FUNCTIONS ============================================\r\n\r\n\r\n// ================================ POSTED VARIABLES =====================================\r\n$BrsCol = "#cwiz_browse#";\r\n$TabCol = "#cwiz_tab#";\r\n$SrcCol = "#cwiz_search#";\r\n$ButCol = "#cwiz_button#";\r\nif (strcmp(trim($BrsCol),"") == 0) {\r\n	$res = nuRunQuery("SELECT ssp_property_value FROM zzsys_style_property WHERE (ssp_property_name = ''background-color'') AND (ssp_zzsys_style_id = (SELECT zzsys_style_id FROM zzsys_style WHERE (sst_class  = ''.selected'')))");\r\n	while($row = db_fetch_array($res)) { $BrsCol = $row[''ssp_property_value'']; }\r\n}\r\nif (strcmp(trim($TabCol),"") == 0) {\r\n	$res = nuRunQuery("SELECT ssp_property_value FROM zzsys_style_property WHERE (ssp_property_name = ''background-color'') AND (ssp_zzsys_style_id = (SELECT zzsys_style_id FROM zzsys_style WHERE (sst_class  = ''.tab'')))");\r\n	while($row = db_fetch_array($res)) { $TabCol = $row[''ssp_property_value'']; }\r\n}\r\nif (strcmp(trim($SrcCol),"") == 0) {\r\n	$res = nuRunQuery("SELECT ssp_property_value FROM zzsys_style_property WHERE (ssp_property_name = ''background-color'') AND (ssp_zzsys_style_id = (SELECT zzsys_style_id FROM zzsys_style WHERE (sst_class  = ''.searchcolor'')))");\r\n	while($row = db_fetch_array($res)) { $SrcCol = $row[''ssp_property_value'']; }\r\n}\r\nif (strcmp(trim($ButCol),"") == 0) {\r\n	$res = nuRunQuery("SELECT ssp_property_value FROM zzsys_style_property WHERE (ssp_property_name = ''background-color'') AND (ssp_zzsys_style_id = (SELECT zzsys_style_id FROM zzsys_style WHERE (sst_class  = ''.button'')))");\r\n	while($row = db_fetch_array($res)) { $ButCol = $row[''ssp_property_value'']; }\r\n}\r\n// remove Hashes from fronts of colors - added back when written to the database\r\n$BrsCol = preg_replace("/[^0-9a-zA-Z]/", "", $BrsCol);\r\n$TabCol = preg_replace("/[^0-9a-zA-Z]/", "", $TabCol);\r\n$SrcCol = preg_replace("/[^0-9a-zA-Z]/", "", $SrcCol);\r\n$ButCol = preg_replace("/[^0-9a-zA-Z]/", "", $ButCol);\r\n// ================================ POSTED VARIABLES =====================================\r\n\r\n// ================================ CALCULATED COLOURS =====================================\r\n$browseBackground = $BrsCol;\r\n$browseBorder = ColorLuminance($BrsCol, -0.5);  // %50 Darker\r\nif (getBrightness($BrsCol) > 128) {$tcol = "000000";} else {$tcol = "ffffff";}\r\n$browseText = $tcol;\r\n\r\n$selCol = ColorLuminance($BrsCol, -0.5); // %50 Darker for Selection\r\n$brsSelBackground = $selCol;\r\n$brsSelBorder = ColorLuminance($selCol, -0.5);  // %50 Darker\r\nif (getBrightness($selCol) > 128) {$tcol = "000000";} else {$tcol = "ffffff";}\r\n$brsSelText = $tcol;\r\n\r\n$lookCol = ColorLuminance($BrsCol, -0.5); // %50 Darker for Lookup\r\n$lkupdescBackground = $lookCol;\r\n$lkupdescBorder = ColorLuminance($lookCol, -0.5);  // %50 Darker\r\nif (getBrightness($lookCol) > 128) {$tcol = "000000";} else {$tcol = "ffffff";}\r\n$lkupdescText = $tcol;\r\n\r\n//	scrollbars\r\n$scrlbarFace = ColorLuminance($BrsCol, -0.1); // %10 Darker\r\n$scrlbarBase = ColorLuminance($BrsCol, 0.3);  // %30 Lighter\r\n$scrlbarShadow = ColorLuminance($BrsCol, -0.5); // %50 Darker\r\n$scrlbarDarkshadow = ColorLuminance($BrsCol, -0.9); // %90 Darker\r\n$scrlbarHighlight = ColorLuminance($BrsCol, 0.4);  // %40 Lighter\r\n\r\n$tabBackground = $TabCol;\r\n$tabBorder = ColorLuminance($TabCol, -0.5);  // %50 Darker\r\nif (getBrightness($TabCol) > 128) {$tcol = "000000";} else {$tcol = "ffffff";}\r\n$tabText = $tcol;\r\n\r\n$buttonBackground = $ButCol;\r\n$buttonBorder = ColorLuminance($ButCol, -0.4); // %40 Darker\r\nif (getBrightness($ButCol) > 128) {$tcol = "000000";} else {$tcol = "ffffff";}\r\n$buttonText = $tcol;\r\n\r\n$searchBackground = $SrcCol;\r\n$searchBorder = ColorLuminance($SrcCol, -0.4); // %40 Darker\r\nif (getBrightness($SrcCol) > 128) {$tcol = "000000";} else {$tcol = "ffffff";}\r\n$searchText = $tcol;\r\n\r\n$lkupcodeBackground = $SrcCol;\r\n$lkupcodeBorder = ColorLuminance($SrcCol, -0.4); // %40 Darker\r\nif (getBrightness($SrcCol) > 128) {$tcol = "000000";} else {$tcol = "ffffff";}\r\n$lkupcodeText = $tcol;\r\n// ================================ CALCULATED COLOURS =====================================\r\n\r\n// BUTTONS\r\n$SQL = "SELECT zzsys_style_id FROM zzsys_style WHERE (sst_class = ''.button'') or (sst_class = ''.actionButton'') or (sst_class = ''.calbutton'')";\r\n$res = nuRunQuery($SQL);\r\nwhile($row = db_fetch_array($res)) {\r\n	nuRunQuery("UPDATE zzsys_style_property SET ssp_property_value = ''#" . $buttonBackground . "'' WHERE (ssp_zzsys_style_id = ''" . $row[''zzsys_style_id''] . "'') AND (ssp_property_name = ''background-color'')");\r\n	nuRunQuery("UPDATE zzsys_style_property SET ssp_property_value = ''#" . $buttonBorder . "'' WHERE (ssp_zzsys_style_id = ''" . $row[''zzsys_style_id''] . "'') AND (ssp_property_name = ''border-color'')");\r\n	nuRunQuery("UPDATE zzsys_style_property SET ssp_property_value = ''#" . $buttonText . "'' WHERE (ssp_zzsys_style_id = ''" . $row[''zzsys_style_id''] . "'') AND (ssp_property_name = ''color'')");\r\n}\r\n\r\n// SEARCH\r\n$SQL = "SELECT zzsys_style_id FROM zzsys_style WHERE (sst_class = ''.searchcolor'')";\r\n$res = nuRunQuery($SQL);\r\nwhile($row = db_fetch_array($res)) {\r\n	nuRunQuery("UPDATE zzsys_style_property SET ssp_property_value = ''#" . $searchBackground . "'' WHERE (ssp_zzsys_style_id = ''" . $row[''zzsys_style_id''] . "'') AND (ssp_property_name = ''background-color'')");\r\n	nuRunQuery("UPDATE zzsys_style_property SET ssp_property_value = ''#" . $searchBorder . "'' WHERE (ssp_zzsys_style_id = ''" . $row[''zzsys_style_id''] . "'') AND (ssp_property_name = ''border-color'')");\r\n	nuRunQuery("UPDATE zzsys_style_property SET ssp_property_value = ''#" . $searchText . "'' WHERE (ssp_zzsys_style_id = ''" . $row[''zzsys_style_id''] . "'') AND (ssp_property_name = ''color'')");\r\n}\r\n\r\n// LOOKUP CODE\r\n$SQL = "SELECT zzsys_style_id FROM zzsys_style WHERE (sst_class = ''.lookupcode'')";\r\n$res = nuRunQuery($SQL);\r\nwhile($row = db_fetch_array($res)) {\r\n	nuRunQuery("UPDATE zzsys_style_property SET ssp_property_value = ''#" . $lkupcodeBackground . "'' WHERE (ssp_zzsys_style_id = ''" . $row[''zzsys_style_id''] . "'') AND (ssp_property_name = ''background-color'')");\r\n	nuRunQuery("UPDATE zzsys_style_property SET ssp_property_value = ''#" . $lkupcodeBorder . "'' WHERE (ssp_zzsys_style_id = ''" . $row[''zzsys_style_id''] . "'') AND (ssp_property_name = ''border-color'')");\r\n	nuRunQuery("UPDATE zzsys_style_property SET ssp_property_value = ''#" . $lkupcodeText . "'' WHERE (ssp_zzsys_style_id = ''" . $row[''zzsys_style_id''] . "'') AND (ssp_property_name = ''color'')");\r\n}\r\n\r\n// LOOKUP DESC\r\n$SQL = "SELECT zzsys_style_id FROM zzsys_style WHERE (sst_class = ''.lookupdesc'')";\r\n$res = nuRunQuery($SQL);\r\nwhile($row = db_fetch_array($res)) {\r\n	nuRunQuery("UPDATE zzsys_style_property SET ssp_property_value = ''#" . $lkupdescBackground . "'' WHERE (ssp_zzsys_style_id = ''" . $row[''zzsys_style_id''] . "'') AND (ssp_property_name = ''background-color'')");\r\n	nuRunQuery("UPDATE zzsys_style_property SET ssp_property_value = ''#" . $lkupdescBorder . "'' WHERE (ssp_zzsys_style_id = ''" . $row[''zzsys_style_id''] . "'') AND (ssp_property_name = ''border-color'')");\r\n	nuRunQuery("UPDATE zzsys_style_property SET ssp_property_value = ''#" . $lkupdescText . "'' WHERE (ssp_zzsys_style_id = ''" . $row[''zzsys_style_id''] . "'') AND (ssp_property_name = ''color'')");\r\n}\r\n\r\n// BROWSE\r\n$SQL = "SELECT zzsys_style_id FROM zzsys_style WHERE (sst_class = ''.nuUnselectedRow'') or (sst_class = ''.selected'')";\r\n$res = nuRunQuery($SQL);\r\nwhile($row = db_fetch_array($res)) {\r\n	nuRunQuery("UPDATE zzsys_style_property SET ssp_property_value = ''#" . $browseBackground . "'' WHERE (ssp_zzsys_style_id = ''" . $row[''zzsys_style_id''] . "'') AND (ssp_property_name = ''background-color'')");\r\n	nuRunQuery("UPDATE zzsys_style_property SET ssp_property_value = ''#" . $browseBorder . "'' WHERE (ssp_zzsys_style_id = ''" . $row[''zzsys_style_id''] . "'') AND (ssp_property_name = ''border-color'')");\r\n	nuRunQuery("UPDATE zzsys_style_property SET ssp_property_value = ''#" . $browseText . "'' WHERE (ssp_zzsys_style_id = ''" . $row[''zzsys_style_id''] . "'') AND (ssp_property_name = ''color'')");\r\n}\r\n\r\n// BROWSE SELECTED\r\n$SQL = "SELECT zzsys_style_id FROM zzsys_style WHERE (sst_class = ''.nuSelectedRow'')";\r\n$res = nuRunQuery($SQL);\r\nwhile($row = db_fetch_array($res)) {\r\n	nuRunQuery("UPDATE zzsys_style_property SET ssp_property_value = ''#" . $brsSelBackground . "'' WHERE (ssp_zzsys_style_id = ''" . $row[''zzsys_style_id''] . "'') AND (ssp_property_name = ''background-color'')");\r\n	nuRunQuery("UPDATE zzsys_style_property SET ssp_property_value = ''#" . $brsSelBorder . "'' WHERE (ssp_zzsys_style_id = ''" . $row[''zzsys_style_id''] . "'') AND (ssp_property_name = ''border-color'')");\r\n	nuRunQuery("UPDATE zzsys_style_property SET ssp_property_value = ''#" . $brsSelText . "'' WHERE (ssp_zzsys_style_id = ''" . $row[''zzsys_style_id''] . "'') AND (ssp_property_name = ''color'')");\r\n}\r\n\r\n// TAB\r\n$SQL = "SELECT zzsys_style_id FROM zzsys_style WHERE (sst_class = ''.unselected'') or (sst_class = ''.tab'') or (sst_class = ''.nuBrowse'') or (sst_class = ''.nuForm'') or (sst_class = ''.nuMenu'')";\r\n$res = nuRunQuery($SQL);\r\nwhile($row = db_fetch_array($res)) {\r\n	nuRunQuery("UPDATE zzsys_style_property SET ssp_property_value = ''#" . $tabBackground . "'' WHERE (ssp_zzsys_style_id = ''" . $row[''zzsys_style_id''] . "'') AND (ssp_property_name = ''background-color'')");\r\n	nuRunQuery("UPDATE zzsys_style_property SET ssp_property_value = ''#" . $tabBorder . "'' WHERE (ssp_zzsys_style_id = ''" . $row[''zzsys_style_id''] . "'') AND (ssp_property_name = ''border-color'')");\r\n	nuRunQuery("UPDATE zzsys_style_property SET ssp_property_value = ''#" . $tabText . "'' WHERE (ssp_zzsys_style_id = ''" . $row[''zzsys_style_id''] . "'') AND (ssp_property_name = ''color'')");\r\n}\r\n\r\n// SCROLLBARS\r\nnuRunQuery("UPDATE zzsys_style_property SET ssp_property_value = ''#" . $scrlbarBase . "'' WHERE (ssp_property_name = ''scrollbar-base-color'')");\r\nnuRunQuery("UPDATE zzsys_style_property SET ssp_property_value = ''#" . $scrlbarFace . "'' WHERE (ssp_property_name = ''scrollbar-face-color'')");\r\nnuRunQuery("UPDATE zzsys_style_property SET ssp_property_value = ''#" . $scrlbarShadow . "'' WHERE (ssp_property_name = ''scrollbar-shadow-color'')");\r\nnuRunQuery("UPDATE zzsys_style_property SET ssp_property_value = ''#" . $scrlbarDarkshadow . "'' WHERE (ssp_property_name = ''scrollbar-darkshadow-color'')");\r\nnuRunQuery("UPDATE zzsys_style_property SET ssp_property_value = ''#" . $scrlbarHighlight . "'' WHERE (ssp_property_name = ''scrollbar-highlight-color'')");\r\n\r\necho "Changes Complete...";\r\n', '2012-06-12 11:40:18', '2012-10-24 12:29:35', 'globeadmin'),
('150874640315e6', 'procedure', 'TESTEMAIL', 'Test Email Settings', 'setup', '14599715516e37', NULL, '', NULL, '', NULL, '2', NULL, '', '', '', '', NULL, 'require_once("emaillib.php");\r\necho "<pre>";\r\necho "Testing Email Settings\\n======================\\n";\r\n\r\n$sql="SELECT * FROM zzsys_setup";\r\n$res = nuRunQuery($sql);\r\nif($row = db_fetch_array($res)) {\r\n  $SMTPhost = $row[''set_smtp_host''];\r\n  $SMTPport = $row[''set_smtp_port''];\r\n  $SMTPauth = $row[''set_smtp_use_ssl''];\r\n  $SMTPuser = $row[''set_smtp_username''];\r\n  $SMTPpass = $row[''set_smtp_password''];\r\n  $SMTPfrom = $row[''set_smtp_from_address''];\r\n  $SMTPfnam = $row[''set_smtp_from_name''];\r\n\r\n  echo "\\nSMTPhost: " .$SMTPhost;\r\n  echo "\\nSMTPport: " .$SMTPport;\r\n  echo "\\nSMTPauth: " .$SMTPauth;\r\n  echo "\\nSMTPuser: " .$SMTPuser;\r\n  echo "\\nSMTPpass: " .$SMTPpass;\r\n  echo "\\nSMTPfrom: " .$SMTPfrom;\r\n  echo "\\nSMTPfnam: " .$SMTPfnam;\r\n\r\n  $content = "<H1>Test Email Settings</h1>";\r\n  $subject = "Test Email Settings";\r\n\r\n  $mail              = new PHPMailer();\r\n  $mail->IsSMTP();\r\n  $mail->Host          = $SMTPhost;\r\n  $mail->Port          = $SMTPport;\r\n  $mail->SMTPSecure    = $SMTPauth ? ''ssl'' : '''';\r\n  $mail->SMTPAuth      = $SMTPauth;\r\n  if ($SMTPauth) {\r\n    $mail->Username  = $SMTPuser;\r\n    $mail->Password  = $SMTPpass;\r\n  }\r\n   \r\n  $mail->From        = $SMTPfrom;\r\n  $mail->FromName    = $SMTPfnam;\r\n  $mail->AddAddress($SMTPfrom,$SMTPfnam);\r\n  $mail->AddReplyTo($SMTPfrom,$SMTPfnam);\r\n \r\n  $mail->AddReplyTo  = $SMTPfrom;\r\n  $mail->AddAddress  = $SMTPfrom;\r\n  $mail->WordWrap    = 80;\r\n  $mail->IsHTML(true);\r\n  $mail->Subject     = $subject;\r\n  $mail->Body        = $content;\r\n\r\n  echo "\\n\\nPhpMailer Says:\\n================\\n\\n";\r\n  if(!$mail->Send()) {\r\n    echo "Error sending: " . $mail->ErrorInfo;;\r\n  } else {\r\n    echo "Test sent";\r\n  } \r\n \r\n}\r\necho "</pre>";', '2012-10-24 12:07:04', '2012-10-24 12:29:23', 'globeadmin');
