INSERT INTO `zzsys_form` (`zzsys_form_id`, `sfo_name`, `sfo_title`, `sfo_type`, `sfo_access_without_login`, `sfo_close_after_save`, `sfo_refresh_after_save`, `sfo_sql_run_before_display`, `sfo_sql`, `sfo_table`, `sfo_primary_key`, `sfo_add_button`, `sfo_save_button`, `sfo_delete_button`, `sfo_clone_button`, `sfo_close_button`, `sfo_print_button`, `sfo_add_title`, `sfo_save_title`, `sfo_delete_title`, `sfo_clone_title`, `sfo_close_title`, `sfo_print_title`, `sfo_add_button_display_condition`, `sfo_save_button_display_condition`, `sfo_delete_button_display_condition`, `sfo_clone_button_display_condition`, `sfo_close_button_display_condition`, `sfo_print_button_display_condition`, `sfo_custom_file_run_before_open`, `sfo_custom_code_run_before_save`, `sfo_custom_file_run_after_save`, `sfo_custom_file_run_after_delete`, `sfo_custom_code_run_before_browse`, `sfo_custom_code_run_before_open`, `sfo_custom_code_run_after_save`, `sfo_custom_code_run_after_delete`, `sfo_report_selection`, `sfo_javascript`, `sfo_comment`, `sfo_help`, `sys_setup`, `sys_added`, `sys_changed`, `sys_user_id`, `sfo_custom_code_run_after_browse`, `sfo_redirect_form_id`, `sfo_row_height`, `sfo_row_zzsys_style_id`, `sfo_rows`, `sys_box_net`) VALUES
('object', 'Objects', 'nuBuilder Objects', NULL, NULL, '0', '1', NULL, 'SELECT form_name, zzsys_object.* FROM zzsys_object\r\nINNER JOIN #browseTable# ON zzsys_object.sob_zzsys_form_id = #browseTable#.form_id \r\nWHERE (sys_setup != ''10'') \r\nORDER BY form_name, sob_all_tab_number, sob_all_column_number, sob_all_order_number \r\n', 'zzsys_object', 'zzsys_object_id', '1', '1', '1', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', '', '', '', NULL, NULL, '', '', '', '', '$sql  = "CREATE TABLE #browseTable# SELECT zzsys_form_id AS form_id, CONCAT(sfo_name,'' (form)'') AS form_name FROM zzsys_form ";\r\nnuRunQuery($sql);\r\n\r\n$sql  = "INSERT INTO #browseTable# SELECT zzsys_object_id AS form_id,";\r\n$sql .= "CONCAT(sob_all_name,'' (subform)'') AS form_name ";\r\n$sql .= "FROM zzsys_object WHERE sob_all_type = ''subform''";\r\nnuRunQuery($sql);\r\n\r\n\r\n', '$sql  = "SELECT zzsys_object_id as ID FROM zzsys_object WHERE sob_all_type = ''subform''";\r\n$t    = nuRunQuery($sql);\r\n$s    = "\\n\\nvar sfIDs = new Array();\\n";\r\nwhile($r = db_fetch_object($t)){\r\n   $s.= "sfIDs[''$r->ID''] = ''1'';\\n";\r\n}\r\naddJSFunction($s);\r\n\r\n\r\n\r\n', '', '', '', '// Function will test that the object does not contain any blanks or illegal parent/child relationships (e.g. subform within a subform)\r\n// Function will alert the user of where the error occured\r\nfunction validateObjectFields() {\r\n	\r\n	var objType = document.getElementById(''sob_all_type'').value;\r\n	var displOn = document.getElementById(''sob_zzsys_form_id'').value\r\n	var parentType = sfIDs[displOn];\r\n\r\n/*\r\n	Main validation functions:\r\n\r\n	 nuValidatePositiveNumber(fieldName,labelName,[alternateMessage]) &&\r\n	 nuValidateNonNegativeNumber(fieldName,labelName,[alternateMessage]) &&\r\n	 nuValidateNonBlank(fieldName,labelName,[alternateMessage]) &&\r\n	 nuValidateNonZero(fieldName,labelName,[alternateMessage]) &&\r\n	 validateNotOnSubform(fieldTypeName,parentType)\r\n*/\r\n\r\n	\r\n	//-- All object types must have a form to be displayed upon, a field name and tab, column & order numbers  --//\r\n	var returnCode = (	( nuValidateNonBlank(''sob_zzsys_form_id'',''Displayed On'') ) &&\r\n		nuValidateNonBlank(''sob_all_type'',''Type'') &&\r\n		nuValidateNonBlank(''sob_all_name'',''Field Name'') &&\r\n		nuValidateNonBlank(''sob_all_tab_number'',''Tab Number'') &&\r\n		nuValidateNonBlank(''sob_all_column_number'',''Column Number'') &&\r\n		nuValidateNonBlank(''sob_all_order_number'',''Order Number'') );\r\n	\r\n	//-- Different criteria apply for each object type selected in ''All'' Tab --//\r\n	if (returnCode) {\r\n	\r\n		switch(objType) {\r\n		\r\n			case ''button'':\r\n				returnCode = nuValidatePositiveNumber(''sob_button_length'',''Button > Length'');\r\n			break;\r\n			\r\n			case ''display'':\r\n				returnCode = (nuValidatePositiveNumber(''sob_display_length'',''Display > Length'') &&\r\n						nuValidateNonBlank(''sob_display_sql'',''SQL to Display'') );\r\n			break;\r\n			\r\n			case ''dropdown'':\r\n				returnCode = (nuValidatePositiveNumber(''sob_dropdown_length'',''Dropdown > Length'') &&\r\n						nuValidateNonBlank(''sob_dropdown_sql'',''SQL'') );\r\n			break;\r\n\r\n			case ''graph'':\r\n				returnCode = (validateNotOnSubform(''Graph'',parentType) &&\r\n						nuValidateNonBlank(''sob_graph_code'',''Graph Code'') && \r\n						nuValidateNonBlank(''sob_graph_zzsys_graph_id'',''Graph Type'')	);\r\n			break;\r\n			\r\n			case ''html'':\r\n				returnCode = validateNotOnSubform(''HTML'',parentType);\r\n			break;\r\n			\r\n			case ''listbox'':\r\n				returnCode = (	validateNotOnSubform(''ListBox'',parentType) &&\r\n							nuValidatePositiveNumber(''sob_listbox_height'',''Listbox > Height'') &&\r\n							nuValidatePositiveNumber(''sob_listbox_length'',''Listbox > Length'') &&\r\n							nuValidateNonBlank(''sob_listbox_sql'',''Listbox > SQL'') );\r\n			break;\r\n\r\n			case ''lookup'':\r\n				returnCode = (	nuValidateNonBlank(''sob_lookup_id_field'',''Lookup > ID Field'')  &&\r\n							nuValidateNonBlank(''sob_lookup_code_field'',''Lookup > Code Field'') &&\r\n							nuValidateNonBlank(''sob_lookup_description_field'',''Lookup > Description Field'') &&\r\n							nuValidatePositiveNumber(''sob_lookup_code_length'',''Lookup > Code Length'') );\r\n			break;\r\n			\r\n			case ''subform'':\r\n				if(document.getElementById(''sob_subform_zzsysform_id'').value == ''''){\r\n					returnCode = (	validateNotOnSubform(''SubForm'',parentType) &&\r\n								nuValidateNonBlank(''sob_subform_table'',''Subform > Table'') &&\r\n								nuValidateNonBlank(''sob_subform_primary_key'',''Subform > Primary Key'') &&\r\n								nuValidateNonBlank(''sob_subform_foreign_key'',''Subform > Foreign Key'') &&\r\n								nuValidateNonBlank(''sob_subform_sql'',''Subform > SQL'') &&\r\n								nuValidateNonNegativeNumber(''sob_subform_left'',''Subform > Left Coordinate'') &&\r\n								nuValidateNonNegativeNumber(''sob_subform_top'',''Subform > Top'') &&\r\n								nuValidatePositiveNumber(''sob_subform_height'',''Subform > Height'') &&\r\n								nuValidateNonNegativeNumber(''sob_subform_width'',''Subform > Width'') &&\r\n								nuValidatePositiveNumber(''sob_subform_title_height'',''Subform > Title Height'') );\r\n				}else{\r\n					returnCode = (	validateNotOnSubform(''SubForm'',parentType) &&\r\n								nuValidateNonNegativeNumber(''sob_subform_left'',''Subform > Left Coordinate'') &&\r\n								nuValidateNonNegativeNumber(''sob_subform_top'',''Subform > Top'') &&\r\n								nuValidatePositiveNumber(''sob_subform_height'',''Subform > Height'') &&\r\n								nuValidateNonNegativeNumber(''sob_subform_width'',''Subform > Width'') );\r\n				}\r\n			break;\r\n			\r\n			case ''text'':\r\n				//returnCode = nuValidatePositiveNumber(''sob_text_length'',''Text > Length'');\r\n			break;\r\n			\r\n			case ''textarea'':\r\n				returnCode = (	nuValidatePositiveNumber(''sob_textarea_height'',''TextArea > Height'') &&\r\n							nuValidatePositiveNumber(''sob_textarea_length'',''TextArea > Length'') );\r\n			break;\r\n\r\n			case ''words'':\r\n			//Do nothing\r\n			break;\r\n		}\r\n	}\r\n	return returnCode;\r\n}\r\n\r\n//Function checks to see if a value is a positive number (greater than zero)\r\nfunction nuValidatePositiveNumber(fieldName,labelName,altMessage) {\r\n\r\n	var val = document.getElementById(fieldName).value;\r\n	\r\n    if ( val == '''' || isNaN(parseFloat(val)) ||  parseInt(val) <= 0  ) {\r\n		var message;\r\n		if (altMessage == undefined || altMessage == null) { \r\n			message = ''"'' + labelName + ''" must be a number greater than zero'';\r\n		} else {\r\n			message = altMessage;\r\n		}\r\n		alert(message);\r\n		return false;\r\n    } else {\r\n		return true;\r\n	}\r\n}\r\n\r\n//Function checks to see if a value is a number greater than or equal to zero\r\nfunction nuValidateNonNegativeNumber(fieldName,labelName,altMessage) {\r\n\r\n	var val = document.getElementById(fieldName).value;\r\n\r\n    if ( val == '''' || isNaN(parseFloat(val)) || ( parseInt(val) < 0 ) ) {\r\n		var message;\r\n		if (altMessage == undefined || altMessage == null) { \r\n			message = ''"'' + labelName + ''" must be a number greater than or equal to zero'';\r\n		} else {\r\n			message = altMessage;\r\n		}\r\n		alert(message);\r\n		return false;\r\n    } else {\r\n		return true;\r\n	}\r\n}\r\n\r\nfunction nuValidateNonZero(fieldName,labelName,altMessage) {\r\n\r\n	var val = document.getElementById(fieldName).value;\r\n\r\n    if ( parseInt(val) == 0 ) {\r\n		var message;\r\n		if (altMessage == undefined || altMessage == null) { \r\n			message = ''"'' + labelName + ''" cannot be equal to zero'';\r\n		} else {\r\n			message = altMessage;\r\n		}\r\n		alert(message);\r\n		return false;\r\n    } else {\r\n		return true;\r\n	}\r\n}\r\n\r\nfunction nuValidateNonBlank(fieldName,labelName,altMessage) {\r\n	\r\n	var field = document.getElementById(fieldName);\r\n	if (field.type == "select") {\r\n		field = field.options[field.selectedInde