('146008847abeba', 'FMInvoice', 'Invoices', NULL, '', '0', '1', '', 'SELECT * FROM transaction\r\nINNER JOIN customer \r\nON transaction.tra_customer_id = customer.customer_id \r\nLEFT JOIN zzsys_list \r\nON transaction.tra_posted = zzsys_list.sli_option  \r\nWHERE (sli_name = ''answer'') \r\nAND (tra_type = ''I'' OR tra_type = ''C'')\r\nORDER BY tra_number', 'transaction', 'transaction_id', '1', '1', '1', '0', '0', NULL, '', '', '', '', '', NULL, '', 'SELECT ''#tra_posted#'' != ''1''', 'SELECT ''#tra_posted#'' != ''1'' AND ''#recordID#'' != ''-1''', 'SELECT ''#tra_posted#'' != ''1'' AND ''#recordID#'' != ''-1''', '', NULL, 'SCFMInvoice.php', '//--', 'SCFMInvoice.php', 'SCFMInvoice.php', '', '//build javascript for screen\r\n$javascript = createJavaScriptSharedFunctions(); \r\naddJSFunction($javascript);', 'if($_POST["tra_posted"] == ''1''){\r\n\r\n	//if we are using the full financial module, we need to update the General Ledger\r\n	if (function_exists("invoiceLedgerUpdate") ) {\r\n	        invoiceLedgerUpdate($recordID);\r\n	}        \r\n\r\n	//getting values from post/form\r\n    	$inv_type                        = "#tra_type#";\r\n	$total_amount                    = "#tra_amount#";\r\n    	$total_tax                       = "#tra_tax#";\r\n	\r\n	//determine transaction type and whether values will be positive for negative\r\n    	if ($inv_type == ''C'') {\r\n		$t    = -1;\r\n	} else {\r\n    		$t    = 1;\r\n	}	\r\n	\r\n	// if transaction is a credit note, we need to set values to negative\r\n	if($inv_type == ''C''){\r\n     		nuRunQuery("UPDATE transaction_item SET tri_tax_amount = tri_tax_amount * $t, tri_total = tri_total * $t, tri_amount = tri_amount * $t WHERE tri_transaction_id = ''$recordID''");\r\n       		nuRunQuery("UPDATE transaction SET tra_tax = $total_tax * $t, tra_amount = $total_amount * $t, tra_total = tra_total * $t WHERE transaction_id = ''$recordID''");     \r\n    	}\r\n\r\n	//set amount outstanding\r\n        nuRunQuery("UPDATE transaction SET tra_outstanding = tra_total WHERE transaction_id = ''$recordID''");\r\n	     	\r\n} //end if posted\r\n\r\n\r\n\r\n\r\n', '', '0', 'function nuLoadThis() {\r\n	calcinv_total();\r\n}\r\n\r\nfunction nuBeforeSave(){\r\n	\r\n	if ( !validateCloseDate() )\r\n		return false;\r\n	\r\n	calcinv_total();\r\n	return true;\r\n\r\n}\r\n\r\nfunction calclinetotal(pthis) {\r\n   \r\n   	if (pthis.length == 18) {\r\n		// Parameter passed through from tri_product_id\r\n   		PRE = pthis;\r\n   	} else {  \r\n		// Parameter passed through from tri_units or tri_cost\r\n   		PRE = pthis.name.substring(0,18);	\r\n   	}\r\n   \r\n   	//calculate the total of the line by quantity * cost\r\n	unit                                = Number(document.getElementById(PRE+''tri_units'').value);\r\n	unitCost                            = Number(document.getElementById(PRE+''tri_unit_cost'').value);\r\n	taxPercent                          = Number(document.getElementById(PRE+''tri_tax_percent'').value);\r\n\r\n	//Make sure rounding is correct before setting field value\r\n	price                               = Math.round(unit  * unitCost * 100)/100;\r\n	taxAmount                           = Math.round(unit  * unitCost * taxPercent * 100)/100;\r\n	total                               = price + taxAmount;\r\n\r\n	document.getElementById(PRE+''tri_amount'').value        = price;\r\n	document.getElementById(PRE+''tri_tax_amount'').value    = taxAmount;\r\n	document.getElementById(PRE+''tri_total'').value         = total;\r\n\r\n	//if the values of the total field is blank then fill them in\r\n	if(document.getElementById(PRE+''tri_total'').value < ''0''){\r\n\r\n		document.getElementById(PRE+''tri_amount'').value        = price;\r\n		document.getElementById(PRE+''tri_tax_amount'').value    = taxAmount;\r\n		document.getElementById(PRE+''tri_total'').value         = total;\r\n	}\r\n\r\n	nuFormat(document.getElementById(PRE+''tri_units''));\r\n	nuFormat(document.getElementById(PRE+''tri_unit_cost''));\r\n	nuFormat(document.getElementById(PRE+''tri_total''));\r\n\r\n	calcinv_total();\r\n   \r\n}\r\n\r\nfunction calcinv_total() {\r\n   \r\n        //calculate the entire total of all the line totals\r\n	theRows                       = Number(document.getElementById(''rowsFMinvoice_item'').value);\r\n\r\n	invAmount                     = ''0'';\r\n	invTax                        = ''0'';\r\n\r\n        //for every line...\r\n	for (int = 0;int < theRows; ++int) {   \r\n\r\n		strlength	= ''0000''+int;\r\n		pstrlength      = strlength.length;\r\n		PRE             = ''FMinvoice_item'' + (''0000''+int).substring(pstrlength-4);\r\n		price           = Number(document.getElementById(PRE+''tri_amount'').value);\r\n		taxAmount       = Number(document.getElementById(PRE+''tri_tax_amount'').value);\r\n		total           = Number(document.getElementById(PRE+''tri_total'').value);\r\n\r\n		//if delete is not checked then add prices\r\n		if (document.getElementById("row"+PRE).checked != true) {\r\n\r\n			invAmount	= Number(invAmount) + price;\r\n		        invTax		= Number(invTax) + taxAmount;\r\n\r\n			nuFormat(document.getElementById(PRE+''tri_amount''));\r\n			nuFormat(document.getElementById(PRE+''tri_tax_amount''));\r\n			nuFormat(document.getElementById(PRE+''tri_total''));\r\n			\r\n		//else set to zero and don''t add\r\n		} else {\r\n\r\n			document.getElementById(PRE+''tri_amount'').value = 0;\r\n			document.getElementById(PRE+''tri_tax_amount'').value = 0;\r\n			document.getElementById(PRE+''tri_total'').value = 0;\r\n			\r\n			nuFormat(document.getElementById(PRE+''tri_amount''));\r\n			nuFormat(document.getElementById(PRE+''tri_tax_amount''));\r\n			nuFormat(document.getElementById(PRE+''tri_total''));\r\n		}\r\n	}\r\n   \r\n\r\n	document.getElementById(''tra_amount'').value        = invAmount; \r\n	document.getElementById(''tra_tax'').value           = invTax;\r\n	document.getElementById(''tra_total'').value         = invAmount + invTax;  \r\n\r\n	nuFormat(document.getElementById(''tra_amount''));\r\n	nuFormat(document.getElementById(''tra_tax''));\r\n	nuFormat(document.getElementById(''tra_total''));\r\n       \r\n}\r\n\r\nfunction getRowPrefixFromLookup(pthis) {\r\n	// Needed to update row price totals if user changes a product lookup\r\n	PRE  = pthis.name.substring(4,22);\r\n	nuCreateCookie(''row_name_for_transaction_item'', PRE, 0);	\r\n	calclinetotal(nuReadCookie(''row_name_for_transaction_item''));	\r\n}', NULL, NULL, '0', '2007-03-20 20:20:07', '2011-12-20 13:24:30', 'globeadmin', '', NULL, 0, 0, 0, '');
