('14a66624d0ba47', 'FMTransactionLog', 'Transaction Log', NULL, '', '', '1', NULL, 'SELECT * FROM ledger_log', 'ledger_log', 'ledger_log_id', '1', '1', '1', '', '', NULL, '', '', '', '', '', NULL, '', 'SELECT ''#lld_posted#'' <> ''1''', 'SELECT ''#lld_posted#'' <> ''1''', '', '', NULL, NULL, NULL, NULL, NULL, '', '$fms = nuRunQuery("SELECT * FROM financial_module_setting");\r\n$fmsObj = db_fetch_object($fms);\r\n$fmsTransactionDueDate = $fmsObj->fms_transaction_due_date;     \r\n   \r\n$javascript = " function  nuLoadThis() {\\n";\r\n	\r\n$javascript .= "	      document.getElementById(''fms_transaction_due_date'').value    = ''$fmsTransactionDueDate''; \\n";\r\n$javascript .= "	      TheRows = document.getElementById(''rowsSFFMTransactionLog'').value;\\n";\r\n$javascript .= "	      credit = 0;\\n";\r\n$javascript .= "	      debit  = 0;\\n";\r\n$javascript .= "	      for(z=0;z<TheRows;z++){\\n";\r\n$javascript .= "	            PREThis =''SFFMTransactionLog''+right(''0000''+z,4);\\n";\r\n$javascript .= "	            if(document.getElementById(PREThis+''lli_ledger_id'').value!=''''){\\n";\r\n$javascript .= "	                 amount   = Number(document.getElementById(PREThis+''lli_amount'').value);\\n";\r\n$javascript .= "	                 if(Number(amount)<0){\\n";\r\n$javascript .= "	                    credit  = Number(credit) +Number((amount*-1));\\n";\r\n$javascript .= "	                    document.getElementById(PREThis+''Credit'').value = Number(amount*-1);         \\n";\r\n$javascript .= "	                }else{ \\n";\r\n$javascript .= "	                   debit  = Number(debit) + Number(amount);\\n";\r\n$javascript .= "	                   document.getElementById(PREThis+''Debit'').value = Number(amount);\\n";\r\n$javascript .= "	                }\\n";\r\n$javascript .= "	           }\\n"; \r\n$javascript .= "	      	if(document.getElementById(PREThis+''Credit'').value == ''''){ \\n"; \r\n$javascript .= "	      		document.getElementById(PREThis+''Credit'').value = 0.00; \\n";       \r\n$javascript .= "	      	} \\n"; \r\n$javascript .= "	      	if(document.getElementById(PREThis+''Debit'').value == ''''){ \\n"; \r\n$javascript .= "	      		document.getElementById(PREThis+''Debit'').value = 0.00; \\n";       \r\n$javascript .= "	      	}\\n";\r\n$javascript .= "	        nuFormat(document.getElementById(PREThis+''Credit''));\\n";\r\n$javascript .= "	        nuFormat(document.getElementById(PREThis+''Debit''));\\n"; \r\n$javascript .= "	      }\\n";\r\n$javascript .= "	      document.getElementById(''credittotal'').value = Number(credit);\\n";\r\n$javascript .= "	      document.getElementById(''debittotal'').value  = Number(debit);\\n";\r\n$javascript .= "	      nuFormat(document.getElementById(''credittotal''));\\n";\r\n$javascript .= "	      nuFormat(document.getElementById(''debittotal''));\\n";\r\nif($this->recordID == ''-1'') {\r\n	$javascript .= "		  document.getElementById(''lld_type'').value = ''Manual'';\\n";\r\n}\r\n$javascript .= " }\\n";\r\n\r\n$this->appendJSfunction($javascript);\r\n', '', '$sql  = "DELETE FROM ledgerlogitem WHERE lliledgerlogID = ''$recordID''";\r\nnuRunQuery($sql);\r\n', '0', 'function nuBeforeSave(){\r\n\r\n	if(document.getElementById(''credittotal'').value != document.getElementById(''debittotal'').value && document.getElementById(''lld_posted'').value == ''1''){\r\n                document.getElementById(''lld_posted'').value = ''0'';\r\n		alert(''Credits need to equal Debits'');\r\n		return false;\r\n	}\r\n\r\n	if((document.getElementById(''credittotal'').value ==''0.00'' || document.getElementById(''debittotal'').value == ''0.00'') && document.getElementById(''lld_posted'').value == ''1''){\r\n                document.getElementById(''lld_posted'').value = ''0'';\r\n		alert(''Credits or Debits should not equal to 0'');\r\n		return false;\r\n	}\r\n	return true;\r\n}\r\n\r\nfunction calcline(){\r\n\r\n	var TheRows = Number(document.getElementById(''rowsSFFMTransactionLog'').value);\r\n	var credit = 0;\r\n	var debit  = 0;\r\n	for(i=0;i<TheRows;i++){\r\n		PREThis =''SFFMTransactionLog''+right(''0000''+i,4);\r\n		if(document.getElementById(PREThis+''Credit'').value!=''0.00'' && document.getElementById(PREThis+''Credit'').value!=''''){\r\n			Credit  = Number(document.getElementById(PREThis+''Credit'').value);\r\n			credit  = credit +Credit;\r\n			document.getElementById(PREThis+''lli_amount'').value = Credit * -1;        \r\n	\r\n		}\r\n		if( document.getElementById(PREThis+''Debit'').value!=''0.00'' && document.getElementById(PREThis+''Debit'').value!=''''){ \r\n	\r\n			Debit   = Number(document.getElementById(PREThis+''Debit'').value);\r\n			debit  = debit + Debit;\r\n			document.getElementById(PREThis+''lli_amount'').value = Debit ;  \r\n		} \r\n	}\r\n\r\n	document.getElementById(''credittotal'').value = credit;\r\n	document.getElementById(''debittotal'').value  = debit;\r\n	nuFormat(document.getElementById(''credittotal''));\r\n	nuFormat(document.getElementById(''debittotal''));\r\n\r\n}', '', '', '0', '2009-07-22 10:20:21', '2009-07-24 11:54:34', 'globeadmin', '', NULL, 0, 0, 0, ''),
('145ff691166ff6', 'Style', 'Style', NULL, '', '', '1', '', 'SELECT * FROM zzsys_style ORDER BY sst_class', 'zzsys_style', 'zzsys_style_id', '1', '1', '1', '1', '0', NULL, 'New Style', '', '', '', '', NULL, '', '', 'SELECT IF(''#sys_setup#'' = ''1'',''0'',''1'') ', '', '', NULL, '', NULL, '', '', '', '', '', '', '0', '', NULL, NULL, '1', '2007-03-19 23:54:41', '2009-06-10 11:24:55', 'globeadmin', NULL, NULL, 0, 0, 0, ''),
('14609afaf3cbad', 'FMPayment', 'Payments', NULL, '', '0', '1', '', 'SELECT * FROM customer', 'customer', 'customer_id', '0', '1', '0', '0', '', NULL, '', '', '', '', '', NULL, '', '', '', '', '', NULL, '', NULL, '', '', '', '$sql = "SELECT CURDATE() AS date";\r\n$result = nuRunQuery($sql);\r\n$date = db_fetch_object($result);\r\n$date = date("d-M-Y");\r\n\r\n$javascript = createJavaScriptSharedFunctions(); \r\n\r\n$javascript .= " function  nuLoadThis() {\\n";\r\n$javascript .= "if(document.getElementById(''unallocated'').value == '''' || document.getElementById(''payment_amount'').value == '''' || document.getElementById(''payment_discount'').value == ''''){ \\n";\r\n$javascript .= "	document.getElementById(''unallocated'').value = 0.00;\\n";\r\n$javascript .= "	document.getElementById(''payment_amount'').value = 0.00;\\n";\r\n$javascript .= "	document.getElementById(''payment_discount'').value = 0.00;\\n";\r\n$javascript .= "	document.getElementById(''tra_date'').value = ''".$date."'';\\n";\r\n$javascript .= "	nuFormat(document.getElementById(''payment_amount''));\\n";\r\n$javascript .= "	nuFormat(document.getElementById(''payment_discount''));\\n";\r\n$javascript .= "	nuFormat(document.getElementById(''unallocated''));\\n";\r\n//$javascript .= "	nuFormat(document.getElementById(''tra_date''));\\n";\r\n$javascript .= "}\\n";\r\n$javascript .= " initrows(); \\n";\r\n$javascript .= "}\\n";\r\n\r\naddJSFunction($javascript);\r\n\r\n', '// get variables from the screen\r\n$payment_description      = $_POST[''payment_description''];\r\n$payment_method           = $_POST[''payment_method''];\r\n$payChequeNo              = $_POST[''cheque_no''];\r\n$unallocated              = $_POST[''unallocated''];\r\n$payment_discount         = $_POST[''payment_discount''];\r\n$DiscountFlag             = $_POST[''discount''];\r\n$PaymentDate        	  = msql_date_nq($_POST[''tra_date'']);\r\n\r\n// Get payment number and date\r\n$paymentno          	= FMnextnum();    \r\n$individualID 		= $recordID;\r\n\r\n// make payment amount 0.00 if it is null for some reason (should never be null)\r\nif(empty($_POST[''payment_amount'']) || null == $_POST[''payment_amount'']) {\r\n	$payment_amount    	= 0.00;\r\n} else {\r\n	$payment_amount   	= $_POST[''payment_amount''];\r\n}\r\n\r\n// make new id for the payment header\r\n$paymentnewid           = uniqid(''1'');\r\n\r\n//add payment or discount header record into transaction table\r\nif($DiscountFlag == ''Payment''){\r\n	$sql  = "INSERT INTO transaction (";\r\n	$sql .= "transaction_id, tra_type, tra_number, tra_date, tra_description, tra_cheque_no, ";\r\n	$sql .= "tra_payment_method, tra_total, tra_previously_paid, tra_outstanding, tra_customer_id) ";\r\n	$sql .= "VALUES ( ";\r\n	$sql .= "''$paymentnewid'',''P'',$paymentno,''$PaymentDate'',\\"$payment_description\\",''$payChequeNo'',";\r\n	$sql .= "''$payment_method'',$payment_amount,0,$payment_amount,''$recordID'') ";\r\n	nuRunQuery($sql);\r\n}\r\nif($DiscountFlag == ''Discount''){\r\n\r\n	$sql  = "INSERT INTO transaction (";\r\n	$sql .= "transaction_id, tra_type, tra_number, tra_date, tra_description, tra_cheque_no, ";\r\n	$sql .= "tra_payment_method, tra_total, tra_previously_paid, tra_outstanding, tra_customer_id,tra_payment_discount) ";\r\n	$sql .= "VALUES ( ";\r\n	$sql .= "''$paymentnewid'',''D'',$paymentno,''$PaymentDate'',\\"$payment_description\\",''$payChequeNo'',";\r\n	$sql .= "''$payment_method'',$payment_discount,0,0,''$recordID'',$payment_discount) ";\r\n	nuRunQuery($sql);\r\n}\r\n\r\nfor($i = 0; $i < $_POST["rowsFMPaymentInvoices"]; $i++) {  // ** looping through subform from Payment Screen\r\n\r\n	$pstrlength =  (''0000''+$i).length;\r\n	$rowno      = ''FMPaymentInvoices''.substr("0000".$i,-4); \r\n\r\n	// only when paying field is NOT 0\r\n	if (round($_POST[$rowno."tra_paying"],2)!=0){\r\n		$inv_type           	= $_POST[$rowno."tra_type"];\r\n		$invoice_id         	= $_POST[$rowno."transaction_id"];\r\n		$inv_paying         	= round($_POST[$rowno."tra_paying"],2);\r\n		$inv_total          	= round($_POST[$rowno."tra_total"],2);\r\n		$inv_previously_paid 	= round($_POST[$rowno."tra_previously_paid"],2);\r\n		$Description       	= $_POST[$rowno."tra_type"]." ".$_POST[$rowno."tra_number"];\r\n		$invLedgerID       	= $_POST[$rowno."invLedgerID"];\r\n	\r\n		// calculate the outstanding and previously paid amounts\r\n		// add the unallocated amount to tra_previously_paid and subtract from tra_outstanding\r\n		// ADDED CREDIT NOTES TO THIS\r\n		// TAKE OUT  || $inv_type == ''C'' FROM if() TO REVERT CHANGES **\r\n		if($inv_type == ''U'' || $inv_type == ''C''){\r\n			$invNowPaid        	= $inv_paying     + $inv_previously_paid +$_POST[''unallocated''];\r\n			$inv_outstanding  	= $inv_total - $invNowPaid;\r\n			$inv_paid           	= iif($inv_outstanding==0,''1'',''0'');            	\r\n		} else {\r\n			$invNowPaid        	= $inv_paying     + $inv_previously_paid;\r\n			$inv_outstanding    	= $inv_total - $invNowPaid;\r\n			$inv_paid           	= iif($inv_outstanding==0,''1'',''0'');\r\n		}\r\n	\r\n		// update the invoice OR credit note with above calculated values\r\n		$sql  = "UPDATE transaction SET tra_previously_paid = $invNowPaid, ";\r\n		$sql .= "tra_outstanding = $inv_outstanding, ";\r\n		$sql .= "tra_paid_in_full = ''$inv_paid'' ";\r\n		$sql .= "WHERE transaction_id = ''$invoice_id''";\r\n		nuRunQuery($sql);\r\n	\r\n		// add the transaction item of the payment\r\n		$sql  = "INSERT INTO transaction_item (transaction_item_id, tri_transaction_id, tri_paying_invoice_id, ";\r\n		$sql .= "tri_description, tri_total) ";\r\n		$sql .= "VALUES (''".uniqid(''1'')."'', ''$paymentnewid'', ''$invoice_id'', ";\r\n		$sql .= "''Paying off $Description  '', $inv_paying) ";\r\n		nuRunQuery($sql);\r\n		\r\n		// Special case when a CREDIT NOTE is used for a CREDIT CLEAR \r\n		// we need to add additional transaction and transaction_items to track info needed a revers-credit-clear\r\n		//-- Required for REVERSE CREDIT CLEAR of CREDIT NOTE -- SDR 19-06-2009\r\n		if($payment_method == ''Credit Clear'' && $inv_type == ''C'' ){\r\n			\r\n			// Check that this is the first time this credit note was used as a credit clear\r\n			$t = nuRunQuery("SELECT transaction_id FROM transaction INNER JOIN transaction_item WHERE tri_transaction_id = ''$invoice_id'' AND tri_paying_invoice_id IS NOT NULL");\r\n			\r\n			if ( !$r = db_fetch_row($t) ) {\r\n				// Insert an additional row in transaction table to hold a ''phantom'' payment which links to the credit note\r\n				$pay_nextno = FMnextnum();    \r\n				$pay_newid  = uniqid(''1'');\r\n				\r\n				// Insert the ''phantom'' payment header record\r\n				$sql  = "INSERT INTO transaction (transaction_id,tra_type,tra_number,tra_date,tra_previously_paid,tra_total,tra_outstanding,tra_posted,tra_customer_id) ";\r\n				$sql .= "VALUES (''$pay_newid'', ''P'', ''$pay_nextno'', ''$PaymentDate'', 0,".$_POST[''payment_amount''].", ".$_POST[''payment_amount''].", ";\r\n				$sql .= "''0'',''$recordID'')";\r\n				nuRunQuery($sql);\r\n							\r\n				// Insert an additional row in transaction_item table to link the credit note to the ''phantom'' payment\r\n				$sql  = "INSERT INTO transaction_item (transaction_item_id, tri_transaction_id, tri_paying_invoice_id, ";\r\n				$sql .= "tri_description, tri_total) ";\r\n				$sql .= "VALUES (''".uniqid(''1'')."'', ''$invoice_id'', ''$pay_newid'', ";\r\n				$sql .= "''Paying off P $pay_nextno'', ''$inv_total'') ";\r\n				nuRunQuery($sql);\r\n			}	\r\n		} // end if Credit Clear	\r\n	}\r\n}\r\n\r\n// make an unallocated payment when unallocated is not 0\r\n// this can be from not applying a payment to an invoice\r\n// or applying a payment that is greater than an invoice(s) \r\nif($_POST[''unallocated'']  != ''0.00''){\r\n\r\n	// only where method is NOT credit clear\r\n	// if method is credit clear, u dont need to insert any new records\r\n	// because it is taken care of, in the update statement\r\n	if($payment_method != ''Cr