ue);  \r\n   	for(i = 0; i < rows; i++){ \r\n       		pstrlength    =  (''0000'' + i).length;\r\n       		PRE           = ''purchase_expense_item'' + (''0000'' + i).substring(pstrlength - 4);\r\n       		if(Number(document.getElementById(PRE + ''tri_units'').value) <= 0 && !document.getElementById(''row''+PRE).checked) {\r\n       			alert(''Expense units must be greater then 0 on line '' + (i+1));\r\n       			return false;\r\n       		}     \r\n   	} \r\n\r\n  	theDate             = mysql_date(document.getElementById(''tra_date'').value);\r\n	yy                  = parseInt(theDate.substring(0,4));\r\n	mm                  = parseInt(theDate.substring(5,7),10);\r\n	dd                  = parseInt(theDate.substring(8),10);\r\n	newDate             = new Date();\r\n	newDate.setFullYear(yy,mm-1,dd);\r\n		   \r\n	dueDate             = document.getElementById(''fms_transaction_due_date'').value;\r\n	yy                  = parseInt(dueDate.substring(0,4));\r\n	mm                  = parseInt(dueDate.substring(5,7),10);\r\n	dd                  = parseInt(dueDate.substring(8),10);\r\n	newDueDate          = new Date();\r\n	newDueDate.setFullYear(yy,mm - 1,dd);\r\n\r\n	//make sure date is in appropriate date range\r\n  	if (newDate <= newDueDate) {\r\n      		alert(''Cannot create a transaction before the close transaction period'');\r\n      		return false;\r\n  	}   \r\n\r\n	return true;\r\n}\r\n\r\nfunction calLine(pthis,screen){\r\n\r\n   	PRE       = pthis.name.substring(0,25);\r\n\r\n	//calculate the total of the line by quantity * cost\r\n	unit                                = Number(document.getElementById(PRE+''tri_units'').value);\r\n	unitCost                            = Number(document.getElementById(PRE+''tri_unit_cost'').value);\r\n	taxPercent                          = 0.1;\r\n\r\n	//Make sure rounding is correct before setting field value\r\n	price                               = Math.round(unit  * unitCost * 100)/100;\r\n	taxAmount                           = Mat