000'' + i).substring(pstrlength - 4);\r\n        	if (document.getElementById(PRE + ''pro_type'').value == ''None Stock Item'') {\r\n            		alert(''Can not update inventory for none stock item'');\r\n            		document.getElementById(PRE + ''tri_product_id'').value = ''''; \r\n            		document.getElementById(''code'' + PRE + ''tri_product_id'').value = ''''; \r\n            		document.getElementById(''name'' + PRE + ''tri_product_id'').value = '''';\r\n            		document.getElementById(''row'' + PRE).checked = true;  \r\n        	}\r\n      	}\r\n}\r\n\r\nfunction mysql_date(pdate){\r\n\r\n	if(pdate == '''') {\r\n		return '''';\r\n	}\r\n\r\n	if (pdate.length == 10) {\r\n		var rdate = "0" + pdate;\r\n	} else {\r\n		var rdate = pdate;\r\n	}\r\n\r\n	var newmth = ''01'';\r\n	mth = rdate.substring(3,6);\r\n	\r\n	if (mth == ''Jan'') {\r\n		newmth = ''01'';\r\n	}\r\n	if (mth == ''Feb'') {\r\n		newmth = ''02'';\r\n	}\r\n	if (mth == ''Mar'') {\r\n		newmth = ''03'';\r\n	}\r\n	if (mth == ''Apr'') {\r\n		newmth = ''04'';\r\n	}\r\n	if (mth == ''May'') {\r\n		newmth = ''05'';\r\n	}\r\n	if (mth == ''Jun'') {\r\n		newmth = ''06'';\r\n	}\r\n	if (mth == ''Jul'') {\r\n		newmth = ''07'';\r\n	}\r\n	if (mth == ''Aug'') {\r\n		newmth = ''08'';\r\n	}\r\n	if (mth == ''Sep'') {\r\n		newmth = ''09'';\r\n	}\r\n	if (mth == ''Oct'') {\r\n		newmth = ''10'';\r\n	}\r\n	if (mth == ''Nov'') {\r\n		newmth = ''11'';\r\n	}\r\n	if (mth == ''Dec'') {\r\n		newmth = ''12'';\r\n	}\r\n	\r\n	return rdate.substring(7) + "-" + newmth + "-" + rdate.substring(0,2);\r\n}\r\n', NULL, NULL, '0', '2007-06-27 10:01:24', '2009-11-04 12:11:09', 'globeadmin', NULL, NULL, 0, 0, 0, ''),
