('146d6528953a1f', 'FMPaymentforSupplier', 'Supplier Payments', NULL, '', '0', '1', '', 'SELECT * \r\nFROM supplier \r\nINNER JOIN supplier_group ON supplier_group_id = sup_supplier_group_id\r\n', 'supplier', 'supplier_id', '', '1', '', '', '', NULL, '', 'Save Record', '', '', '', NULL, '', '', '', '', '', NULL, NULL, NULL, NULL, NULL, '', ' //retrieve financial settings\r\n $fmsObj = financialSettings();\r\n\r\n //retrieve final transaction date\r\n $fmsTransactionDueDate = $fmsObj->fms_transaction_due_date;  \r\n\r\n //get today\r\n $today = nuDateFormat(Today(),''d-M-Y'');\r\n \r\n //update fields on loading\r\n $javascript  = "function nuLoadThis(){ \\n";\r\n //get date values\r\n $javascript .= "    mydate                           = new Date();\\n";\r\n $javascript .= "    day                              = mydate.getDate(); \\n";\r\n $javascript .= "    month                            = mydate.getMonth(); \\n";\r\n $javascript .= "    year                             = mydate.getFullYear(); \\n";\r\n //update fields on the page with payment details\r\n $javascript .= "    document.getElementById(''payment_date'').value       = day + ''/'' + (month+1) + ''/'' + year; \\n";\r\n $javascript .= "    document.getElementById(''payment_amount'').value     =''0.00''; \\n";\r\n $javascript .= "    document.getElementById(''unallocated_amount'').value =''0.00''; \\n";\r\n $javascript .= "    document.getElementById(''outstanding_amount'').value =''0.00''; \\n";\r\n $javascript .= "    nuFormat(document.getElementById(''payment_date'')); \\n";\r\n $javascript .= "    nuFormat(document.getElementById(''payment_amount'')); \\n";\r\n $javascript .= "    nuFormat(document.getElementById(''unallocated_amount'')); \\n";\r\n $javascript .= "    nuFormat(document.getElementById(''outstanding_amount'')); \\n";\r\n $javascript .= "    document.getElementById(''fms_transaction_due_date'').value    = ''$fmsTransactionDueDate''; \\n";\r\n $javascript .= "    initrows(); \\n";\r\n\r\n $javascript .= "}   \\n";\r\n\r\naddJSFunction($javascript);\r\n ', '	//get a new id for the record\r\n    	$newid                 	= uniqid(''1'');\r\n	\r\n	//loop through every purchase order being paid	    \r\n   	for($i = 0; $i <= $_POST["rowspurchase_order"]; $i++) {  // ** reading Rows from Payment Screen Loop\r\n    	$rowno             = "purchase_order".substr("0000".$i,-4);\r\n\r\n		//if the bill is having some amount paid on it then update its transaction record\r\n        	if (round($_POST[$rowno."tra_paying"],2)!=0){\r\n        \r\n			//get all data from the order row\r\n			$invType           = $_POST[$rowno."tra_type"];\r\n			$invoiceID         = $_POST[$rowno."transaction_id"];\r\n			$invPaying         = round($_POST[$rowno."tra_paying"],2);\r\n			$invTotal          = round($_POST[$rowno."tra_total"],2);\r\n			$invPreviouslyPaid = round($_POST[$rowno."tra_previously_paid"],2);\r\n			$Description       = $_POST[$rowno."tra_type"]." ".$_POST[$rowno."tra_number"];\r\n			$invNowPaid        = $invPaying     + $invPreviouslyPaid;\r\n			$invOutstanding    = $invTotal - $invNowPaid;\r\n			$invPaid           = iif($invOutstanding==0,''1'',''0'');\r\n			$iniPurchaseOrderDescription = $_POST[$rowno."purchase_order_description"];\r\n    \r\n			//update the transaction amounts as per the bill being paid\r\n			$sql               = "UPDATE transaction SET tra_previously_paid = $invNowPaid, ";\r\n			$sql               = $sql."tra_outstanding = $invOutstanding, ";\r\n			$sql               = $sql."tra_paid = ''$invPaid'' ";\r\n			$sql               = $sql."WHERE transaction_id = ''$invoiceID''";\r\n			nuRunQuery($sql);\r\n          \r\n			//add transaction item records as per the bill being paid\r\n			$sql               = "INSERT INTO transaction_item (transaction_item_id, tri_transaction_id, tri_paying_invoice_id, ";\r\n			$sql               = $sql."tri_description, tri_total,tri_purchase_order_description) ";\r\n			$sql               = $sql."VALUES (''".uniqid(''1'')."'', ''$newid'', ''$invoiceID'', ";\r\n			$sql               = $sql."''Paying off $Description '', $invPaying,''$iniPurchaseOrderDescription'') ";\r\n			nuRunQuery($sql);    \r\n        	}\r\n	}\r\n    \r\n	//get a new unique number and payment details for a transaction record\r\n	$nextno = FMnextnum();\r\n	$PaymentDate             	= msql_date_nq($_POST[''payment_date'']);\r\n	$PaymentAmount           	= $_POST[''payment_amount''];	\r\n	$PaymentDescription      	= $_POST[''payment_description''];\r\n	$PaymentMethod           	= $_POST[''payment_method''];\r\n	$payChequeNo             	= $_POST[''cheque_no''];\r\n    \r\n	//insert a new transaction record for the whole bill\r\n	$sql  = "INSERT INTO transaction (";\r\n	$sql .= "transaction_id, tra_type, tra_number, tra_date, tra_description, tra_cheque_no, ";\r\n	$sql .= "tra_payment_method, tra_total, tra_previously_paid, tra_outstanding, tra_customer_id) ";\r\n	$sql .= "VALUES ( ";\r\n	$sql .= "''$newid'',''CRP'',''$nextno'',''$PaymentDate'',\\"$PaymentDescription\\",''$payChequeNo'',";\r\n	$sql .= "''$PaymentMethod'',$PaymentAmount,0,0,''$recordID'') ";\r\n	nuRunQuery($sql);\r\n	$paydate =  $_POST[''payment_date''];\r\n	//if the ledger log function exists then run it\r\n	if(function_exists("supplierPaymentUpdateLedger")) {\r\n		supplierPaymentUpdateLedger($recordID,$newid,$paydate);\r\n	} ', '', '0', 'function  nuBeforeSave() {\r\n\r\n    	updateUnallocated();\r\n\r\n     	var Msg1                       = ''You are attempting to over pay for an invoice'';\r\n     	var Msg2                       = ''Payments allocated should equal to payment amount'';\r\n\r\n     	var theRows                    = Number(document.getElementById(''rowspurchase_order'').value);\r\n     	var unallocated                = Number(document.getElementById(''unallocated_amount'').value);\r\n     	var paymentAmount              = Number(document.getElementById(''payment_amount'').value);\r\n     	var totalActualPayment         = Number(0);\r\n     	var paymentMethod              = document.getElementById(''payment_method'').value;\r\n     	var chequeNo                   = document.getElementById(''cheque_no'').value;    \r\n	//check all data is valid\r\n     	if(document.getElementById(''payment_method'').value==''''){\r\n       		alert(''Payment method can not left blank'');\r\n        	return false;\r\n     	}\r\n\r\n     	if (paymentAmount==''0'' ){\r\n         	alert(''Can not make 0 dollar payment'');\r\n         	return false;\r\n     	}\r\n\r\n     	if (chequeNo=='''' && paymentMethod ==''Cheque'' ){\r\n         	alert(''Cheque number cannot left blank'');\r\n         	return false;\r\n     	}\r\n   \r\n     	//loop thru sub form and get total amount being paid\r\n     	for( int = 0; int < theRows; int++) {\r\n\r\n          	strlength           = ''0000''+int;\r\n          	pstrlength          = strlength.length;\r\n          	PRE                 = ''purchase_order'' + (''0000''+int).substring(pstrlength-4);\r\n          \r\n          	totalActualPayment  = round_decimals(totalActualPayment  + Number(document.getElementById(PRE+''tra_paying'').value),2);\r\n\r\n          	//check if over paying an invoice\r\n          	var bal                     = Number(document.getElementById(PRE+''tra_outstanding'').value);\r\n          	if (Number(bal) < 0) {\r\n              	 	bal = Number(bal) * -1;\r\n          	}\r\n          	var paying                 = Number(document.getElementById(PRE+''tra_paying'').value);\r\n          	if (Number(paying) < 0) {\r\n               		paying = Number(paying) * -1;\r\n          	}\r\n\r\n          	if (Number(paying) > Number(bal) ){\r\n              		alert(Msg1);\r\n              		return false;\r\n          	}\r\n    	}\r\n	//complain if total is incorrect\r\n    	if ( Number(totalActualPayment) != Number(paymentAmount ) ){\r\n         	alert(Msg2);\r\n         	return false;\r\n    	}\r\n     \r\n   	theDate = mysql_date(document.getElementById(''payment_date'').value);\r\n   	var yy = parseInt(theDate.substring(0,4));\r\n   	var mm = parseInt(theDate.substring(5,7),10);\r\n   	var dd = parseInt(theDate.substring(8),10);\r\n   	newDate = new Date();\r\n   	newDate.setFullYear(yy,mm-1,dd);\r\n   \r\n   	dueDate = (document.getElementById(''fms_transaction_due_date'').value);\r\n   	var yy = parseInt(dueDate.substring(0,4));\r\n   	var mm = parseInt(dueDate.substring(5,7),10);\r\n   	var dd = parseInt(dueDate.substring(8),10);\r\n   	newDueDate = new Date();\r\n   	newDueDate.setFullYear(yy,mm-1,dd);\r\n	//check dates are valid\r\n   	if (newDate <= newDueDate){\r\n       		alert(''Cannot create a transaction before the close transaction period'');\r\n       		return false;\r\n   	}\r\n\r\n   	return true;\r\n\r\n}\r\n\r\n\r\nfunction updateUnallocated() {\r\n\r\n   	theRows                    = Number(document.getElementById(''rowspurchase_order'').value);\r\n   	paymentAmount              = Number(document.getElementById(''payment_amount'').value);\r\n     	totalActualPayment         = Number(0);\r\n	//go through lal rows and add to a running total\r\n     	for(int = 0; int < theRows; int++){\r\n         	strlength                    = ''0000''+int;\r\n         	pstrlength                   = strlength.length;\r\n         	PRE                          = ''purchase_order'' + (''0000''+int).substring(pstrlength-4);\r\n         \r\n        	totalActualPayment           = Number(totalActualPayment)  + Number(document.getElementById(PRE+''tra_paying'').value);\r\n     	}\r\n	//add that total to the unallocated field\r\n     	if ( (Number(paymentAmount))>= Number(totalActualPayment) ) {\r\n        	document.getElementById(''unallocated_amount'').value = Number(paymentAmount) - Number(totalActualPayment);\r\n     	} else {\r\n        	document.getElementById(''unallocated_amount'').value = 0.00\r\n     	}\r\n\r\n      	nuFormat(document.getElementById(''unallocated_amount''));\r\n}\r\n\r\n\r\n\r\n\r\nfunction setUnallocated() {\r\n     \r\n     	document.getElementById(''unallocated_amount'').value  = Number(document.getElementById(''payment_amount'').value);\r\n     \r\n     	nuFormat(document.getElementById(''unallocated_amount''));\r\n     	nuFormat(document.getElementById(''payment_amount''));\r\n     \r\n     	initrows();\r\n}\r\n\r\n\r\n\r\nfunction calcline(pthis){\r\n\r\n  	var PRE  = pthis.name.substring(0,18);\r\n\r\n  	nuFormat(document.getElementById(''payment_amount''));\r\n  	nuFormat(document.getElementById(''unallocated_amount''));\r\n\r\n  	if (Number(document.getElementById(PRE+''tra_paying'').value) == 0.00 ) {\r\n      		if(  Number(document.getElementById(PRE+''tra_outstanding'').value) > Number(document.getElementById(''unallocated_amount'').value) ) {\r\n          		document.getElementById(PRE+''tra_paying'').value = Number(document.getElementById(''unallocated_amount'').value);\r\n      		} else {\r\n          		document.getElementById(PRE+''tra_paying'').value = Number(document.getElementById(PRE+''tra_outstanding'').value);\r\n      		}\r\n  	}\r\n\r\n  	updateUnallocated();\r\n\r\n  	nuFormat(document.getElementById(PRE+''tra_paying''));\r\n  	nuFormat(document.getElementById(''unallocated_amount''));\r\n}\r\n\r\n\r\n\r\nfunction validateMethod() {\r\n         \r\n         initrows();\r\n\r\n         if (document.getElementById(''payment_method'').value == ''Cheque'') {\r\n             document.getElementById(''cheque_no'').disabled = false;\r\n             document.getElementById(''cheque_no'').style.backgroundColor=''white'';\r\n         }else{\r\n             document.getElementById(''cheque_no'').value = '''';\r\n             document.getElementById(''cheque_no'').disabled = true;\r\n             document.getElementById(''cheque_no'').style.backgroundColor=''CCCCCC'';\r\n\r\n         }\r\n\r\n         document.getElementById(''payment_amount'').value = 0.00;\r\n         \r\n         nuFormat(document.getElementById(''payment_amount''));\r\n         \r\n         setUnallocated();\r\n\r\n}\r\n\r\nfunction initrows(){\r\n\r\n   	var TheRows    = Number(document.getElementById(''rowspurchase_order'').value);\r\n\r\n   	for (int = 0; int < TheRows; ++int) {\r\n       		strlength                    = ''0000''+int;\r\n       		pstrlength                   = strlength.length;\r\n       		PRE                          = ''purchase_order'' + (''0000''+int).substring(pstrlength-4);\r\n       		document.getElementById(PRE+''tra_paying'').value=''0.00'';\r\n       		document.getElementById(PRE+''tra_paying'').style.backgroundColor=''white'';\r\n       		document.getElementById(PRE+''tra_paying'').disabled = false;\r\n   	} // end for\r\n}\r\n\r\n\r\nfunction setPaymentAmount(){\r\n\r\n        var TheRows    = Number(document.getElementById(''rowspurchase_order'').value);\r\n     \r\n        document.getElementById(''payment_amount'').value   = 0;\r\n        for (int = 0; int < TheRows; ++int) {\r\n                strlength                    = ''0000''+int;\r\n       		pstrlength                   = strlength.length;\r\n       		PRE                          = ''purchase_order'' + (''0000''+int).substring(pstrlength-4);\r\n    	        document.getElementById(''payment_amount'').value   =  Number(document.getElementById(''payment_amount'').value) + Number(document.getElementById(PRE+''tra_outstanding'').value);\r\n        }\r\n    	        nuFormat(document.getElementById(''payment_amount''));\r\n}\r\n\r\nfunction round_decimals(original_number, decimals){\r\n\r\n   	power = Math.pow(10,decimals);\r\n   	r     = Math.round(original_number*power)/power;\r\n   	return r;\r\n\r\n}\r\n\r\n\r\nfunction mysql_date(pdate){\r\n   	if ( pdate == '''') {\r\n      		return '''';\r\n   	}\r\n\r\n   	if (pdate.length == 10) {\r\n      		rdate = "0" + pdate;\r\n   	} else {\r\n      		rdate = pdate;\r\n   	}\r\n\r\n   	newmth                      =''01'';\r\n   	mth                         = rdate.substring(3,6);\r\n\r\n   	if (mth == ''Jan'') {\r\n   		newmth = ''01'';\r\n   	}\r\n   	if (mth == ''Feb'') {\r\n   		newmth = ''02'';\r\n   	}\r\n   	if (mth == ''Mar'') {\r\n   		newmth = ''03'';\r\n   	}\r\n   	if (mth == ''Apr'') {\r\n   		newmth = ''04'';\r\n   	}\r\n   	if (mth == ''May'') {\r\n   		newmth = ''05'';\r\n   	}\r\n   	if (mth == ''Jun'') {\r\n   		newmth = ''06'';\r\n   	}\r\n   	if (mth == ''Jul'') {\r\n   		newmth = ''07'';\r\n   	}\r\n   	if (mth == ''Aug'') {\r\n   		newmth = ''08'';\r\n   	}\r\n  	if (mth == ''Sep'') {\r\n  		newmth = ''09'';\r\n  	}\r\n  	if (mth == ''Oct'') {\r\n  		newmth = ''10'';\r\n  	}\r\n   	if (mth == ''Nov'') {\r\n   		newmth = ''11'';\r\n   	}\r\n   	if (mth == ''Dec'') {\r\n   		newmth = ''12'';\r\n   	} \r\n\r\n   return rdate.substring(7) + "-" + newmth + "-" + rdate.substring(0,2);\r\n}\r\n\r\n\r\n\r\n', NULL, NULL, '0', '2007-08-30 14:15:53', '2009-07-02 09:29:18', 'globeadmin', NULL, NULL, 0, 0, 0, ''),
