('14875742d6665d', 'FMLU_parent_ledger', 'Parent Ledger', NULL, '', '', '1', NULL, 'SELECT * FROM ledger\r\nWHERE led_type = ''#led_type#''\r\nAND (led_postable = 0 OR led_postable IS NULL)\r\nAND led_code != ''#led_code#''\r\nORDER BY led_code', 'ledger', 'ledger_id', '0', '0', '0', '0', '', NULL, '', '', '', '', '', NULL, '', 'SELECT ''#led_built_in#'' != ''1''', '', 'SELECT ''#led_built_in#'' != ''1'' ', '', NULL, NULL, NULL, NULL, NULL, '', '', '', '', '0', '', NULL, NULL, '0', '2008-07-10 11:30:05', '2009-11-11 14:28:01', 'globeadmin', '$tmp = nuRunQuery("SELECT led_code FROM ledger WHERE ledger_id = ''#selectedID#''");\r\n$row = db_fetch_object($tmp);\r\n$parCode = $row->led_code;\r\n\r\n$id = ''#selectedID#'';\r\nif($id == '''') {\r\n        //do nothing\r\n} else if(substr($parCode,2,2) == ''00'') {\r\n	$sub = substr($parCode,0,2);\r\n\r\n	$code_array = array();\r\n	for($i = 1;$i < 100 ; $i+=1) {\r\n		if($i < 10) {\r\n			array_push($code_array, $sub.''0''.$i.''-00-00'');\r\n		} else {\r\n		        array_push($code_array, $sub.$i.''-00-00'');		\r\n		}\r\n	}\r\n	\r\n	$used_array = array();\r\n        $sub = $sub.''%-00-00'';\r\n	$tmp = nuRunQuery("SELECT led_code FROM `ledger` WHERE `led_code` LIKE ''$sub''");\r\n	while($row = db_fetch_object($tmp)) {\r\n		$code = $row->led_code;\r\n		array_push($used_array, $code);\r\n	}\r\n\r\n	$diff_array = array_diff($code_array,$used_array);\r\n	\r\n        $codes = '''';\r\n        $num = 0;\r\n	for($i = 0; $i < count($code_array) ; $i+=1) {\r\n		if($diff_array[$i] != '''') {\r\n			$codes = $codes.$diff_array[$i].''!'';\r\n			$num++;\r\n		}\r\n	}\r\n	\r\n	if($num > 0) {\r\n		$updateField[''led_status''] = $codes;\r\n	} else if($num == 0) {\r\n		$updateField[''led_status''] = ''No more children are available'';\r\n	}\r\n	\r\n} else if(substr($parCode,5,2) == ''00'') {\r\n	$sub = substr($parCode,0,5);\r\n\r\n	$code_array = array();\r\n	for($i = 1;$i < 100 ; $i+=1) {\r\n		if($i < 10) {\r\n			array_push($code_array, $sub.''0