('14a0d08d839464', 'FMBatch', 'Deposits', NULL, '', '', '1', NULL, 'SELECT * FROM batch', 'batch', 'batch_id', '1', '1', '', '', '', NULL, '', '', '', '', '', NULL, '', 'SELECT ''#bat_posted#'' != ''1''', '', '', '', NULL, NULL, NULL, NULL, NULL, '', '$javascript = createJavaScriptSharedFunctions();\r\n\r\n$sql = <<<EOSQL\r\n	\r\n	SELECT COUNT(*) AS num_rows FROM transaction \r\n	WHERE tra_type = ''P'' \r\n	AND tra_banked = 0 \r\n	AND tra_total > 0\r\n	AND (tra_payment_method = ''Cash'' \r\n	OR tra_payment_method = ''Cheque''\r\n	OR tra_payment_method = ''Other'')\r\n	ORDER BY tra_date\r\n\r\nEOSQL;\r\n$t = nuRunQuery($sql);\r\n\r\nwhile ($r = db_fetch_object($t)) {\r\n	$javascript .= " var NumRows = ".$r->num_rows."; \\n";\r\n}\r\n\r\naddJSFunction($javascript);', '// Initialise the document number	\r\n$document = 0;\r\n	\r\n// Update transactions that have had their payments banked\r\nfor($i = 0; $i <= $_POST["rowsFMOpenPayments"]; $i++) {  // Loop through subform rows\r\n\r\n	$rowno      = "FMOpenPayments".substr("0000".$i,-4);\r\n	$invoiceID 	= $_POST[$rowno."transaction_id"];\r\n	$banked 	= $_POST[$rowno."tra_banked"];\r\n	$batchID 	= ''#newID#'';\r\n	\r\n	// Flag the records that have been banked and link them to the batch\r\n	if ($banked == ''1''){\r\n		//Increment the document number\r\n		$document++;\r\n		\r\n		$sql  = "UPDATE transaction SET tra_document_no = ''$document'' ";\r\n		$sql .= "WHERE transaction_id = ''$invoiceID'' ";\r\n		nuRunQuery($sql);\r\n		\r\n	}\r\n}\r\n\r\n// Remove the batch ID from all records that have not been banked\r\n$sql  = "UPDATE transaction SET tra_batch_id = NULL ";\r\n$sql .= "WHERE tra_banked <> ''1'' ";\r\nnuRunQuery($sql);', '', '0', 'function nuLoadThis() {\r\n\r\n   document.getElementById(''bat_amount'').value=''0.00'';\r\n\r\n   if (document.getElementById(''recordID'').value != ''-1'')\r\n        parent.window.close();\r\n}\r\n\r\nfunction nuBeforeSave() {\r\n\r\n    if(!validateCloseDate())\r\n	return false;\r\n\r\n    if(NumRows < 1) {\r\n    	alert(''There is nothing to save'');\r\n    	return false;\r\n 	\r\n    }\r\n    		\r\n    calcbatch();\r\n    return true;\r\n}\r\n \r\n \r\nfunction calcbatch() {\r\n\r\n	//calculate the number of rows of the subform "FMOpenPayments"\r\n	var TheRows = Number(document.getElementById(''rowsFMOpenPayments'').value);\r\n	\r\n	var Amount  = document.getElementById(''bat_amount'')\r\n	var TheTot = 0;\r\n	\r\n	//go through the rows of the subform\r\n	for (i=0;i<TheRows;++i) {\r\n\r\n		//get the field names on the subform\r\n		prefix 		= ''FMOpenPayments''+(''0000''+i).substring((''0000''+i).length-4);\r\n		traBanked	= prefix + ''tra_banked'';\r\n		traTotal	= prefix + ''tra_total'';\r\n		\r\n		//add the total of each row to the previous total	\r\n		if (document.getElementById(traBanked).value == ''1'')\r\n			TheTot = TheTot+Number(document.getElementById(traTotal).value);\r\n		}\r\n\r\n	//set the total amount banked this batch\r\n        Rounded = reformatIt(TheTot,2);	\r\n	Amount.value = Rounded;\r\n	Amount.onchange();\r\n}\r\n\r\n\r\nfunction reformatIt(pValue, pDecimals){\r\n\r\n   if(arguments.length != 2){\r\n      pDecimals = 2;\r\n   }\r\n\r\n   return Math.round(pValue * Math.pow(10,pDecimals)) / Math.pow(10,pDecimals);   \r\n}', '', 'hello world', '0', '2009-05-15 15:16:56', '2009-11-25 11:57:40', 'globeadmin', '', NULL, 0, 0, 0, ''),
