('14bce873f10ed2', 'stockmovement', 'Stock Movement', NULL, '', '', '1', NULL, 'SELECT * FROM stock_movement', 'stock_movement', 'stock_movement_id', '1', '0', '1', '', '', NULL, '', '', '', '', '', NULL, '', 'SELECT ''#sm_post#'' != ''1''', 'SELECT ''#sm_post#'' != ''1''', '', '', NULL, NULL, NULL, NULL, NULL, '', '$javascript  = "function  nuLoadThis() { \\n\\n";\r\n$javascript .= "  var TheRows  = Number(document.getElementById(''rowssm_movement_items'').value); \\n";\r\n$javascript .= "  for(int=0;int<TheRows;int++){ \\n";\r\n$javascript .= "       pstrlength =  (''0000''+int).length; \\n";\r\n$javascript .= "       PRE = ''sm_movement_items''+(''0000''+int).substring(pstrlength-4); \\n";\r\n$javascript .= "       if(document.getElementById(PRE+''smi_number'').value < 0 ){ \\n";\r\n$javascript .= "        document.getElementById(PRE+''smi_number'').value = document.getElementById(PRE+''smi_number'').value * -1; \\n";\r\n$javascript .= "      } \\n";\r\n$javascript .= "   } \\n";\r\n$javascript .= " } \\n";\r\n$javascript .= createJavaScriptSharedFunctions();\r\n\r\naddJSFunction($javascript);\r\n', 'if($_POST["sm_post"] == ''1''){\r\n	$id			  = $recordID;\r\n	$type		= $_POST["sm_type"];\r\n	if($type == ''D'') {\r\n		nuRunQuery("UPDATE stock_movement_item SET smi_number = (smi_number * -1) WHERE smi_stock_id = ''$id''");\r\n	}\r\n			\r\n	//loop through every purchase item on the order screen\r\n	for ($i = 0; $i < $_POST["rowssm_movement_items"]; $i++) {\r\n		if($_POST["rowsm_movement_items".substr("0000".$i,-4)] != ''on'') {\r\n			$product      = $_POST["sm_movement_items".substr("0000".$i,-4)."smi_product_id"];\r\n			$number      = $_POST["sm_movement_items".substr("0000".$i,-4)."smi_number"];\r\n			\r\n			if($type == ''D'') {\r\n				if(function_exists("purchaseOrderUpdateLedger")) {\r\n					stockDispatch($product,$number);\r\n				} 	\r\n			} else if ($type == ''R'') {\r\n				if(function_exists("purchaseOrderUpdateLedger")) {\r\n					stockReceive($product,$number);\r\n				} \r\n			}\r\n		}		\r\n	}\r\n}', '', '0', 'function postIt() {\r\n	document.getElementById(''sm_post'').value = 1;\r\n	SaveThis();\r\n}', '', '', '0', NULL, NULL, NULL, '', NULL, 0, 0, 0, '');
INSERT INTO `zzsys_form` (`zzsys_form_id`, `sfo_name`, `sfo_title`, `sfo_type`, `sfo_access_without_login`, `sfo_close_after_save`, `sfo_refresh_after_save`, `sfo_sql_run_before_display`, `sfo_sql`, `sfo_table`, `sfo_primary_key`, `sfo_add_button`, `sfo_save_button`, `sfo_delete_button`, `sfo_clone_button`, `sfo_close_button`, `sfo_print_button`, `sfo_add_title`, `sfo_save_title`, `sfo_delete_title`, `sfo_clone_title`, `sfo_close_title`, `sfo_print_title`, `sfo_add_button_display_condition`, `sfo_save_button_display_condition`, `sfo_delete_button_display_condition`, `sfo_clone_button_display_condition`, `sfo_close_button_display_condition`, `sfo_print_button_display_condition`, `sfo_custom_file_run_before_open`, `sfo_custom_code_run_before_save`, `sfo_custom_file_run_after_save`, `sfo_custom_file_run_after_delete`, `sfo_custom_code_run_before_browse`, `sfo_custom_code_run_before_open`, `sfo_custom_code_run_after_save`, `sfo_custom_code_run_after_delete`, `sfo_report_selection`, `sfo_javascript`, `sfo_comment`, `sfo_help`, `sys_setup`, `sys_added`, `sys_changed`, `sys_user_id`, `sfo_custom_code_run_after_browse`, `sfo_redirect_form_id`, `sfo_row_height`, `sfo_row_zzsys_style_id`, `sfo_rows`, `sys_box_net`) VALUES
('14bd66888bbd15', 'FMReverse_Payments', 'Reverse Payments', NULL, '', '', '1', NULL, 'SELECT * FROM supplier', 'supplier', 'supplier_id', '0', '1', '0', '0', '', NULL, '', '', '', '', '', NULL, '', '', '', '', '', NULL, NULL, NULL, NULL, NULL, '', '$sql = "SELECT CURDATE() AS date";\r\n$result = nuRunQuery($sql);\r\n$date = db_fetch_object($result);\r\n$date = date("d-M-Y");\r\n\r\n$javascript  = "function  nuLoadThis() { \\n\\n";\r\n$javascript .= "	document.getElementById(''tra_date'').value = ''".$date."'';\\n";\r\n$javascript .= "  var TheRows  = Number(document.getElementById(''rowsFMReverse_payment_item'').value); \\n";\r\n$javascript .= "  for(int=0;int<TheRows;int++){ \\n";\r\n$javascript .= "       pstrlength =  (''0000''+int).length; \\n";\r\n$javascript .= "       PRE = ''FMReverse_payment_item''+(''0000''+int).substring(pstrlength-4); \\n";\r\n$javascript .= "       if(document.getElementById(PRE+''allocated'').value!=''0.00'' && document.getElementById(PRE+''allocated'').value!='''' ){ \\n";\r\n$javascript .= "        document.getElementById(PRE+''reverse'').disabled = true; \\n";\r\n$javascript .= "      } \\n";\r\n$javascript .= "   } \\n";\r\n$javascript .= " } \\n";\r\n$javascript .= createJavaScriptSharedFunctions();\r\n\r\naddJSFunction($javascript);\r\n', '// Get values from reverse payment screen\r\n$inv_date                     = msql_date_nq($_POST[''tra_date'']);\r\n\r\n// Loop through subform Rows from Reverse Payment Screen\r\nfor($i = 0; $i < $_POST["rowsFMReverse_payment_item"]; $i++) { \r\n	$rowno             = "FMReverse_payment_item".substr("0000".$i,-4);\r\n\r\n	// if the reverse drop down in the subform is set to yes\r\n	if (round($_POST[$rowno."reverse"])==''1''){\r\n	\r\n	    	//retrieve the relevant invoice details\r\n	    	$invoice_id            = $_POST[$rowno."transaction_id"];\r\n	    	$inv_grand_total       = round($_POST[$rowno."tra_total"],2);\r\n	    	$invPaying             = $inv_grand_total *-1;\r\n	\r\n	    	//Insert Reverse Payment header, type is B, (ie. Bounced Cheque)\r\n	    	$nextno        = FMnextnum();     \r\n	    	$reverseno     = $nextno;\r\n	    	$reverseNewid  = uniqid(''1'');\r\n	    	$sql           = "INSERT INTO transaction (transaction_id, tra_type, ";\r\n	    	$sql	      .= "tra_posted,tra_number, tra_date, tra_customer_id, ";\r\n	    	$sql          .= "tra_description, tra_cheque_no, tra_total, ";\r\n	    	$sql	      .= "tra_previously_paid, tra_outstanding) ";\r\n	    	$sql          .= "VALUES (''$reverseNewid'', ''CRIB'',''1'', ''$reverseno'', ";\r\n	    	$sql          .= "''$inv_date'',''$recordID'', ";\r\n		$sql          .= "''".$_POST[''payment_description'']."'', ";\r\n		$sql          .= "''".$_POST[''cheque_no'']."'', ";\r\n		$sql          .= "$inv_grand_total, ";\r\n		$sql          .= "$inv_grand_total, 0)";\r\n		nuRunQuery($sql);\r\n		     \r\n	        // Get the invoice that the payment was applied to\r\n	        $invoicesql  = "SELECT transaction.tra_outstanding AS tra_outstanding, ";\r\n	        $invoicesql .= "transaction.tra_previously_paid AS tra_previously_paid, ";\r\n	        $invoicesql .= "transaction.transaction_id AS transaction_id, ";\r\n	        $invoicesql .= "payment.tra_total AS tra_total, ";\r\n	        $invoicesql .= "transaction_item.tri_total AS tri_paying ";\r\n	        $invoicesql .= "FROM transaction AS payment ";\r\n	        $invoicesql .= "INNER JOIN transaction_item ON tri_transaction_id = payment.transaction_id ";\r\n	        $invoicesql .= "INNER JOIN transaction as transaction ON transaction_item.tri_paying_invoice_id = transaction.transaction_id ";\r\n	        $invoicesql .= "WHERE payment.transaction_id = ''$invoice_id''";\r\n	        $invoicers   = nuRunQuery($invoicesql);\r\n\r\n        	// If the payment has not allocated any amount to an invoice\r\n        	// (no invoice item has been created), only create the unallocated record 	        \r\n        	if(!$obj  = db_fetch_object($invoicers)){\r\n               \r\n			//Update Payment Paid In Full Flag\r\n	    		$sql  = "UPDATE transaction SET tra_paid_in_full = ''1'' ";\r\n	    		$sql .= "WHERE transaction_id = ''$invoice_id''"; \r\n	    		nuRunQuery($sql);\r\n	        	\r\n	        	// Get unallocated payments\r\n	    		$sql  = "SELECT * FROM transaction ";\r\n            		$sql .= "INNER JOIN transaction_item ON tri_transaction_id = transaction_id ";\r\n            		$sql .= "WHERE tri_paying_invoice_id = ''$invoice_id'' AND tra_type = ''U''";\r\n            		$unallocated = nuRunQuery($sql);\r\n\r\n            		if($unallocatedObj = db_fetch_object($unallocated)){\r\n\r\n	           	        //Update the invoice Previously Paid, Outstanding amount \r\n		                $inv_total               = $unallocatedObj->tra_total;\r\n		        	$unallocatedinvoiceID    = $unallocatedObj->transaction_id;\r\n		        	$sql   = "UPDATE transaction SET tra_previously_paid = $inv_total, ";\r\n		        	$sql   = $sql."tra_outstanding = 0, ";\r\n		        	$sql   = $sql."tra_paid_in_full = 1, ";\r\n		        	$sql   = $sql."tra_paid = 1 ";\r\n		        	$sql   = $sql."WHERE transaction_id = ''$unallocatedinvoiceID''";\r\n		        	nuRunQuery($sql);       \r\n		         \r\n		        	$inv_outstanding         = $obj->tra_previously_paid + $obj->tra_outstanding;\r\n	                	$inv_previously_paid     = 0;\r\n	                	$paying_inv_paid_in_full = iif($inv_outstanding == 0 , ''1'',''0'');  \r\n            		}\r\n            		\r\n        	} else {\r\n\r\n			// Rerun query \r\n			$invoicers = nuRunQuery($invoicesql);\r\n         \r\n           		while($obj  = db_fetch_object($invoicers)){\r\n        			\r\n        			// Get unallocated payments and credit notes\r\n        			$sql   = "SELECT * FROM transaction ";\r\n                		$sql  .= "INNER JOIN transaction_item ON tri_transaction_id = transaction_id ";\r\n                		$sql  .= "WHERE tri_paying_invoice_id = ''$invoice_id'' AND (tra_type = ''U'' OR tra_type = ''C'')";\r\n                		$unallocated    = nuRunQuery($sql);\r\n\r\n                		if($unallocatedObj = db_fetch_object($unallocated)){\r\n\r\n           	     			//Update the invoice Previously Paid, Outstanding amount \r\n		    			$inv_total                = $unallocatedObj->tra_total;\r\n		     			$unallocatedinvoiceID     = $unallocatedObj->transaction_id;\r\n		     			$sql   = "UPDATE transaction SET tra_previously_paid = $inv_total, ";\r\n		     			$sql   = $sql."tra_outstanding = 0, ";\r\n		     			$sql   = $sql."tra_paid_in_full = 1, ";\r\n		     			$sql   = $sql."tra_paid = 1 ";\r\n		     			$sql   = $sql."WHERE transaction_id = ''$unallocatedinvoiceID''";\r\n		     			nuRunQuery($sql);       \r\n		         \r\n		     			$inv_outstanding          = $obj->tra_previously_paid + $obj->tra_outstanding;\r\n	             			$inv_previously_paid      = 0;\r\n	             			$paying_inv_paid_in_full  = iif($inv_outstanding == 0 , ''1'',''0''); \r\n	             			 \r\n                		} else {\r\n	        	\r\n	             			$inv_paying               = $obj->tri_paying;\r\n		     			$inv_outstanding          = $obj->tra_outstanding;\r\n		     			$inv_previously_paid      = $obj->tra_previously_paid;\r\n		     			$inv_previously_paid      = $inv_previously_paid -  $inv_paying;\r\n		     			$inv_outstanding          = $inv_outstanding + $inv_paying;\r\n		     			$paying_inv_paid_in_full  = iif($inv_outstanding == 0 , ''1'',''0''); \r\n\r\n	        		} \r\n\r\n	       			$paying_invoice_id   = $obj->transaction_id;\r\n\r\n	        		//Update the invoice Previously Paid, Outstanding amount \r\n	        		$sql                 = "UPDATE transaction SET tra_previously_paid = $inv_previously_paid, ";\r\n	        		$sql                 = $sql."tra_outstanding = $inv_outstanding, ";\r\n	        		$sql                 = $sql."tra_paid_in_full = ''$paying_inv_paid_in_full'' ";\r\n	        		$sql                 = $sql."WHERE transaction_id = ''$paying_invoice_id''";\r\n	        		nuRunQuery($sql);  \r\n	        \r\n             		}//End While\r\n        \r\n        	}// End else\r\n        \r\n        	//Update Payment Paid In Full Flag\r\n		$sql   = "UPDATE transaction SET tra_paid_in_full = ''1'' WHERE transaction_id = ''$invoice_id''"; \r\n		nuRunQuery($sql);\r\n	        \r\n		//add payment item for payment header and link to this subform invoice header\r\n		$sql   = "INSERT INTO transaction_item (transaction_item_id, tri_transaction_id, tri_paying_invoice_id, ";\r\n		$sql   = $sql."tri_description, tri_total) ";\r\n		$sql   = $sql."VALUES (''".uniqid(''1'')."'', ''$reverseNewid'', ''$invoice_id'', ";\r\n		$sql   = $sql."''Reverse Payment'', $inv_grand_total*-1) ";\r\n		nuRunQuery($sql);\r\n	\r\n		//if we are using the full financial module, we need to update the General Ledger\r\n		if (function_exists("reversePaymentLedgerUpdate") ) {\r\n			reverseSupplierLedgerUpdate($recordID, $reverseno, $inv_grand_total);\r\n		}        \r\n        \r\n  	} //END if (round($_POST[$rowno."reverse"])==''1'')\r\n}\r\n', '', '0', 'function  nuBeforeSave() {\r\n\r\n   if (false == updatePaymentamount() ) {\r\n       alert(''Can not make 0.00 dollar reverse payment''); \r\n       return false;\r\n   }\r\n\r\n   if (false == validateCloseDate() ) {  \r\n       return false;        \r\n   }\r\n   \r\n   return true;\r\n}\r\n\r\nfunction updatePaymentamount(){\r\n     \r\n     var TheRows                    = Number(document.getElementById(''rowsFMReverse_payment_item'').value);\r\n     var TotalActualPayment         = Number(0);\r\n     for(int=0;int<TheRows;int++){\r\n          pstrlength =  (''0000''+int).length;\r\n          PRE = ''FMReverse_payment_item''+(''0000''+int).substring(pstrlength-4);\r\n          if(document.getElementById(PRE+''reverse'').value==''1''){\r\n              TotalActualPayment  = TotalActualPayment  + Number(document.getElementById(PRE+''tra_total'').value);\r\n          }\r\n     }\r\n     document.getElementById(''payment_amount'').value = TotalActualPayment ;\r\n\r\n     nuFormat (document.getElementById(''payment_amount''));\r\n\r\n\r\n     if(document.getElementById(''payment_amount'').value > 0) {\r\n         return true;\r\n      }\r\n      return false;\r\n}\r\n\r\n\r\n\r\nfunction validateDate() {\r\n    \r\n    var now = new Date();\r\n    var monthNames = new Array( ''Jan'',''Feb'',''Mar'',''Apr'',''May'',''Jun'',''Jul'',''Aug'',''Sep'',''Oct'',''Nov'',''Dec'');\r\n    var today = now.getDate()+''-''+(monthNames[now.getMonth()])+''-''+now.getFullYear();\r\n  \r\n    thedate = mysql_date(document.getElementById(''tra_date'').value);\r\n    var yy = parseInt(thedate.substring(0,4));\r\n    var mm = parseInt(thedate.substring(5,7),10);\r\n    var dd = parseInt(thedate.substring(8),10);\r\n    payment_date = new Date();\r\n    payment_date.setFullYear(yy,mm-1,dd);\r\n\r\n       if (document.getElementById(''tra_date'').value =='''') {\r\n\r\n        document.getElementById(''tra_date'').value = today;\r\n\r\n     }\r\n\r\n\r\n      if (document.getElementById(''tra_date'').value != today ) {\r\n         \r\n                 var answer = confirm(''Reverse Payment date is not todays date, is this correct?'' );\r\n                 if (!answer) { \r\n             	       return false;  \r\n                 }\r\n         }\r\n\r\n      return true;\r\n               \r\n}//end func\r\n\r\n', '', '', '0', NULL, NULL, NULL, '', NULL, 0, 0, 0, ''),
('14bd68b254de8f', 'FMReceiptRefund', 'Receipt Refund', NULL, '', '', '1', NULL, 'SELECT * FROM supplier', 'supplier', 'supplier_id', '1', '1', '0', '0', '0', NULL, '', '', '', '', '', NULL, '', '', '', '', '', NULL, NULL, NULL, NULL, NULL, '', '', '', '', '0', 'function calcallocated() {    \r\n     updateRefundAmount();\r\n     return validatePaying();\r\n}\r\n\r\nfunction  nuBeforeSave() {\r\n\r\n   //if (false == validateCloseDate() ) { \r\n   //    return false;        \r\n   //}\r\n  \r\n   if (false == validatePaying() ) {\r\n        return false;\r\n   }\r\n\r\n   return true;\r\n}\r\n\r\nfunction validatePaying() {\r\n\r\n  var PAGE                       = parent.document.forms[0];\r\n  var TheRows                    = Number(document.getElementById(''rowsFMReceipt_Refund_item'').value);\r\n  var TotalActualPayment         = Number(0);\r\n  var Msg1                       = ''The amount you have selected to refund is invalid'';\r\n\r\n  if ( (Number(document.getElementById(''amount'').value) *-1) <= 0.00)  {\r\n     alert(Msg1);\r\n     return false;\r\n } \r\n\r\n    for(int=0;int<TheRows;int++){\r\n          pstrlength =  (''0000''+int).length;\r\n          PRE = ''FMReceipt_Refund_item''+(''0000''+int).substring(pstrlength-4);\r\n         if ( (Number(document.getElementById(PRE+''tra_paying'').value) * -1) >  (Number(document.getElementById(PRE+''tra_outstanding'').value) * -1)   ) {\r\n              alert(Msg1);\r\n              return false;\r\n         }\r\n    }\r\n    return true;\r\n   \r\n}\r\n\r\nfunction updateRefundAmount() {\r\n\r\n    var PAGE                       = parent.document.forms[0];\r\n    var TheRows                    = Number(document.getElementById(''rowsFMReceipt_Refund_item'').value);\r\n    var TotalActualPayment  = Number(0);\r\n\r\n    for(int=0;int<TheRows;int++){\r\n          pstrlength =  (''0000''+int).length;\r\n          PRE = ''FMReceipt_Refund_item''+(''0000''+int).substring(pstrlength-4);\r\n         TotalActualPayment  = TotalActualPayment  + Number(document.getElementById(PRE+''tra_paying'').value);\r\n    }\r\n   document.getElementById(''amount'').value = TotalActualPayment ;\r\n   nuFormat(document.getElementById(''amount''));\r\n\r\n}\r\n\r\nfunction calcline(pthis){\r\n var PAGE = parent.document.forms[0];\r\n var PRE = pthis.name.substring(0,25);\r\n\r\n if (Number(document.getElementById(PRE+''tra_paying'').value) == 0.00 ) {\r\n  \r\n     document.getElementById(PRE+''tra_paying'').value = document.getElementById(PRE+''tra_outstanding'').value;\r\n\r\n } else {\r\n\r\n    document.getElementById(PRE+''tra_paying'').value = 0.00;\r\n }\r\n\r\n updateRefundAmount();\r\n nuFormat(document.getElementById(PRE+''tra_paying''));\r\n \r\n}\r\n\r\nfunction initrows(){\r\n  \r\n var PAGE         = parent.document.forms[0];\r\n var TheRows      = Number(document.getElementById(''rowsFMReceipt_Refund_item'').value);\r\n\r\n  for(int=0;int<TheRows;++int){\r\n      pstrlength =  (''0000''+int).length;\r\n      PRE = ''FMReceipt_Refund_item''+(''0000''+int).substring(pstrlength-4);\r\n      document.getElementById(PRE+''tra_paying'').value=''0.00'';\r\n      document.getElementById(PRE+''tra_previously_paid'').value=Number(document.getElementById(PRE+''tra_original_previously_paid'').value);\r\n      document.getElementById(PRE+''tra_outstanding'').value=Number(document.getElementById(PRE+''tra_original_balance'').value);\r\n      nuFormat(document.getElementById(PRE+''tra_previously_paid''));\r\n      nuFormat(document.getElementById(PRE+''tra_outstanding''));\r\n  } // end for \r\n\r\n} //end func', '', '', '0', NULL, NULL, NULL, '', NULL, 0, 0, 0, ''),
