('146490f15d1d1c', 'BarGraph', 'Standard bar graph', '// MUST GIVE THIS 3 FIELDS (X AXIS, TYPE, Y AXIS)\r\n\r\n    include_once("jpgraphpro/jpgraph.php");\r\n    include_once("jpgraphpro/jpgraph_line.php");\r\n    include_once("jpgraphpro/jpgraph_bar.php");\r\n\r\n    //main query\r\n    $t=nuRunQuery("SELECT * FROM $graphTable");\r\n    $columnnames    = TableFieldNames($t);\r\n\r\n    //set constants\r\n    $XAXIS_LABEL    = "";\r\n    $LEDGEND_LABEL  = $columnnames[1];\r\n    $YAXIS_LABEL    = $columnnames[2];\r\n    $HEADING        = "";\r\n\r\n    //init arrays\r\n    $xLabels  = array();\r\n    $xLedgend = array();\r\n    $xGroups  = array();\r\n\r\n    while($r=db_fetch_row($t)){\r\n    //--ticket labels\r\n        if(!in_array ($r[0], $xLabels)){\r\n            $xLabels[]=$r[0];\r\n        }\r\n    //-- ledgend labels\r\n        if(!in_array ($r[1], $xLedgend)){\r\n            $xLedgend[]=$r[1];\r\n        }\r\n        $xGroups[$r[1]][]=$r[2];\r\n     }\r\n\r\n    //initialse colors for each bar in graph\r\n    $colours        = setColourArray();\r\n\r\n    //--- Setup graph\r\n    $graph = new Graph(550, 300,''auto'');\r\n    $graph->SetScale("textlin");\r\n    $graph->SetColor("white");\r\n    $graph->SetMarginColor("white");\r\n    $graph->SetMargin(60,120,40,80);\r\n    $graph->title->SetFont(FF_ARIAL,FS_NORMAL,16);\r\n    $graph->title->Set($HEADING);\r\n    $graph->xaxis->title->Set($XAXIS_LABEL);\r\n    $graph->xaxis->title->SetFont(FF_ARIAL,FS_NORMAL,9);\r\n    $graph->xaxis->SetFont(FF_ARIAL,FS_NORMAL,9);\r\n    $graph->yaxis->title->Set($YAXIS_LABEL);\r\n    $graph->yaxis->title->SetFont(FF_ARIAL,FS_NORMAL,9);\r\n    $graph->yaxis->SetFont(FF_ARIAL,FS_NORMAL,9);\r\n    $graph->img->SetAntiAliasing();\r\n    $graph->legend->Pos(0.03,0.5,"right","center");\r\n    $graph->legend->SetFont(FF_FONT1,FS_BOLD);\r\n\r\n    // Setup X-scale\r\n    $graph->xaxis->SetTickLabels($xLabels);\r\n    $graph->xaxis->SetFont(FF_ARIAL,FS_NORMAL,9);\r\n    $graph->xaxis->SetLabelAngle(45);\r\n\r\n    //great $thisBarPlotList\r\n    $_index         = 0;\r\n    $_color_index   = 1;\r\n    $thisBarPlotList = array();\r\n    foreach ($xGroups as $key=>$value) {\r\n\r\n            $thisBarPlot = null;\r\n            $thisBarPlot = new BarPlot($value);\r\n            $thisBarPlot->SetFillColor($colours[$_color_index]);\r\n            $thisBarPlot->SetLegend($xLedgend[$_index]);\r\n            $thisBarPlotList[$_index] = $thisBarPlot;\r\n            $_index++;\r\n            $_color_index++;\r\n\r\n    }\r\n\r\n    // Create the grouped bar plot\r\n    $thisBarPlotGroup = null;\r\n    $thisBarPlotGroup = new GroupBarPlot($thisBarPlotList);\r\n\r\n    // Add to graph\r\n    $graph->Add($thisBarPlotGroup);\r\n    //create\r\n    $graph->Stroke();\r\n'),
('1488030d2c4851', 'PieChart', 'Standard Pie Chart', '// MUST GIVE THIS 2 FIELDS, (description, amount)\r\n\r\n	// Getting jpgraph\r\n	include_once("jpgraphpro/jpgraph.php");\r\n	include_once("jpgraphpro/jpgraph_line.php");\r\n	include_once("jpgraphpro/jpgraph_bar.php");\r\n	include_once("jpgraphpro/jpgraph_pie.php");\r\n	\r\n	// Getting temp table\r\n	$t=nuRunQuery("SELECT * FROM $graphTable");\r\n	$columnnames    = TableFieldNames($t);\r\n	\r\n	//init arrays\r\n	$xLabels  = array();\r\n	$xLedgend = array();\r\n	$xGroups  = array();\r\n	\r\n	// Looping through the temp table, filling in the arrays with graph titles and values\r\n	while($r=db_fetch_row($t)){\r\n		//--ticket labels\r\n		if(!in_array ($r[0], $xLabels)){\r\n			$xLabels[]=$r[0];   //$xLabels is  (May Jun etc.)\r\n		}\r\n		\r\n		//-- ledgend labels\r\n		if(!in_array ($r[1], $xLedgend)){\r\n			$xLedgend[]=$r[1];	//$xLedgend is (GP, Sales etc..)\r\n		}\r\n		\r\n			$xGroups[$r[1]][]=$r[2];  //$xGroups) is (Values 10,000 2,000 etc.)\r\n		\r\n	}\r\n	\r\n	// USE THESE TO TEST WHAT VALUES ARE IN THE ARRAYS\r\n	//print_r($xLedgend);//  (GP, Sales etc..)\r\n	//print_r($xLabels);  //(May Jun etc.)\r\n	//print_r($xGroups);  // (Values 10,000 2,000 etc.)\r\n\r\n	//initialse colors for each bar in graph\r\n	$colours        = setColourArray();\r\n	\r\n	//--- Setup graph (ALL THESE PROPERTIES ARE IN THE JPGRAPH REFERENCE)\r\n	$graph = new PieGraph(540,280,''auto'');\r\n	$graph->SetScale("textlin");\r\n	$graph->SetColor("#C0C0C0");\r\n	$graph->SetMarginColor("#C0C0C0");\r\n	$graph->SetMargin(60,120,40,80);\r\n	$graph->title->Set($graphName); \r\n	$graph->title->SetFont(FF_FONT1,FS_BOLD,11); \r\n	$graph->legend->Pos(0.01,0.5,"left","center");\r\n	$graph->img->SetAntiAliasing();\r\n	$graph->legend->SetFont(FF_ARIAL,FS_NORMAL,7);\r\n	\r\n	$pieplot = new PiePlot($xLedgend); \r\n	$pieplot->SetLegends($xLabels);\r\n	$pieplot->SetSliceColors($colours);\r\n	$graph->Add($pieplot); \r\n\r\n	// Creates the graph\r\n	$graph->Stroke();\r\n	\r\n'),
('14880336eb5e27', 'LineGraph', 'Standard Line Graph', '// MUST GIVE THIS 3 FIELDS (X AXIS, TYPE, Y AXIS)\r\n\r\n	// Getting jpgraph\r\n	include_once("jpgraphpro/jpgraph.php");\r\n	include_once("jpgraphpro/jpgraph_line.php");\r\n	include_once("jpgraphpro/jpgraph_bar.php");\r\n	\r\n	// Getting temp table\r\n	$t=nuRunQuery("SELECT * FROM $graphTable");\r\n	$columnnames    = TableFieldNames($t);\r\n	\r\n	//init arrays\r\n	$xLabels  = array();\r\n	$xLedgend = array();\r\n	$xGroups  = array();\r\n	\r\n	// Looping through the temp table, filling in the arrays with graph titles and values\r\n	while($r=db_fetch_row($t)){\r\n		//--ticket labels\r\n		if(!in_array ($r[0], $xLabels)){\r\n			$xLabels[]=$r[0];   //$xLabels is  (May Jun etc.)\r\n		}\r\n		\r\n		//-- ledgend labels\r\n		if(!in_array ($r[1], $xLedgend)){\r\n			$xLedgend[]=$r[1];	//$xLedgend is (GP, Sales etc..)\r\n		}\r\n		\r\n			$xGroups[$r[1]][]=$r[2];  //$xGroups) is (Values 10,000 2,000 etc.)\r\n		\r\n	}\r\n	\r\n	// USE THESE TO TEST WHAT VALUES ARE IN THE ARRAYS\r\n	//print_r($xLedgend);  (GP, Sales etc..)\r\n	//print_r($xLabels);  (May Jun etc.)\r\n	//print_r($xGroups);   (Values 10,000 2,000 etc.)\r\n\r\n	//initialse colors for each bar in graph\r\n	$colours        = setColourArray();\r\n	\r\n	//--- Setup graph (ALL THESE PROPERTIES ARE IN THE JPGRAPH REFERENCE)\r\n	$graph = new Graph(420,200,''auto'');\r\n	$graph->SetScale("textlin");\r\n	$graph->SetColor("#C0C0C0");\r\n	$graph->SetMarginColor("#C0C0C0");\r\n	$graph->SetMargin(60,120,40,80);\r\n	$graph->xaxis->title->SetFont(FF_ARIAL,FS_NORMAL,7);\r\n	$graph->xaxis->SetFont(FF_ARIAL,FS_NORMAL,7);\r\n	$graph->xaxis->SetTickLabels($xLabels);\r\n	$graph->yaxis->title->SetFont(FF_ARIAL,FS_NORMAL,7);\r\n	$graph->yaxis->SetFont(FF_ARIAL,FS_NORMAL,7);\r\n	$graph->title->Set($graphName); \r\n	$graph->title->SetFont(FF_FONT1,FS_BOLD,11); \r\n	$graph->legend->Pos(0.01,0.5,"right","center");\r\n	$graph->img->SetAntiAliasing();\r\n	$graph->img->SetTransparent("white");\r\n	$graph->legend->SetFont(FF_ARIAL,FS_NORMAL,7);\r\n\r\n	// Adds a new line for each group (GP, Sales etc.)\r\n	$count = sizeof($xGroups);\r\n	for($i=0;$i<$count;$i++){\r\n		$line[$i]= new LinePlot($xGroups[$xLedgend[$i]]);\r\n		$line[$i]->SetLegend($xLedgend[$i]);\r\n		$line[$i]->SetFillFromYMin(); \r\n		$line[$i]->SetColor($colours[$i+1]);\r\n		$graph->Add( $line[$i]);\r\n	}\r\n		\r\n	// Creates the graph\r\n	$graph->Stroke();\r\n	\r\n'),
('11121314', 'nuPieChart', 'Draws a pie chart based on given data', '// MUST GIVE THIS 2 FIELDS, (LABEL, VALUE)\r\n// groups together VALUEs that have the same LABEL, then sums together elements in each group to\r\n// produce a single value for each label. These are then fed into jpgraph to produce a pie chart.\r\n// example:\r\n// \r\n// The following tempTable is sent to this function:\r\n// +--------+--------+\r\n// | LABEL  |  VALUE |\r\n// +--------+--------+\r\n// | John   |   10   |\r\n// | Lucy   |   44   |\r\n// | Alex   |   23   |\r\n// | John   |   13   |\r\n// | Lucy   |    1   |\r\n// +--------+--------+\r\n// \r\n// then the function would group together the LABELs and VALUEs to produce:\r\n// John - 10, 13\r\n// Lucy - 44, 1\r\n// Alex - 23\r\n//\r\n// and then these would be summed together to produce:\r\n// John - 23\r\n// Lucy - 45\r\n// Alex - 23\r\n//\r\n// Jpgraph would then use this information to make a pie chart with three slices;\r\n// one for each LABEL.\r\n\r\n	// Getting jpgraph\r\n	include_once("jpgraphpro/jpgraph.php");\r\n	include_once("jpgraphpro/jpgraph_pie.php");\r\n	\r\n	// Getting temp table\r\n	$t=nuRunQuery("SELECT * FROM $graphTable");\r\n	$columnnames    = TableFieldNames($t);\r\n	\r\n	//init arrays\r\n	$xLabels  = array();\r\n	$xGroups  = array();\r\n	\r\n	// Looping through the temp table, grouping together labels and values\r\n	while($r=db_fetch_row($t)){\r\n		//--ticket labels\r\n		if(!in_array ($r[0], $xLabels)){\r\n			$xLabels[]=$r[0];   //$xLabels is  (May Jun etc.)\r\n		}\r\n				\r\n		$xGroups[$r[0]][]=$r[1];  //$xGroups) is (Values 10,000 2,000 etc.)\r\n		//tofile("$r[0] $r[1] $r[2] X");\r\n	}\r\n	\r\n	// USE THESE TO TEST WHAT VALUES ARE IN THE ARRAYS\r\n	//print_r($xLedgend);//  (GP, Sales etc..)\r\n	//print_r($xLabels);  //(May Jun etc.)\r\n	//print_r($xGroups);  // (Values 10,000 2,000 etc.)\r\n\r\n	//initialse colors for each bar in graph\r\n	$colours        = setColourArray();\r\n	\r\n	//--- Setup graph (ALL THESE PROPERTIES ARE IN THE JPGRAPH REFERENCE)\r\n	$graph = new PieGraph(540,280,''auto'');\r\n	$graph->SetScale("textlin");\r\n	$graph->SetColor("#C0C0C0");\r\n	$graph->SetMarginColor("#C0C0C0");\r\n	$graph->SetMargin(60,120,40,80);\r\n	$graph->title->Set($graphName); \r\n	$graph->title->SetFont(FF_FONT1,FS_BOLD,11); \r\n	$graph->legend->Pos(0.01,0.5,"left","center");\r\n	$graph->img->SetAntiAliasing();\r\n	$graph->legend->SetFont(FF_ARIAL,FS_NORMAL,7);\r\n	\r\n\r\n        // grab each group of values, sum them together, then add them to the pie graph''s\r\n        // setup array\r\n        $count = sizeof($xLabels);\r\n        $totals = array();\r\n	for($i=0;$i<$count;$i++){\r\n            $sumlol = array_sum($xGroups[$xLabels[$i]]);\r\n            $totals[] = $sumlol;\r\n	}\r\n\r\n        // make the pie using the setup array,\r\n        // attach the labels to it as a legend,\r\n        // and then give each slice a different colour\r\n	$pieplot = new PiePlot($totals); \r\n	$pieplot->SetLegends($xLabels);\r\n	$pieplot->SetSliceColors($colours);\r\n	$graph->Add($pieplot); \r\n\r\n	// Draw the graph\r\n	$graph->Stroke();\r\n	\r\n'),
('1234', 'nuLineGraph', 'Draws a line graph based on given data', '// MUST GIVE THIS 3 FIELDS (XVALUE, LABEL, YVALUE)\r\n// groups together the X and Y VALUEs that have the same LABEL, then plots the groups on\r\n// separate lines on the lane set of axes to produce a line graph.\r\n// example:\r\n// The following tempTable is sent to this function:\r\n// +--------+--------+--------+\r\n// | XVALUE | LABEL  | YVALUE |\r\n// +--------+--------+--------+\r\n// |    1   |    A   |     3  |\r\n// |    3   |    A   |     6  |\r\n// |    5   |    B   |     9  |\r\n// |    7   |    B   |    12  |\r\n// |    9   |    B   |    15  |\r\n// |   11   |    C   |    18  |\r\n// |   13   |    A   |    21  |\r\n// |   15   |    B   |    24  |\r\n// |   17   |    C   |    27  |\r\n// |   19   |    A   |    30  |\r\n// |   21   |    C   |    33  |\r\n// |   23   |    C   |    36  |\r\n// +--------+--------+--------+\r\n// \r\n// then the function would group together the LABELs and VALUEs to produce the following set of\r\n// coordinates:\r\n// A - (1,3), (3,6), (13,21), (19,30)\r\n// B - (5,9), (7,12), (9,15), (15,24)\r\n// C - (11,18), (17,27), (21,33), (23,36)\r\n//\r\n// Jpgraph would then use this information to make a line graph with three sets of points joined\r\n// with lines one line for each LABEL.\r\n\r\n	// Getting jpgraph\r\n	include_once("jpgraphpro/jpgraph.php");\r\n	include_once("jpgraphpro/jpgraph_line.php");\r\n	include_once("jpgraphpro/jpgraph_bar.php");\r\n	\r\n	// Getting temp table\r\n	$t=nuRunQuery("SELECT * FROM $graphTable");\r\n	$columnnames    = TableFieldNames($t);\r\n	\r\n	//init arrays\r\n	$xLabels  = array();\r\n	$xLedgend = array();\r\n	$xGroups  = array();\r\n	\r\n	// Looping through the temp table, filling in the arrays with graph titles and values\r\n	while($r=db_fetch_row($t)){\r\n		//--ticket labels\r\n		if(!in_array ($r[0], $xLabels)){\r\n			$xLabels[]=$r[0];   //$xLabels is  (May Jun etc.)\r\n		}\r\n		\r\n		//-- ledgend labels\r\n		if(!in_array ($r[1], $xLedgend)){\r\n			$xLedgend[]=$r[1];	//$xLedgend is (GP, Sales etc..)\r\n		}\r\n		\r\n			$xGroups[$r[1]][]=$r[2];  //$xGroups) is (Values 10,000 2,000 etc.)\r\n		\r\n	}\r\n	\r\n	// USE THESE TO TEST WHAT VALUES ARE IN THE ARRAYS\r\n	//print_r($xLedgend);  (GP, Sales etc..)\r\n	//print_r($xLabels);  (May Jun etc.)\r\n	//print_r($xGroups);   (Values 10,000 2,000 etc.)\r\n\r\n	//initialse colors for each bar in graph\r\n	$colours        = setColourArray();\r\n	\r\n	//--- Setup graph (ALL THESE PROPERTIES ARE IN THE JPGRAPH REFERENCE)\r\n	$graph = new Graph(420,200,''auto'');\r\n	$graph->SetScale("textlin");\r\n	$graph->SetColor("#C0C0C0");\r\n	$graph->SetMarginColor("#C0C0C0");\r\n	$graph->SetMargin(60,120,40,80);\r\n	$graph->xaxis->title->SetFont(FF_ARIAL,FS_NORMAL,7);\r\n	$graph->xaxis->SetFont(FF_ARIAL,FS_NORMAL,7);\r\n	$graph->xaxis->SetTickLabels($xLabels);\r\n	$graph->yaxis->title->SetFont(FF_ARIAL,FS_NORMAL,7);\r\n	$graph->yaxis->SetFont(FF_ARIAL,FS_NORMAL,7);\r\n	$graph->title->Set($graphName); \r\n	$graph->title->SetFont(FF_FONT1,FS_BOLD,11); \r\n	$graph->legend->Pos(0.01,0.5,"right","center");\r\n	$graph->img->SetAntiAliasing();\r\n	$graph->img->SetTransparent("white");\r\n	$graph->legend->SetFont(FF_ARIAL,FS_NORMAL,7);\r\n\r\n	// Adds a new line for each group (GP, Sales etc.)\r\n	$count = sizeof($xGroups);\r\n	for($i=0;$i<$count;$i++){\r\n		$line[$i]= new LinePlot($xGroups[$xLedgend[$i]]);\r\n		$line[$i]->SetLegend($xLedgend[$i]);\r\n		$line[$i]->SetFillFromYMin(); \r\n		$line[$i]->SetColor($colours[$i+1]);\r\n		$graph->Add( $line[$i]);\r\n	}\r\n		\r\n	// Creates the graph\r\n	$graph->Stroke();\r\n	\r\n'),
('5678', 'nuBarGraph', 'Draws a bar graph based on given data', '// MUST GIVE THIS 3 FIELDS (XLABEL, LABEL, YVALUE)\r\n// groups together LABELs and VALUEs that have the same XLABEL, these are then fed into jpgraph \r\n// to produce a bar graph\r\n// example:\r\n// \r\n// The following tempTable is sent to this function:\r\n// +--------+--------+--------+\r\n// | XLABEL | LABEL  | YVALUE |\r\n// +--------+--------+--------+\r\n// |  JAN   |    A   |     3  |\r\n// |  FEB   |    A   |     6  |\r\n// |  JAN   |    B   |     9  |\r\n// |  FEB   |    B   |    12  |\r\n// |  MAR   |    B   |    15  |\r\n// |  JAN   |    C   |    18  |\r\n// |  MAR   |    A   |    21  |\r\n// |  APR   |    B   |    24  |\r\n// |  FEB   |    C   |    27  |\r\n// |  APR   |    A   |    30  |\r\n// |  MAR   |    C   |    33  |\r\n// |  APR   |    C   |    36  |\r\n// +--------+--------+--------+\r\n// \r\n// then the function would group together the LABELs and VALUEs to produce:\r\n// JAN - A(3), B(9), C(18)\r\n// FEB - A(6), B(12), C(27)\r\n// MAR - B(15), A(21), C(33)\r\n// APR - B(24), A(30), C(36)\r\n//\r\n// Jpgraph would then use this information to make a pie chart with twelve bars divided into\r\n// four groupings; one grouping for each XLABEL, and one bar per grouping for each LABEL\r\n//\r\n// An example of the graph (not using the values above):\r\n// +........................\r\n// |             A C        \r\n// +..B..........A.C........\r\n// |  B      C   A C        \r\n// +.AB.....BC...A.C....B...\r\n// | AB     BC   ABC   AB   \r\n// +.ABC....BC...ABC...AB...\r\n// | ABC   ABC   ABC   ABC  \r\n// +-----+-----+-----+-----+\r\n// | JAN | FEB | MAR | APR |\r\n//\r\n    include_once("jpgraphpro/jpgraph.php");\r\n    include_once("jpgraphpro/jpgraph_line.php");\r\n    include_once("jpgraphpro/jpgraph_bar.php");\r\n\r\n    //main query\r\n    $t=nuRunQuery("SELECT * FROM $graphTable");\r\n    $columnnames    = TableFieldNames($t);\r\n\r\n    //set constants\r\n    $XAXIS_LABEL    = "";\r\n    $LEDGEND_LABEL  = $columnnames[1];\r\n    $YAXIS_LABEL    = $columnnames[2];\r\n    $HEADING        = "";\r\n\r\n    //init arrays\r\n    $xLabels  = array();\r\n    $xLedgend = array();\r\n    $xGroups  = array();\r\n\r\n    while($r=db_fetch_row($t)){\r\n    //--ticket labels\r\n        if(!in_array ($r[0], $xLabels)){\r\n            $xLabels[]=$r[0];\r\n        }\r\n    //-- ledgend labels\r\n        if(!in_array ($r[1], $xLedgend)){\r\n            $xLedgend[]=$r[1];\r\n        }\r\n        $xGroups[$r[1]][]=$r[2];\r\n     }\r\n\r\n    //initialse colors for each bar in graph\r\n    $colours        = setColourArray();\r\n\r\n    //--- Setup graph\r\n    $graph = new Graph(550, 300,''auto'');\r\n    $graph->SetScale("textlin");\r\n    $graph->SetColor("white");\r\n    $graph->SetMarginColor("white");\r\n    $graph->SetMargin(60,120,40,80);\r\n    $graph->title->SetFont(FF_ARIAL,FS_NORMAL,16);\r\n    $graph->title->Set($HEADING);\r\n    $graph->xaxis->title->Set($XAXIS_LABEL);\r\n    $graph->xaxis->title->SetFont(FF_ARIAL,FS_NORMAL,9);\r\n    $graph->xaxis->SetFont(FF_ARIAL,FS_NORMAL,9);\r\n    $graph->yaxis->title->Set($YAXIS_LABEL);\r\n    $graph->yaxis->title->SetFont(FF_ARIAL,FS_NORMAL,9);\r\n    $graph->yaxis->SetFont(FF_ARIAL,FS_NORMAL,9);\r\n    $graph->img->SetAntiAliasing();\r\n    $graph->legend->Pos(0.03,0.5,"right","center");\r\n    $graph->legend->SetFont(FF_FONT1,FS_BOLD);\r\n\r\n    // Setup X-scale\r\n    $graph->xaxis->SetTickLabels($xLabels);\r\n    $graph->xaxis->SetFont(FF_ARIAL,FS_NORMAL,9);\r\n    $graph->xaxis->SetLabelAngle(45);\r\n\r\n    //great $thisBarPlotList\r\n    $_index         = 0;\r\n    $_color_index   = 1;\r\n    $thisBarPlotList = array();\r\n    foreach ($xGroups as $key=>$value) {\r\n\r\n            $thisBarPlot = null;\r\n            $thisBarPlot = new BarPlot($value);\r\n            $thisBarPlot->SetFillColor($colours[$_color_index]);\r\n            $thisBarPlot->SetLegend($xLedgend[$_index]);\r\n            $thisBarPlotList[$_index] = $thisBarPlot;\r\n            $_index++;\r\n            $_color_index++;\r\n\r\n    }\r\n\r\n    // Create the grouped bar plot\r\n    $thisBarPlotGroup = null;\r\n    $thisBarPlotGroup = new GroupBarPlot($thisBarPlotList);\r\n\r\n    // Add to graph\r\n    $graph->Add($thisBarPlotGroup);\r\n    //create\r\n    $graph->Stroke();\r\n'),
('9abc', 'nuBoxPlot', 'Draws a set of box plots based on given data', '// MUST GIVE 2 FIELDS (LABEL, VALUE)\r\n// groups together VALUEs that have the same LABEL, sorts the elements of each group,\r\n// performs a simple statistical analysis to gather \r\n// min, max, lower quartile(Q1), upper quartile(Q3), and median(Q2) values\r\n// from each group, then draws a box plot for each group that displays these.\r\n// NOTE: at least two groups of data need to be provided to avoid a jpgraph error\r\n//\r\n// example:\r\n// \r\n// The following tempTable is sent to this function:\r\n// +--------+--------+\r\n// | LABEL  |  VALUE |\r\n// +--------+--------+\r\n// | John   |   10   |\r\n// | Lucy   |   44   |\r\n// | Alex   |   23   |\r\n// | John   |   13   |\r\n// | Lucy   |    1   |\r\n// +--------+--------+\r\n// \r\n// then the function would group together the LABELs and VALUEs to produce:\r\n// John - 10, 13\r\n// Lucy - 44, 1\r\n// Alex - 23\r\n//\r\n// and then these would be analysed to gather Q1, Q3, etc.. then each group would be drawn\r\n// as a boxplot using Q1, Q3, etc.\r\n//\r\n// Jpgraph would then use this information to make a pie chart with three slices;\r\n// one for each LABEL.\r\n	// Getting jpgraph\r\n	include_once("jpgraphpro/jpgraph.php");\r\n	include_once("jpgraphpro/jpgraph_line.php");\r\n	include_once("jpgraphpro/jpgraph_stock.php");\r\n	\r\n	// Getting temp table\r\n	$t=nuRunQuery("SELECT * FROM $graphTable");\r\n	$columnnames    = TableFieldNames($t);\r\n	\r\n	//init arrays\r\n	$xLabels  = array();\r\n	$xGroups  = array();\r\n	$plots = 0;\r\n\r\n	// Looping through the temp table, filling in the arrays with graph titles and values\r\n	while($r=db_fetch_row($t)){\r\n		//--ticket labels\r\n		if(!in_array ($r[0], $xLabels)){\r\n			$xLabels[]=$r[0];   //$xLabels is  (May Jun etc.)\r\n		}\r\n		\r\n		$xGroups[$r[0]][]=$r[1];  //$xGroups is (Values 10,000 2,000 etc.)\r\n		$plots ++;\r\n	}\r\n\r\n//there need to be at least 2 plots for the thing to draw, so display an error message to the \r\n//user if enough data isn''t supplied\r\nif($plots < 2){\r\n    $graph = new Graph(420,200,''auto'');\r\n	$graph->SetScale("linlin");\r\n    $text = new Text("Two or more sets of data must be provided.");\r\n    $text->SetFont(FF_ARIAL, FS_BOLD, 34);\r\n    $text->SetWordWrap(16);\r\n    $graph->Add($text);\r\n    $datay=array(0,2,1,1,1);\r\n    $graph->Add(new BoxPlot($datay));\r\n    $graph->Add(new BoxPlot($datay));\r\n}else{	\r\n	// USE THESE TO TEST WHAT VALUES ARE IN THE ARRAYS\r\n	//print_r($xLedgend);  (GP, Sales etc..)\r\n	//print_r($xLabels);  (May Jun etc.)\r\n	//print_r($xGroups);   (Values 10,000 2,000 etc.)\r\n\r\n	//initialse colors for each bar in graph\r\n	//$colours        = setColourArray();\r\n	\r\n	//--- Setup graph (ALL THESE PROPERTIES ARE IN THE JPGRAPH REFERENCE)\r\n	$graph = new Graph(420,200,''auto'');\r\n	$graph->SetScale("textlin");\r\n	$graph->SetColor("#C0C0C0");\r\n	//$graph->SetMarginColor("#C0C0C0");\r\n	//$graph->SetMargin(60,120,40,80);\r\n	//$graph->xaxis->title->SetFont(FF_ARIAL,FS_NORMAL,7);\r\n	//$graph->xaxis->SetFont(FF_ARIAL,FS_NORMAL,7);\r\n	//$graph->xaxis->SetTickLabels($xLabels);\r\n	//$graph->yaxis->title->SetFont(FF_ARIAL,FS_NORMAL,7);\r\n	//$graph->yaxis->SetFont(FF_ARIAL,FS_NORMAL,7);\r\n	$graph->title->Set($graphName); \r\n	$graph->title->SetFont(FF_FONT1,FS_BOLD,11); \r\n	//$graph->legend->Pos(0.01,0.5,"right","center");\r\n	$graph->img->SetAntiAliasing();\r\n	$graph->img->SetTransparent("white");\r\n	//$graph->legend->SetFont(FF_ARIAL,FS_NORMAL,7);\r\n\r\n	// Adds a new line for each group (GP, Sales etc.)\r\n	$count = sizeof($xLabels);\r\n    $datagoeshere = array();\r\n	for($i=0;$i<$count;$i++){\r\n		$dat = $xGroups[$xLabels[$i]];\r\n		sort($dat);\r\n		$min = $dat[0];\r\n		$Q1 = getQuartile($dat, 1);\r\n		$median = getQuartile($dat, 2);\r\n		$Q3 = getQuartile($dat, 3);\r\n		$max = $dat[sizeof($dat)-1];\r\n    $datagoeshere[] = $Q1;\r\n    $datagoeshere[] = $Q3;\r\n    $datagoeshere[] = $min;\r\n    $datagoeshere[] = $max;\r\n    $datagoeshere[] = $median;\r\n		//$box[$i] = new BoxPlot(array($Q1,$Q3,$min,$max,$median));\r\n		//$box[$i]->SetLegend($xLabels[$i]);\r\n		//$box[$i]->SetColor($colours[$i+1]);\r\n    //tofile("$Q1 $Q3 $min $max $median");\r\n	}\r\n	$plot = new BoxPlot($datagoeshere);\r\n        //$plot->SetWidth(9);\r\n	$graph->Add($plot);\r\n}\r\n	// Creates the graph\r\n	$graph->Stroke();\r\n	\r\n\r\n\r\n//returns the xth quartile of a sorted array of numbers (0<x<4).\r\nfunction getQuartile($sorted, $x){\r\n	$x = $x%4;\r\n	$n = sizeof($sorted);\r\n	$raw = (($x/4.0)*($n-1) + 1);\r\n	$ind = floor($raw);\r\n	$dif = $raw - $ind;\r\n	$first = $sorted[$ind];\r\n	$second = $sorted[$ind+1];\r\n	return $first + ($dif*($second - $first));\r\n}'),
('def10', 'nuScatterPlot', 'Draws a scatter plot based on data provided', '// MUST GIVE THIS 2 FIELDS (XVALUES, YVALUES)\r\n// jpgraph plots the provided points to a scatterplot.\r\n// The points have coordinates (XVALUE,YVALUE).\r\n//\r\n// example:\r\n// \r\n// The following tempTable is sent to this function:\r\n// +--------+--------+\r\n// | XVALUE | YVALUE |\r\n// +--------+--------+\r\n// |    1   |   10   |\r\n// |    2   |   44   |\r\n// |    3   |   23   |\r\n// |    4   |   13   |\r\n// |    5   |    1   |\r\n// +--------+--------+\r\n//\r\n// Jpgraph would then use this information to draw a set of points:\\\r\n// (1,10), (2,44), (3,23), (4,13), (5,1)\r\n//\r\n\r\n	// Getting jpgraph\r\n	include_once("jpgraphpro/jpgraph.php");\r\n	include_once("jpgraphpro/jpgraph_line.php");\r\n	include_once("jpgraphpro/jpgraph_scatter.php");\r\n	\r\n	// Getting temp table\r\n	$t=nuRunQuery("SELECT * FROM $graphTable");\r\n	$columnnames    = TableFieldNames($t);\r\n	\r\n	//init arrays\r\n	$xValues  = array();\r\n	$yValues  = array();\r\n	\r\n	// Looping through the temp table, filling in the arrays with graph titles and values\r\n	while($r=db_fetch_row($t)){		\r\n			$xValues[]=$r[0];  //$xGroups) is (Values 10,000 2,000 etc.)\r\n			$yValues[]=$r[1];\r\n		\r\n	}\r\n	\r\n	// USE THESE TO TEST WHAT VALUES ARE IN THE ARRAYS\r\n	//print_r($xLedgend);  (GP, Sales etc..)\r\n	//print_r($xLabels);  (May Jun etc.)\r\n	//print_r($xGroups);   (Values 10,000 2,000 etc.)\r\n\r\n	//initialse colors for each bar in graph\r\n	$colours        = setColourArray();\r\n	\r\n	//--- Setup graph (ALL THESE PROPERTIES ARE IN THE JPGRAPH REFERENCE)\r\n	$graph = new Graph(420,200,''auto'');\r\n	$graph->SetScale("linlin");\r\n	$graph->SetColor("#C0C0C0");\r\n	$graph->SetMarginColor("#C0C0C0");\r\n	$graph->SetMargin(60,120,40,80);\r\n	$graph->xaxis->title->SetFont(FF_ARIAL,FS_NORMAL,7);\r\n	$graph->xaxis->SetFont(FF_ARIAL,FS_NORMAL,7);\r\n	$graph->xaxis->SetTickLabels($xLabels);\r\n	$graph->yaxis->title->SetFont(FF_ARIAL,FS_NORMAL,7);\r\n	$graph->yaxis->SetFont(FF_ARIAL,FS_NORMAL,7);\r\n	$graph->title->Set($graphName); \r\n	$graph->title->SetFont(FF_FONT1,FS_BOLD,11); \r\n	$graph->legend->Pos(0.01,0.5,"right","center");\r\n	$graph->img->SetAntiAliasing();\r\n	$graph->img->SetTransparent("white");\r\n	$graph->legend->SetFont(FF_ARIAL,FS_NORMAL,7);\r\n\r\n	// Adds a new line for each group (GP, Sales etc.)\r\n	$scatter = new ScatterPlot($xValues, $yValues);\r\n	$graph->Add($scatter);\r\n		\r\n	// Creates the graph\r\n	$graph->Stroke();\r\n	\r\n');
