('149c3144dbaf53', 'Financials', 'function financialSettings() {\r\n	$fms = nuRunQuery("SELECT * FROM financial_module_setting");\r\n	return db_fetch_object($fms);\r\n}\r\n\r\nfunction FMnextnum($tablename = "next_number",$key1 = "next_number_id",$key2 = "nxn_key") {\r\n		\r\n	$newid     = uniqid();\r\n	nuRunQuery("INSERT INTO $tablename SET $key2 = ''$newid''");\r\n	\r\n	$rs 		= nuRunQuery("SELECT $key1 FROM $tablename WHERE $key2 = ''$newid''");\r\n	$obj            = db_fetch_object($rs);\r\n	$nextno         = $obj->next_number_id;\r\n	\r\n	return $nextno;\r\n}\r\n\r\nfunction createJavaScriptSharedFunctions() {\r\n    \r\n	$javascript = ""; \r\n\r\n	$javascript .= "function validateCloseDate(){ \\n\\n";\r\n	$javascript .= "thedate             = mysql_date(document.getElementById(''tra_date'').value); \\n";\r\n	$javascript .= "yy                  = parseInt(thedate.substring(0,4)); \\n";\r\n	$javascript .= "mm                  = parseInt(thedate.substring(5,7),10); \\n";\r\n	$javascript .= "dd                  = parseInt(thedate.substring(8),10); \\n";\r\n	$javascript .= "newDate             = new Date(); \\n";\r\n	$javascript .= "newDate.setFullYear(yy,mm-1,dd); \\n\\n";\r\n	$javascript .= "duedate             = (document.getElementById(''fms_transaction_due_date'').value); \\n";\r\n	$javascript .= "yy                  = parseInt(duedate.substring(0,4)); \\n";\r\n	$javascript .= "mm                  = parseInt(duedate.substring(5,7),10); \\n";\r\n	$javascript .= "dd                  = parseInt(duedate.substring(8),10); \\n";\r\n	$javascript .= "newDueDate          = new Date(); \\n";\r\n	$javascript .= "newDueDate.setFullYear(yy,mm-1,dd); \\n\\n";\r\n	$javascript .= "if (newDate <= newDueDate){ \\n";\r\n	$javascript .= "alert(''Your transaction date needs to be greater than the following date: '' + duedate); \\n";\r\n	$javascript .= "return false; \\n";\r\n	$javascript .= "} \\n\\n";\r\n	$javascript .= "return true; \\n";\r\n	$javascript .= "} \\n\\n";\r\n	\r\n	$javascript .= "function mysql_date(pdate){ \\n";\r\n	$javascript .= "if(pdate==''''){return '''';} \\n";\r\n	$javascript .= "if(pdate.length==10){ \\n";\r\n	$javascript .= "	var rdate = \\"0\\"+pdate;  \\n";\r\n	$javascript .= "}else{  \\n";\r\n	$javascript .= "var rdate = pdate;  \\n";\r\n	$javascript .= "}  \\n";\r\n	$javascript .= "var newmth=''01'';  \\n";\r\n	$javascript .= "mth = rdate.substring(3,6);  \\n";\r\n	$javascript .= "if(mth==''Jan''){newmth=''01'';}  \\n";\r\n	$javascript .= "if(mth==''Feb''){newmth=''02'';}  \\n";\r\n	$javascript .= "if(mth==''Mar''){newmth=''03'';}  \\n";\r\n	$javascript .= "if(mth==''Apr''){newmth=''04'';}  \\n";\r\n	$javascript .= "if(mth==''May''){newmth=''05'';}  \\n";\r\n	$javascript .= "if(mth==''Jun''){newmth=''06'';}  \\n";\r\n	$javascript .= "if(mth==''Jul''){newmth=''07'';}  \\n";\r\n	$javascript .= "if(mth==''Aug''){newmth=''08'';}  \\n";\r\n	$javascript .= "if(mth==''Sep''){newmth=''09'';}  \\n";\r\n	$javascript .= "if(mth==''Oct''){newmth=''10'';}  \\n";\r\n	$javascript .= "if(mth==''Nov''){newmth=''11'';}  \\n";\r\n	$javascript .= "if(mth==''Dec''){newmth=''12'';}  \\n";\r\n	$javascript .= "return rdate.substring(7)+\\"-\\"+newmth+\\"-\\"+rdate.substring(0,2);  \\n";\r\n	$javascript .= "}  \\n";\r\n\r\n	return $javascript;\r\n}\r\n'),
