ase_order_item".substr("0000".$i,-4)."tri_units"];\r\n			$iniPreUnits       = $_POST["FMpurchase_order_item".substr("0000".$i,-4)."tri_pre_units"];\r\n			$units             = $iniUnits-$iniPreUnits;\r\n			$iniAmount         = $_POST["FMpurchase_order_item".substr("0000".$i,-4)."tri_amount"];\r\n			$iniTotal          = $_POST["FMpurchase_order_item".substr("0000".$i,-4)."tri_total"];\r\n			if($invType == ''CRIC'') {\r\n				$iniAmount = $iniAmount * -1;\r\n			}\r\n	\r\n			// The exGST inventory section goes to an Asset GL            	\r\n			$product    = nuRunQuery("SELECT * FROM product INNER JOIN product_group ON pro_product_group_id = product_group_id WHERE product_id = ''$iniproductID''");\r\n			$productObj = db_fetch_object($product);\r\n			$prgAssetLedgerID = $productObj->prg_asset_ledger_id;\r\n				\r\n			//insert into the ledger log\r\n			insertledgerlogitem($ledgerlogID,$prgAssetLedgerID,$iniAmount);   \r\n	\r\n			$inventoryTotal = $inventoryTotal +$iniAmount;\r\n		}\r\n	}\r\n\r\n	//loop through every general expense item on the expense screen\r\n	for($i = 0; $i <= $_POST["rowspurchase_expense_item"]; $i++) { //** reading Rows from update general expense subform\r\n\r\n		//if the order item exist we will get its details and add to the requested ledger log\r\n		if($_POST["deleteboxpurchase_expense_item"] == 1) {\r\n\r\n			//get transaction item details\r\n			$iniledgerID      = $_POST["purchase_expense_item".substr("0000".$i,-4)."tri_ledger_id"];\r\n			$iniAmount         = $_POST["purchase_expense_item".substr("0000".$i,-4)."tri_amount"];\r\n			\r\n			if($invType == ''CRIC'') {\r\n				$iniAmount = $iniAmount * -1;\r\n			}\r\n\r\n			if($_POST["purchase_expense_item".substr("0000".$i,-4)."tri_ledger_id"] == null && $_POST["purchase_expense_item".substr("0000".$i,-4)."tri_units"] > 0) {\r\n				$iniledgerID = $expense_ledger_id;\r\n			}\r\n\r\n			if($iniledgerID != null) {\r\n				insertledgerlogitem($ledgerlogID,$iniledgerID,$iniAmount);\r\n			}\r\n			$expenseTotal = $expenseTotal + $iniAmount;\r\n		}\r\n	} \r\n\r\n	// The exGST remainder goes into the expense account   \r\n	insertledgerlogitem($ledgerlogID,$expense_ledger_id,($PaymentAmount-$expenseTotal)); \r\n\r\n	// The incGST goes into the liability account     \r\n	insertledgerlogitem($ledgerlogID,$sugLiabilityLedgerID,-($invTotal));\r\n	\r\n	// The gst amount goes into the GST GL   \r\n	insertledgerlogitem($ledgerlogID,$fmsGSTLedgerID,$invTax); \r\n}\r\n\r\nfunction receiptTypeLedgerUpdate($recordID) {\r\n\r\n	$sql  = "UPDATE transaction ";\r\n	$sql .= "SET tra_expense_ledger_id = (SELECT ret_general_ledger_id FROM receipt_type WHERE receipt_type_id = tra_receipt_type ) "; \r\n	$sql .= "WHERE tra_receipt_id = ''$recordID'' "; \r\n	nuRunQuery($sql);\r\n	\r\n}\r\n\r\nfunction receiptDetailUpdateLedger($recordID) {\r\n       \r\n        receiptTypeLedgerUpdate($recordID); \r\n         \r\n	//financial settings data \r\n	$fmsObj                		= financialSettings();\r\n	$fms_gst_ledger_id        	= $fmsObj->fms_gst_ledger_id;\r\n	$fms_cash_at_bank_ledger_id 	= $fmsObj->fms_cash_at_bank_ledger_id;\r\n\r\n	//get receipt record\r\n	$receiptRS  = nuRunQuery("SELECT * FROM receipt WHERE receipt_id = ''$recordID''");\r\n	$receiptObj = db_fetch_object($receiptRS);\r\n\r\n	$rec_batch_number		= $receiptObj->rec_batch_number; \r\n	$rec_date			= $receiptObj->tra_date;\r\n	$rec_batch_total		= $receiptObj->rec_batch_total;\r\n	$lld_description        	= "Reciept ".$rec_batch_number;  \r\n\r\n	$GST                   		= 0; \r\n  \r\n	// Create ledgerlog header \r\n	$ledger_log_id = insertledgerlogheader($rec_date, $lld_description, ''Receipt'');\r\n\r\n	//get receipt details\r\n	$receiptDetailsRS = nuRunQuery("SELECT * FROM transaction WHERE tra_receipt_id = ''$recordID''");\r\n\r\n	//update legder for each \r\n	while ($receiptDetailObj = db_fetch_object($receiptDetailsRS)) {\r\n\r\n\r\n		// The exGST total goes into the ledger account link with expense type\r\n		insertledgerlogitem(