reversePaymentLedgerUpdate($recordID, $reverseno, $inv_grand_total) {\r\n\r\n	// Get values from reverse payment screen\r\n	$inv_date                     = msql_date_nq($_POST[''tra_date'']);\r\n\r\n	// Get FM Settings\r\n	$fmsObj                       = financialSettings();\r\n	$fms_cash_at_bank_ledger_id   = $fmsObj->fms_cash_at_bank_ledger_id;  \r\n	\r\n	// Get customer account receievable ledger.\r\n	$sql   		= "SELECT * FROM customer_group ";\r\n	$sql	       .= "INNER JOIN customer ON cus_customer_group_id = customer_group_id ";\r\n	$sql           .= "WHERE customer_id =''$recordID''";\r\n	$led      	= nuRunQuery($sql);\r\n	$ledObj     	= db_fetch_object($led);\r\n	$ledgerID   	= $ledObj->csg_ledger_id;\r\n\r\n	// Make the ledger header\r\n	$cusCodeTemp           = nuRunQuery("SELECT * FROM customer WHERE customer_id = ''$recordID''"); \r\n	$cusCodeObj            = db_fetch_object($cusCodeTemp);\r\n	$cus_code              = $cusCodeObj->cus_code; \r\n	$lldDescription        = "Reverse Receipt ".$reverseno."\\n Customer ".$cus_code;\r\n	$ledgerlogID           = insertledgerlogheader($inv_date, $lldDescription, ''Reverse Payment'');\r\n	\r\n	// Update ledger \r\n	// The incGST total goes into the ASSET \r\n	insertledgerlogitem($ledgerlogID,$fms_cash_at_bank_ledger_id,-$inv_grand_total);  \r\n	   \r\n	// The incGST total goes into ASSET -> Account Recievable GL	\r\n	insertledgerlogitem($ledgerlogID,$ledgerID,$inv_grand_total);\r\n\r\n}\r\n\r\nfunction paymentLedgerUpdate($recordID) {\r\n\r\n	// get variables from the screen\r\n	$payment_description      = $_POST[''payment_description''];\r\n	$payment_method           = $_POST[''payment_method''];\r\n	$payChequeNo              = $_POST[''cheque_no''];\r\n	$unallocated              = $_POST[''unallocated''];\r\n	$payment_discount         = $_POST[''payment_discount''];\r\n	$DiscountFlag             = $_POST[''discount''];\r\n	$PaymentDate        	  = msql_date_nq($_POST[''tra_date'']);\r\n	\r\n	// make payment amount 0.00 if it is null for some reason (should never be null)\r\n	if(empty($_POST[''payment_amount'']) || null == $_POST[''payment_amount'']) {\r\n		$payment_amount    = 0.00;\r\n	} else {\r\n		$payment_amount    = $_POST[''payment_amount''];\r\n	}\r\n	\r\n	// get customer code for ledger header description\r\n	$cusCodeTemp    	= nuRunQuery("SELECT * FROM customer WHERE customer_id = ''$recordID''"); \r\n	$cusCodeObj     	= db_fetch_object($cusCodeTemp);\r\n	$cus_code        	= $cusCodeObj->cus_code; \r\n	$lldDescription  	= "Payment ".$paymentno."\\n Customer ".$cus_code;  \r\n\r\n	// make ledger header\r\n	$ledgerlogID             = insertledgerlogheader($PaymentDate, $lldDescription, ''Payment''); \r\n	\r\n	// The incGST total goes into ASSET -> Account Recievable GL\r\n	$sql  = "SELECT * FROM customer_group ";\r\n	$sql .= "INNER JOIN customer ON cus_customer_group_id = customer_group_id ";\r\n	$sql .= "WHERE customer_id =''$recordID''";\r\n	$led  = nuRunQuery($sql);\r\n	$ledObj           = db_fetch_object($led);\r\n	$ledgerID         = $ledObj->csg_ledger_id;\r\n	$discountledgerID = $ledObj->csg_discount_ledger_id;\r\n	\r\n	// get payment method GL\r\n	$pym         = nuRunQuery("SELECT * FROM payment_method WHERE pym_code = ''$payment_method'' ");\r\n	$pymObj      = db_fetch_object($pym);\r\n	$pymLedgerID = $pymObj->pym_ledger_id;\r\n	\r\n	// make ledger items\r\n	if($DiscountFlag != ''Discount''){\r\n		// The incGST total goes into the ASSET \r\n		insertledgerlogitem($ledgerlogID,$pymLedgerID,$payment_amount);  \r\n		// The receivable Account	 \r\n		insertledgerlogitem($ledgerlogID,$ledgerID,-$payment_amount);  \r\n	}\r\n	\r\n	// get fm settings\r\n	$fmsObj                      = financialSettings();\r\n	$fmsGSTCollectedLedgerID     = $fmsObj->fms_gst_collected_ledger_id;  \r\n	$fmsCashAtBankLedgerID       = $fmsObj->fms_cash_at_bank_ledger_id;\r\n	$fmsGST                      = $fmsObj->fms_gst;\r\n	$amount                      = $payment_discount/Round((1+$fmsGST/100),2);\r\n	\r\n	// make discount ledger items\r\n	if  ($DiscountFlag == ''Discount''){\r\n		// Account recievable goes down   	\r\n		insertledgerlogitem($ledgerlogID,$ledgerID,-$payment_discount);\r\n		\r\n		// Discount given account goes up    \r\n		insertledgerlogitem($ledgerlogID,$discountledgerID,$amount);\r\n		\r\n		// GST collected account goes uo\r\n		insertledgerlogitem($ledgerlogID,$fmsGSTCollectedLedgerID,($payment_discount-$amount));\r\n	}\r\n\r\n}\r\n\r\nfunction invoiceLedgerUpdate($recordID) {\r\n\r\n	//getting values from post/form\r\n	$inv_number                      = $_POST["tra_number"];\r\n    	$inv_type                        = $_POST["tra_type"];\r\n    	$inv_customer_id                 = $_POST["tra_customer_id"];\r\n    	$inv_date                        = msql_date_nq($_POST[''tra_date'']);\r\n	$total_amount                    = $_POST["tra_amount"];\r\n    	$total_tax                       = $_POST["tra_tax"];\r\n    	$grand_total                     = $_POST["tra_total"];\r\n	$cus_discount 	           	 = $_POST["tra_discount"]; \r\n      \r\n    	//get financial settings\r\n    	$fmsObj                          = financialSettings();\r\n    	$fms_gst_ledger_id               = $fmsObj->fms_gst_ledger_id;\r\n    	$fms_gst_collected_ledger_id     = $fmsObj->fms_gst_collected_ledger_id;\r\n    \r\n    	//get customer\r\n	$sql  = "SELECT * FROM customer_group ";\r\n        $sql .= "INNER JOIN customer ON cus_customer_group_id = customer_group_ID ";\r\n        $sql .= "WHERE customer_id = ''$inv_customer_id'' ";\r\n        $cus_gr    = nuRunQuery($sql);\r\n       	$cus_grObj = db_fetch_object($cus_gr);\r\n       	$ledger_id = $cus_grObj->csg_ledger_id;\r\n	$cus_code  = $cus_grObj->cus_code;\r\n    \r\n 	//create id for ledger record \r\n    	$ledger_log_id = uniqid();\r\n    	\r\n	//determine transaction type and whether values will be positive for negative\r\n    	if ($inv_type == ''C'') {\r\n		$t    = -1;\r\n		$type = "Credit"; \r\n    	} else {\r\n    		$t    = 1;\r\n	    	$type = "Invoice";\r\n    	}	\r\n	\r\n	//create ledger description\r\n    	$lld_description = $type." ".$inv_number."\\n Customer ".$cus_code;     \r\n	\r\n	//create ledger header\r\n	$ledger_log_id = insertledgerlogheader($inv_date, $lld_description, $type);\r\n\r\n	//ledger transactions for all product groups\r\n	$sql  = "SELECT product_id, product_group_id, prg_income_ledger_id, SUM( tri_amount ) AS Amount, SUM( tri_tax_amount ) AS Tax, SUM( tri_total ) AS Total ";\r\n	$sql .= "FROM transaction_item ";\r\n	$sql .= "INNER JOIN product ON product_id = tri_product_id ";\r\n	$sql .= "INNER JOIN product_group ON pro_product_group_id = product_group_id ";\r\n	$sql .= "WHERE tri_transaction_id = ''$recordID'' ";\r\n	$sql .= "GROUP BY product_group_id ";\r\n	$trans = nuRunQuery($sql);\r\n\r\n	while ($transObj = db_fetch_object($trans)) {\r\n		$prg_income_ledger_id = $transObj->prg_income_ledger_id;\r\n		$ini_amount 	      = $transObj->Amount;\r\n		$group_id	      = $transObj->product_group_id;		\r\n		insertledgerlogitem($ledger_log_id,$prg_income_ledger_id,-($ini_amount*$t));\r\n	}\r\n\r\n	// The incGST total goes into the customer recievable account\r\n        insertledgerlogitem($ledger_log_id,$ledger_id,($grand_total*$t));  \r\n    \r\n	// The GST amount goes into GST collect liability GL\r\n        insertledgerlogitem($ledger_log_id,$fms_gst_collected_ledger_id,-($total_tax*$t));\r\n}\r\n\r\nfunction insertledgerlogheader($lld_date,$lld_description,$type){\r\n	   \r\n	$tt = TT();\r\n	$lld_batch_id = FMnextnum();\r\n\r\n	$ledger_log_id = uniqid(''1'');\r\n	$sql  = "INSERT INTO ledger_log(ledger_log_id, lld_batch_id, lld_date,lld_description,lld_posted,lld_type) VALUES ";\r\n	$sql .= "(''$ledger_log_id'',''$lld_batch_id'',''$lld_date'',''$lld_description'',1,''$type'')";\r\n	nuRunQuery($sql);\r\n	return $ledger_log_id;\r\n}\r\n\r\nfunction insertledgerlogitem($lli_ledger_log_id,$lli_ledger_id,$lli_amount){\r\n	\r\n	$ledger_log_item_id = uniqid(''1'');\r\n	$sql  = "INSERT INTO ledger_log_item(ledger_log_item_id,lli_ledger_id, lli_ledger_log_id,lli_amount) VALUES ";\r\n	$sql .= "(''$ledger_log_item_id'',''$lli_ledger_id'',''$lli_ledger_log_id'',0$lli_amount)";\r\n	nuRunQuery($sql);\r\n	\r\n}\r\n'),
