('14bce94bf95f87', 'Stock', 'function stockDispatch($product,$number) {\r\n	$qry = nuRunQuery("SELECT pro_stock_on_hand AS stock FROM product WHERE product_id = ''$product''");\r\n	$obj = db_fetch_object($qry);\r\n	$num = $obj->stock - $number;\r\n	nuRunQuery("UPDATE product SET pro_stock_on_hand = $num WHERE product_id = ''$product''");\r\n}\r\n\r\nfunction stockReceive($product,$number) {\r\n	$qry = nuRunQuery("SELECT pro_stock_on_hand AS stock FROM product WHERE product_id = ''$product''");\r\n	$obj = db_fetch_object($qry);\r\n	$num = $obj->stock + $number;\r\n	nuRunQuery("UPDATE product SET pro_stock_on_hand = $num WHERE product_id = ''$product''");\r\n}'),
('14e829b57e0091', 'Build Statement', '#   Build Statement Function\r\n#\r\n#   CORE FIELDS ON TABLE\r\n#       transaction_id\r\n#       tra_customer_id\r\n#       tra_date\r\n#       tra_number\r\n#       tra_total\r\n#       tra_type\r\n#       transamount\r\n#       balanceamount\r\n#       finalamount\r\n#       per1amount\r\n#       per2amount\r\n#       per3amount\r\n#       per4amount\r\n#       theOrder VARCHAR(1)\r\n#       isBlankLine\r\n#       cus_name (THIS IS WHAT THE DEFAULT STATEMENT GROUPS BY)\r\n#       dateCriteriaLabel\r\n#\r\n#   Parameters:\r\n#       Name of the temp table this generates\r\n#       Table name with first column of customer_id (used to only build statement for certain customers)\r\n#       To Date in mysql format (list of transactions ends here, also all period totals are based off this date)\r\n#       Line Count Array (example below)\r\n#       lineCounts[''headerAndFooter'']  = 21;\r\n#       lineCounts[''header'']           = 28;\r\n#       lineCounts[''noHeaderOrFooter''] = 41;\r\n#       lineCounts[''Footer'']           = 35;\r\nfunction buildStatement(\r\n    $dataTableName, \r\n    $customerTable, \r\n    $todate,\r\n    $lineCounts = array(''headerAndFooter''=>21,''header''=>28,''noHeaderOrFooter''=>41,''Footer''=>35)\r\n) {\r\n    \r\n    // set $fromdate to be the first of the month of $todate\r\n    $fromdate = date(''Y-m-'',strtotime($todate)).''01'';\r\n    \r\n    //Fields to put in blank lines\r\n    $fieldsToPutInBlankLines[]  = ''per1amount'';\r\n    $fieldsToPutInBlankLines[]  = ''per2amount'';\r\n    $fieldsToPutInBlankLines[]  = ''per3amount'';\r\n    $fieldsToPutInBlankLines[]  = ''per4amount'';\r\n    $fieldsToPutInBlankLines[]  = ''finalamount'';\r\n    \r\n	//Build datatable\r\n    //Check here for datatable fields\r\n	buildDataTable($dataTableName, $fromdate, $todate);\r\n\r\n	//Populate Opening Balance\r\n	populateOpeningBalance($dataTableName, $customerTable, $fromdate, $todate);\r\n\r\n	//Populate Transactions\r\n	populateTransactions($dataTableName, $customerTable, $fromdate, $todate);\r\n\r\n	//Calculate Running Balance (this also adds the final balance to finalamount, our Amount Due field)\r\n	calculateRunningBalance($dataTableName);\r\n    \r\n    //Update Current/30/60/90 Totals\r\n    updatePeriodTotals($dataTableName, $todate, $customerTable);\r\n    \r\n    //Pad Lines\r\n    insertPadLines($dataTableName, $lineCounts, $fieldsToPutInBlankLines);\r\n    \r\n}\r\n\r\nfunction buildDataTable($tableName, $fromdate, $todate) {\r\n    \r\n    // define fields needed in the statement report here\r\n    nuRunQuery("\r\n        CREATE TABLE $tableName (\r\n            transaction_id VARCHAR(15) NULL,\r\n            tra_customer_id VARCHAR(15) NULL,\r\n            tra_date DATE NULL,\r\n            tra_number VARCHAR(11) NULL,\r\n            tra_total DOUBLE(11,2) NULL,\r\n            tra_type VARCHAR(15) NULL,\r\n            transamount DOUBLE(11,2) NULL,\r\n            balanceamount DOUBLE(11,2) NULL,\r\n            finalamount DOUBLE(11,2) NULL,\r\n            per1amount DOUBLE(11,2) NULL,\r\n            per2amount DOUBLE(11,2) NULL,\r\n            per3amount DOUBLE(11,2) NULL,\r\n            per4amount DOUBLE(11,2) NULL,\r\n            theOrder VARCHAR(1) NULL DEFAULT ''z'',\r\n            isBlankLine INT(1) NOT NULL DEFAULT 0,\r\n            cus_name VARCHAR(255) NULL,\r\n            dateCriteriaLabel VARCHAR(100) NULL DEFAULT ''Between $fromdate And $todate''\r\n        )\r\n    ");\r\n    nuRunQuery("ALTER TABLE $tableName ADD INDEX(transaction_id), ADD INDEX(tra_customer_id) ");\r\n    \r\n}\r\n\r\nfunction populateOpeningBalance($tableName, $customerTable, $fromdate, $todate) {\r\n    \r\n	//Setup Variables\r\n    $fmsOBJ        =   financialSettings();\r\n    \r\n    nuRunQuery("\r\n        INSERT INTO $tableName \r\n        SELECT ''openingbalance'' AS transaction_id, tra_customer_id, ''$fromdate'' AS tra_date, NULL AS tra_number, \r\n        NULL AS tra_total, ''Opening Balance'' AS tra_type, NULL AS transamount,\r\n        COALESCE(SUM(IF(tra_type IN(''P'',''D'',''R''),IF(tra_date < ''$fromdate'',tra_total,0)*-1,IF(tra_type IN(''I'',''B'',''C''),IF(tra_date < ''$fromdate'',tra_total,0),''0.00''))),''0.00'') AS balanceamount,\r\n        COALESCE(SUM(IF(tra_type IN(''P'',''D'',''R''),tra_total*-1,IF(tra_type IN(''I'',''B'',''C''),tra_total,''0.00''))),''0.00'') AS finalamount,\r\n        NULL AS per1amount, NULL AS per2amount, NULL AS per3amount, NULL AS per4amount, 0 AS theOrder, NULL AS isBlankLine,\r\n        customer.cus_name, ''Between $fromdate And $todate'' AS dateCriteriaLabel\r\n        FROM transaction\r\n        INNER JOIN $customerTable ON $customerTable.customer_id = tra_customer_id\r\n        INNER JOIN customer ON customer.customer_id = tra_customer_id\r\n        WHERE (transaction.tra_type IN (''P'',''R'',''B'',''D'') OR (transaction.tra_type IN (''C'',''I'') AND transaction.tra_posted = 1))\r\n        GROUP BY tra_customer_id\r\n        \r\n    ");\r\n    \r\n}\r\n\r\nfunction populateTransactions($tableName, $customerTable, $fromdate, $todate) {\r\n    \r\n	//Setup Variables\r\n    $fmsOBJ        =   financialSettings();\r\n    \r\n    nuRunQuery("\r\n        INSERT INTO $tableName \r\n        SELECT transaction.transaction_id, transaction.tra_customer_id, transaction.tra_date, transaction.tra_number, transaction.tra_total, \r\n        (Case WHEN transaction.tra_type =''P'' THEN ''Payment'' WHEN transaction.tra_type =''C'' THEN ''Credit'' WHEN transaction.tra_type = ''R'' THEN ''Refund'' WHEN transaction.tra_type = ''D'' THEN ''Discount'' WHEN transaction.tra_type = ''I'' THEN ''Invoice'' WHEN transaction.tra_type = ''B'' THEN ''Reverse Payment'' END) AS tra_type, \r\n        COALESCE(IF(transaction.tra_type IN(''D'',''P'',''R''), transaction.tra_total*-1,transaction.tra_total),''0.00'') AS transamount, \r\n        NULL AS balanceamount, NULL AS finalamount, NULL AS per1amount, NULL AS per2amount, NULL AS per3amount, NULL AS per4amount, 0 AS theOrder, 0 AS isBlankLine,\r\n        customer.cus_name, ''Between $fromdate And $todate'' AS dateCriteriaLabel\r\n        FROM transaction\r\n        INNER JOIN $customerTable ON $customerTable.customer_id = tra_customer_id\r\n        INNER JOIN customer ON tra_customer_id = customer.customer_id\r\n        LEFT JOIN transaction_item ON tri_transaction_id = transaction.transaction_id \r\n        LEFT JOIN transaction AS invoiceBeingPaid ON invoiceBeingPaid.transaction_id = tri_paying_invoice_id \r\n        WHERE (transaction.tra_type IN (''P'',''R'',''B'',''D'') OR (transaction.tra_type IN (''C'',''I'') AND transaction.tra_posted = 1)) AND (transaction.tra_date BETWEEN ''$fromdate'' AND ''$todate'') AND transaction.tra_total != ''0''\r\n        GROUP BY transaction.transaction_id \r\n        ORDER BY transaction.tra_customer_id, transaction.tra_date, transaction.tra_number \r\n    ");\r\n    \r\n}\r\n\r\nfunction calculateRunningBalance($tableName) {\r\n\r\n	//add balance amount, final amounts\r\n	$cusQRY = nuRunQuery("SELECT DISTINCT tra_customer_id FROM $tableName ");\r\n	while($cusOBJ = db_fetch_object($cusQRY)) {\r\n		$openingBalanceQRY = nuRunQuery("SELECT balanceamount FROM $tableName WHERE tra_customer_id = ''$cusOBJ->tra_customer_id'' AND tra_type = ''Opening Balance''");\r\n		$openingBalanceOBJ = db_fetch_object($openingBalanceQRY);\r\n		$balance = $openingBalanceOBJ->balanceamount;\r\n	\r\n		$transactionQRY = nuRunQuery("SELECT * FROM $tableName WHERE tra_customer_id = ''$cusOBJ->tra_customer_id'' AND tra_type != ''Opening Balance''");\r\n		while($transactionOBJ = db_fetch_object($transactionQRY)) {\r\n			$balance += $transactionOBJ->transamount;\r\n			nuRunQuery("UPDATE $tableName SET balanceamount = $balance WHERE transaction_id = ''$transactionOBJ->transaction_id''");\r\n		}\r\n        nuRunQuery("UPDATE $tableName SET finalamount = $balance WHERE tra_customer_id = ''$cusOBJ->tra_customer_id'' ");\r\n	\r\n    }\r\n    \r\n}\r\n\r\nfunction updatePeriodTotals($tableName, $todate, $customerTable){\r\n    \r\n	//Age balance date ranges\r\n    /* These date ranges were using a fixed 30/60/90 day figure\r\n       Changed to use calendar ranges\r\n	$monthDay	   =   nuDateAddDays($todate,-30);\r\n	$monthDay2	   =   nuDateAddDays($todate,-60);\r\n	$monthDay3	   =   nuDateAddDays($todate,-90);\r\n    */\r\n    \r\n    // Calendar periods\r\n    // first period is always the first of todate''s month\r\n    $currentFromDate        = date(''Y-m-'',strtotime($todate)).''01'';\r\n    $currentToDate          = $todate;\r\n    $currentMonthNo         = date(''n'',strtotime($todate));\r\n    $currentYear            = date(''Y'',strtotime($todate));\r\n    \r\n    // for the other periods we need to adjust todates month number\r\n    $period30MonthNo        = $currentMonthNo - 1;\r\n    $period30Year           = $currentYear;\r\n    $period60MonthNo        = $currentMonthNo - 2;\r\n    $period60Year           = $currentYear;\r\n    $period90MonthNo        = $currentMonthNo - 3;\r\n    $period90Year           = $currentYear;\r\n    \r\n    // change month and year nos if it crosses over years\r\n    if($period30MonthNo <= 0){\r\n        $period30MonthNo += 12;\r\n        $period30Year    -= 1;\r\n    } else if($period60MonthNo <= 0){\r\n        $period60MonthNo += 12;\r\n        $period60Year    -= 1;\r\n    } else if($period90MonthNo <= 0){\r\n        $period90MonthNo += 12;\r\n        $period90Year    -= 1;\r\n    }\r\n    \r\n    // make sure month/day numbers are turned to a string with leading 0 (jan is ''01'' instead of 1)\r\n    $period30MonthNo = substr(''0''.$period30MonthNo,-2);\r\n    $period60MonthNo = substr(''0''.$period60MonthNo,-2);\r\n    $period90MonthNo = substr(''0''.$period90MonthNo,-2);\r\n    \r\n    // figure out period30/60/90 fromdates and todates\r\n    $period30FromDate       = $period30Year.''-''.$period30MonthNo.''-01'';\r\n    $period30ToDate         = date(''Y-m-t'', strtotime($period30FromDate));\r\n    $period60FromDate       = $period60Year.''-''.$period60MonthNo.''-01'';\r\n    $period60ToDate         = date(''Y-m-t'', strtotime($period60FromDate));\r\n    $period90FromDate       = $period90Year.''-''.$period90MonthNo.''-01'';\r\n    $period90ToDate         = date(''Y-m-t'', strtotime($period90FromDate));\r\n    \r\n    // this gets all I C R U ready to be summed for period dates\r\n	nuRunQuery("\r\n		CREATE TABLE $tableName"."theinvs \r\n		(\r\n			INDEX (transaction_id)\r\n		)\r\n		SELECT \r\n			customer_id,\r\n			transaction_id,\r\n			tra_date,\r\n			tra_total\r\n		FROM\r\n			transaction\r\n		INNER JOIN \r\n			$customerTable ON $customerTable.customer_id = tra_customer_id\r\n        WHERE \r\n			(\r\n				(transaction.tra_type IN (''C'',''I'')\r\n				AND \r\n				transaction.tra_posted = 1) \r\n			OR\r\n				transaction.tra_type IN (''R'',''U'')\r\n			)\r\n			AND \r\n			transaction.tra_date <= ''$currentToDate''\r\n	");\r\n	\r\n    // this gets all payments and discounts ready to be summed for period dates\r\n	nuRunQuery("\r\n		INSERT INTO $tableName"."theinvs \r\n		SELECT \r\n			$customerTable.customer_id,\r\n			transaction.transaction_id,\r\n			IFNULL(invoiceBeingPaid.tra_date,transaction.tra_date) AS tra_date,\r\n			transaction_item.tri_total * -1 AS tri_total\r\n		FROM\r\n			transaction\r\n		INNER JOIN\r\n			transaction_item ON transaction_item.tri_transaction_id = transaction.transaction_id\r\n		LEFT JOIN\r\n			$tableName"."theinvs AS invoiceBeingPaid ON invoiceBeingPaid.transaction_id = transaction_item.tri_paying_invoice_id \r\n		INNER JOIN \r\n			$customerTable ON $customerTable.customer_id = transaction.tra_customer_id\r\n        WHERE \r\n			transaction.tra_type IN (''P'',''D'')\r\n			AND \r\n			transaction.tra_date <= ''$currentToDate''\r\n	");\r\n    \r\n	// this gets reverse payments ready to be summed for period dates\r\n    nuRunQuery("\r\n		INSERT INTO $tableName"."theinvs \r\n		SELECT \r\n			$customerTable.customer_id,\r\n			transaction.transaction_id,\r\n			IFNULL(invoice.tra_date,transaction.tra_date) AS tra_date,\r\n			transaction_item.tri_total * -1 AS tri_total\r\n		FROM \r\n			transaction\r\n		INNER JOIN \r\n			transaction_item ON transaction_item.tri_transaction_id = transaction.transaction_id\r\n        LEFT JOIN \r\n            transaction AS payment ON payment.transaction_id = transaction_item.tri_paying_invoice_id AND (payment.tra_type = ''P'' OR payment.tra_type = ''D'')\r\n        LEFT JOIN \r\n            transaction_item AS payment_item ON payment_item.tri_transaction_id = payment.transaction_id\r\n		LEFT JOIN \r\n			transaction AS invoice ON payment_item.tri_paying_invoice_id = invoice.transaction_id AND invoice.tra_date <= ''$currentToDate''\r\n		INNER JOIN \r\n			$customerTable ON $customerTable.customer_id = transaction.tra_customer_id\r\n        WHERE \r\n			transaction.tra_type IN (''B'')\r\n			AND \r\n			transaction.tra_date <= ''$currentToDate''\r\n    ");\r\n	\r\n	nuRunQuery("\r\n		UPDATE $tableName\r\n		INNER JOIN (\r\n			SELECT \r\n				customer_id,\r\n				SUM( IF(tra_date BETWEEN ''$currentFromDate'' AND ''$currentToDate'',tra_total,0) ) AS current,\r\n				SUM( IF(tra_date BETWEEN ''$period30FromDate'' AND ''$period30ToDate'',tra_total,0) ) AS period30,\r\n				SUM( IF(tra_date BETWEEN ''$period60FromDate'' AND ''$period60ToDate'',tra_total,0) ) AS period60,\r\n				SUM( IF(tra_date <= ''$period90ToDate'',tra_total,0) ) AS period90\r\n			FROM\r\n				$tableName"."theinvs \r\n			GROUP BY \r\n				customer_id \r\n		) AS periodTotals ON periodTotals.customer_id = $tableName.tra_customer_id\r\n		SET \r\n			per1amount = periodTotals.current,\r\n			per2amount = periodTotals.period30,\r\n			per3amount = periodTotals.period60,\r\n			per4amount = periodTotals.period90\r\n	");\r\n	\r\n    nuRunQuery("DROP TABLE $tableName"."theinvs ");\r\n    \r\n}\r\n\r\nfunction insertPadLines($tableName, $lineCounts, $fieldsToPutInBlankLines){\r\n    \r\n    $headerDetailsQRY = nuRunQuery("SELECT * FROM $tableName GROUP BY cus_name ");\r\n    while($headerDetailsOBJ = mysql_fetch_object($headerDetailsQRY)){\r\n        //line variables\r\n        $headerAndFooter    = $lineCounts[''headerAndFooter''];\r\n        $header             = $lineCounts[''header''];\r\n        $noHeaderOrFooter   = $lineCounts[''noHeaderOrFooter''];\r\n        $Footer             = $lineCounts[''Footer''];\r\n        padLines($tableName, ''transaction_id'', ''cus_name'', mysql_real_escape_string($headerDetailsOBJ->cus_name), $headerAndFooter, $header, $noHeaderOrFooter, $Footer, $fieldsToPutInBlankLines);\r\n    }\r\n    \r\n}\r\n\r\n// fromdate is ordered after todate in the parameters so that defaults are easier\r\nfunction getOutstandingAmountTable($to_date, $from_date = false, $customer_id = false){\r\n    $tempTable = TT();\r\n	$sql  = "\r\n        CREATE TABLE $tempTable \r\n        SELECT tra_customer_id AS customer_id, \r\n        COALESCE(SUM(IF(tra_type IN(''P'',''D'',''R''), tra_total*-1,IF(tra_type IN(''I'',''B'',''C''),tra_total,''0''))),''0'') AS outstandingAmount,\r\n        cus_name, cus_address\r\n        FROM transaction \r\n        LEFT JOIN customer ON tra_customer_id = customer_id\r\n        WHERE (transaction.tra_type IN (''P'',''R'',''B'',''D'') OR (transaction.tra_type IN (''C'',''I'') AND transaction.tra_posted = 1)) \r\n    ";\r\n    if($from_date){\r\n        $sql .= "AND tra_date BETWEEN ''$from_date'' AND ''$to_date'' ";\r\n    } else {\r\n        $sql .= "AND tra_date <= ''$to_date'' ";\r\n    }\r\n    if($customer_id){\r\n        $sql .= "AND tra_customer_id = ''$customer_id'' ";\r\n    }\r\n    $sql .= "\r\n        GROUP BY tra_customer_id\r\n    ";\r\n	nuRunQuery($sql);\r\n    nuRunQuery("ALTER TABLE $tempTable ADD INDEX(customer_id) ");\r\n    return $tempTable;\r\n}'),
('14e829bd6eb2e4', 'Padding Report Lines', 'function padLines($tableName, $tablePrimaryKey, $sectionKeyName, $sectionKeyValue, $linesOnOnePage, $linesOnPageWithJustHead, $linesOnPageWithoutHeadOrFoot, $linesOnPageWithJustFoot, $fieldsToPutInBlankLines = array()){\r\n    // This function relies on supplying the right numbers...\r\n    // If the report has a page header then make $linesOnPageWithoutHeadOrFoot = 0 (as there cannot be a page with just lines)\r\n    \r\n    /* TESTING LINE INSERT LOOP\r\n    $preTotalRowsToHave = 64;\r\n    $preaddflag     = true;\r\n    while ($preaddflag){\r\n        $t = nuRunQuery("SELECT * FROM $tableName WHERE $sectionKeyName = ''$sectionKeyValue'' ");\r\n        $o = db_fetch_object($t);\r\n        if (mysql_num_rows($t) % $preTotalRowsToHave <> 0) {\r\n            $prenewID = uniqid(1);\r\n            nuRunQuery("INSERT INTO $tableName (`$tablePrimaryKey`, tra_type) VALUES (''$prenewID'',''preinsert'') ");\r\n            if(sizeof($fieldsToPutInBlankLines) > 0){\r\n                $updateSQL = "UPDATE $tableName SET ";\r\n                foreach($fieldsToPutInBlankLines as $k => $v){\r\n                    $updateSQL .= "`$v` = ''".$o->$v."'', ";\r\n                }\r\n                $updateSQL .= " `$tablePrimaryKey` = ''$prenewID'' WHERE `$tablePrimaryKey` = ''$prenewID'' ";\r\n                nuRunQuery($updateSQL);\r\n            }\r\n        }else{\r\n            $preaddflag = false;\r\n        }\r\n    }\r\n    */\r\n    \r\n    // add IsBlankLine to table\r\n    //nuRunQuery("ALTER TABLE $tableName ADD isBlankLine INT(1) NOT NULL DEFAULT 0 ");\r\n    \r\n    $totalAmountOfRows = 0;\r\n    \r\n    // get how many rows are in the table\r\n    $rowCountQRY = nuRunQuery("SELECT * FROM $tableName WHERE $sectionKeyName = ''$sectionKeyValue'' ");\r\n    $rowCount    = mysql_num_rows($rowCountQRY);\r\n    \r\n    // if there are rows in tableName\r\n    if($rowCount){\r\n        // if rowCount is less than linesOnOnePage pad the rest of the lines\r\n        if($rowCount <= $linesOnOnePage){\r\n            $totalAmountOfRows = ($linesOnOnePage);\r\n        // if rowCount is greater than linesOnOnePage but less than or equal to linesOnPageWithJustHead\r\n        } else if($rowCount > $linesOnOnePage && $rowCount <= ($linesOnPageWithJustHead + $linesOnPageWithJustFoot)){\r\n            $totalAmountOfRows = ($linesOnPageWithJustHead + $linesOnPageWithJustFoot);\r\n        // if rowCount is greater than ($linesOnPageWithJustHead + $linesOnPageWithJustFoot) but less than or equal to ($linesOnPageWithJustHead + $linesOnPageWithoutHeadOrFoot + $linesOnPageWithJustFoot)\r\n        } else if($rowCount > ($linesOnPageWithJustHead + $linesOnPageWithJustFoot) && $rowCount <= ($linesOnPageWithJustHead + $linesOnPageWithoutHeadOrFoot + $linesOnPageWithJustFoot)){\r\n            $totalAmountOfRows = ($linesOnPageWithJustHead + $linesOnPageWithoutHeadOrFoot + $linesOnPageWithJustFoot);\r\n        // this means that line number is more than 3 pages, which means we need to have an additional page without header/footer\r\n        } else {\r\n            // if the report has page headers then there is not going to be $linesOnPageWithoutHeadOrFoot at all\r\n            // so instead of adding another page of just lines, we need to insert another page of just header with lines\r\n            if($linesOnPageWithoutHeadOrFoot == 0){\r\n                $multiplier = 1;\r\n                while($totalAmountOfRows < $rowCount){\r\n                    $totalAmountOfRows = ($linesOnPageWithJustHead + $linesOnPageWithoutHeadOrFoot + $linesOnPageWithJustFoot)+$linesOnPageWithJustHead*$multiplier;\r\n                    $multiplier++;\r\n                }\r\n            // else we have no page headers so we can just another page of just lines\r\n            } else {\r\n                $multiplier = 1;\r\n                while($totalAmountOfRows < $rowCount){\r\n                    $totalAmountOfRows = ($linesOnPageWithJustHead + $linesOnPageWithoutHeadOrFoot + $linesOnPageWithJustFoot)+$linesOnPageWithoutHeadOrFoot*$multiplier;\r\n                    $multiplier++;\r\n                }\r\n            }\r\n        }\r\n        \r\n        // main insert loop\r\n        // basically inserts lines until it reaches $totalAmountOfRows\r\n        $addflag = true;\r\n        while ($addflag){\r\n            $t = nuRunQuery("SELECT * FROM $tableName WHERE $sectionKeyName = ''$sectionKeyValue'' ");\r\n            $o = db_fetch_object($t);\r\n            if (mysql_num_rows($t) % $totalAmountOfRows <> 0) {\r\n                $newID = uniqid(1);\r\n                nuRunQuery("INSERT INTO $tableName (`$tablePrimaryKey`, $sectionKeyName) VALUES (''$newID'', ''$sectionKeyValue'') ");\r\n                if(sizeof($fieldsToPutInBlankLines) > 0){\r\n                    $updateSQL = "UPDATE $tableName SET ";\r\n                    foreach($fieldsToPutInBlankLines as $k => $v){\r\n                        $updateSQL .= "`$v` = ''".$o->$v."'', ";\r\n                    }\r\n                    $updateSQL .= " `$tablePrimaryKey` = ''$newID'', isBlankLine = 1 WHERE `$tablePrimaryKey` = ''$newID'' ";\r\n                    //$updateSQL .= " `$tablePrimaryKey` = ''$newID'' WHERE `$tablePrimaryKey` = ''$newID'' ";\r\n                    nuRunQuery($updateSQL);\r\n                }\r\n            }else{\r\n                $addflag = false;\r\n            }\r\n        }\r\n    }\r\n}');
